css:
/* i really want this to be global */

/* extended */
#trackduck .td_bar_controls {
	margin-top: -30px;
    margin-left: 30px;
    width: 90px;
}

#trackduck .td_logo,
#trackduck .td_bar_controls,
#trackduck .td_bar_controls a{
	height: 30px;
}

#trackduck .td_bar_list_anim {
    width: 30px;
    padding-top: 30px;
}

#trackduck .td_logo { width: 30px; }
#trackduck .td_bar_controls a { width: 30px; }
#trackduck .td_bar_hidden:hover .td_logo:after,
#trackduck .td_logo:after { margin-top: 8px; margin-left: 6.5px; width: 19px; height: 15px; }
#trackduck .td_bar_controls .td_bar_share:after { margin-top: 4px; margin-left: 3px; }
#trackduck .td_bar_controls .td_bar_hide:after { margin-top: 6px; margin-left: 4px; }
#trackduck .td_bar_controls .td_bar_new_issue:after { margin-top: 3px; margin-left: 1px; }

#trackduck .td_bar_controls .td_bar_hide .td_bar_count {
    top: 1px;
    left: 13px;
}

/* collapsed */
#trackduck .td_bar_hidden:not(:hover) .td_logo:after {
    background-size: 150px 105px;
    margin-top: 8px;
    margin-left: 8px;
    width: 19px;
    height: 16px;
}

/* collapsed:hover */
#trackduck .td_bar_hidden:hover .td_bar_controls {
    width: 90px;
}

/* bug list */
#trackduck .td_bar.td_bar_show_list .td_bar_list_anim {
    width: 300px;
}
