css:
@-moz-document domain("extra.to"), domain("extratorrent.cc") {
/* Fat trimmed, slim, darkened theme for Extra.to: Restyles to a dark grey / pro theme similar to Discord App colors, and removes clutter and, warnings, advertising and useless interface junk. */

/* REMOVE JUNK */

/*Remove VPN Warning */
html body table tbody tr table tbody tbody tr td div {
    display: none;
}

/* Remove Left Side Bar, and other Warnings */
div.blog_top, div.top_torr, div.blog_left, div.blog_right, div.blog_content, div.blog_left, div.ads2, div iframe, div.wrntxt3, div.wrntx2, td.tabledata1, div.warn4, div.warn5, div.wrn_txt4, div.wrn_txt5, a.tzbtn {
    display: none;
}

/* Remove extra search field and tag cloud */
div.borderdark {
    border-style: none;
    text-align: left;
}

/* Remove Search Button */
td.h_search_btn {
    display: none;
}

/* Remove menubar end caps */
div.menul, div.menur  {
    display: none;
}

/* Remove and Edit some padding and unify table styles */
td.trlbl {
    display: none;
}

table, td {
    padding: 0px !important;
    margin: 0px !important;
}

table {
    border: none !important;
}

td {
    padding: 4px !important;
}

/* Remove ugly and uneccessary logos and icons */
img[src*="images/cat"], img[src*="/flags/"], img[src*="icon_download"], img[src*="rss"], img[src*="icon_comment"], img[src*="sort"], img[src*="logo"], img[src*="download_normal"], img[src*="warning"], img[src*="icon_big"] {
    display: none;
}

/* Remove Promoted Content */
div.mgbox {
display: none !important;
}

/* STYLES */

html body {
    background-color: #373a3e;
    color: #adaeb1;
    border-style: none;
}

a.menu {
color: #adaeb1;
}

a:hover {
color: #ffffff !important;
}

a, .link:hover {
    color: #1897cf;
    text-decoration: none;
    cursor: pointer;
}

a:hover {
    color: #ffffff;
}

.highlight {
    background-color: #6389af;
    color: #cfdbe6;
}
select {
	background-color: #373a3e;
    border: 1px #3e4146 solid;
    color: #adaeb1;
}

td.sy, td.ly, span.seed, span.leech {
	color: #51647D !important;
}

div.divmenu {
    background-color: transparent;
    border-bottom-style: none;
}

h1 {
    font-size: 10pt;
}

form {
    text-align: left;
}

td.submenu2, table.tablemenu td  {
background-color: #373a3e !important;
}

td.submenu2 a, table.tablemenu td a {
color: #1897cf;
}

td.submenu2 a:hover, table.tablemenu td a:hover {
color: #ffffff;
}

div.easy-autocomplete-container, div.easy-autocomplete-container ul, div.easy-autocomplete-container li {
    background-color: #373a3e;
}

div.easy-autocomplete.eac-blue-light ul li.selected, .easy-autocomplete.eac-blue-light ul .eac-category.selected {
    background-color: #3e4146;
}
    
table, tr, td, tbody {
    background-color: #373a3e;
    border: 0px;
    border-style: none;
}

td.tli {
    background-color: #33363c;
    border-bottom-style: solid;
    border-color: #adaeb1;
    border: 1px;
}

.main_menu {
    padding: 0;
    background-color: transparent;
}

.menu {
    background-color: transparent;
    border-style: none;
}

td.td_menu {
    border-style: none;
    background-color: transparent;
    background-image: none;
}

.e_content {
    border-style: none;
}

input.stext {
    padding-left: 4;
    width: 300px;
    background-color: transparent;
    color: #adaeb1;
}

td.menu_spacer, td.ms {
    padding: 0 15px 0 15px;
}

b.pager_no_link {
    border: 1px #3e4146 solid;
    color: #adaeb1;
    font-weight: bold;
    padding: 4px 7px;
    background-color: transparent;
}

a.pager_link {
    border: 1px #3e4146 solid;
    color: #1897cf;
    text-decoration: none;
    padding: 4px 7px;
    background-color: transparent;
}

table.tl th {
    text-align: left;
    border: 1px #3e4146 solid;
    font-weight: normal;
    padding: 5px;
    white-space: nowrap;
}

tbody tr td {
    padding-left: 0;
    margin-left: 0;
    border-style: none;
}

td.tabledata0, td.tabledata1 {
    background-color: transparent;
    border: 0px;
}

/* Link meta */
sup.nano.done, div.usr, div.usr a, div.usrm a {
    color: #51647D !important;
}

}
