css:
@-moz-document domain("kogama.com") {
.avatar.offline {
	border-radius: 35px;
	box-shadow: rgb(38, 38, 38) 0px 0px 7px 0px;
	background-color: rgba(255, 255, 255, 0.0666667);
	border: 3px outset rgba(255, 255, 255, 0.0666667);
	transition: all 147ms;
}
.avatar.offline:hover {
	border: 3px outset rgba(255, 255, 255, 0.76);
}
.avatar.away {
	border-radius: 35px;
	box-shadow: rgb(38, 38, 38) 0px 0px 7px 0px;
	background-color: rgba(255, 129, 61, 0.33);
	border: 3px outset rgba(255, 129, 61, 0.3);
	transition: all 147ms;
}
.avatar.away:hover {
	border: 3px outset rgba(255, 129, 61, 0.58);
	border: 3px outset rgba(255, 129, 61, 0.74);
}
.avatar.online {
	border: 3px outset rgba(34, 255, 0, 0.12);
	border-radius: 35px;
	box-shadow: rgb(38, 38, 38) 0px 0px 7px 0px;
	background-color: rgba(34, 255, 0, 0.12);
	transition: all 147ms;
}
.avatar.online:hover {
	border: 3px outset rgba(34, 255, 0, 0.59) !important;
}
#chat-extended-side #chat-bar ul.windows li.chat-window.open .chatbox ul.messages .to_me .username {
	color: #1370cb !important;
}

/* Feed Stuff */
#profile-news-feed ul.news-feed-thumbs > li.item {
	background-color: #262626 !important;
	color: #ffffff !important;
}
#profile-news-feed ul.news-feed-thumbs > li.item.status_updated .feed-item .status-message a,
#profile-news-feed ul.news-feed-thumbs > li.item.wall_post .feed-item .status-message a {
	color: #ffffff;
}
#profile-news-feed ul.news-feed-thumbs > li.item .feed-header .feed-text .user {
	color: #ffffff !important;
	font-size: 1rem !important;
}
#root-page-mobile #profile-news-feed .feed-comments .comments ul.comment-list li .body {
	color: #262626 !important;
}
#root-page-mobile #profile-news-feed .feed-comments .comments ul.comment-list li .header .username a {
	color: #ffffff !important;
}
#root-page-mobile #profile-news-feed .feed-comments .comments form textarea {
	box-shadow: inset 0 0 5px 0 rgba(0, 0, 0, .1);
	background: rgb(53, 53, 53) none repeat scroll 0% 0%;
	color: white;
}
#root-page-mobile #profile-news-feed .feed-comments .comments ul.comment-list li .body {
	color: #eeeeee !important;
}
#profile-news-feed ul.news-feed-thumbs > li.item .feed-comments .comments .paging .paginator a {
	color: #eeeeee !important;
}
.pure-button-primary,
.pure-button-selected,
a.pure-button-primary,
a.pure-button-selected {
	transition: all 187ms ease-out;
	background: linear-gradient(270deg, hsl(197.3, 44.9%, 34.9%), hsl(197.3, 44.9%, 34.9%), #47A53D, #47A53D) !important;
	background-position: 1% 50% !important;
	background-size: 300% 300% !important;
	text-decoration: none;
	border: none;
	border: 1px solid rgba(34, 255, 0, 0.12);
}
.pure-button-primary:hover,
.pure-button-selected:hover,
a.pure-button-primary:hover,
a.pure-button-selected:hover {
	border: 1px solid hsl(197.3, 44.9%, 34.9%) !important;
	background-position: 99% 50% !important;
}

/* end feed stuff */
#comment {
	background-color: rgb(53, 53, 53);
	color: white;
}
body#root-page-mobile header#pageheader #profile-extended {
	background-color: #2f2f2f;
}

/* Chat */
#chat-extended-side #chat-bar ul.windows li.chat-window.open .chatbox form textarea {
	font-size: 11.9px;
	width: 100%;
	background: #353535;
	height: 60px;
	border-radius: 0;
	color: #ffffff;
}
#chat-extended-side #chat-bar ul.windows li.chat-window.open .chatbox ul.messages .message {
	color: white;
	font-size: .813rem;
	overflow: hidden;
	overflow-wrap: break-word;
	word-wrap: break-word;
}
#chat-extended-side #chat-bar ul.windows li.chat-window.open .chatbox ul.messages .message from_me .username {
	margin-right: 4px;
	font-weight: 700;
	color: rgba(25, 25, 25, 1);
}
#chat-extended-side #chat-bar ul.windows li.chat-window.open .chatbox ul.messages .to_me .username {
	color: #0081ff !important;
}
#chat-extended-side #chat-bar ul.windows li.chat-window.open .chatbox ul.messages .message .username {
	color: rgb(255, 255, 255);
}
#chat-extended-side #chat-bar ul.windows li.chat-window.open .chatbox ul.messages {
	background-color: rgb(75, 75, 75);
}
#chat-extended-side #chat-bar ul.windows li.chat-window.open .chatbox .header {
	background-color: #262626;
}

/* End Chat */

/* Pricing Page */
.product.product-mobile.product-cheap.product-index-1,
.product.product-popular.product-index-2,
.product.product-best.product-index-3 {
	position: relative;
	-webkit-box-shadow: 0px 2px 10px 0px rgba(50, 50, 50, 0.5);
	-moz-box-shadow: 0px 2px 10px 0px rgba(50, 50, 50, 0.5);
	box-shadow: 0px 2px 10px 0px rgba(50, 50, 50, 0.5);
	transform: scale(1);
	border: 0;
	-webkit-transition: 0.4s ease;
	-moz-transition: 0.4s ease;
	-o-transition: 0.4s ease;
	transition: 0.4s ease;
	z-index: 13;
}
.product.product-mobile.product-cheap.product-index-1:hover,
.product.product-popular.product-index-2:hover,
.product.product-best.product-index-3:hover {
	position: relative;
	-webkit-box-shadow: 0px 2px 10px 0px rgba(50, 50, 50, 0.5);
	-moz-box-shadow: 0px 2px 10px 0px rgba(50, 50, 50, 0.5);
	box-shadow: 0px 2px 10px 0px rgba(50, 50, 50, 0.5);
	transform: scale(1.25);
	border: 0;
	z-index: 15;
}

/*Profile Badges*/

/*End Badges*/
.silver {
	opacity: 0;
}
#mobile-page #profile-page .section-top .progression {
	width: 20% !important;
}
#mobile-page #profile-page .section-top .progression {
	margin-top: 6px;
	display: block;
	margin: 0 auto;
}
body#root-page-mobile header#pageheader #meta-nav > li.chat > a i {
	font-size: 24px;
	width: 28px;
	display: inline-block;
	fill: #ffffff !important;
}
body#root-page-mobile header#pageheader #meta-nav > li.profile-notify #header-icon {
	fill: #ffffff;
	cursor: pointer;
	filter: grayscale(100%);
	transition: all 250ms;
}
body#root-page-mobile header#pageheader #meta-nav > li.profile-notify #header-icon:hover {
	fill: white;
}
#profile-news-feed ul.news-feed-thumbs > li.item,
#profile-status-update form.status-message textarea,
#profile-status-update .button-wrapper button,
#mobile-page #profile-page section.creations-custom .section-description .description-container {
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23);
	border-radius: 3.35px
}
.logo .logo-image {
	background-image: url(http://static.kogstatic.com/kogama_icon-64x64.png);
	filter: grayscale(100%);
	transition: all;
	transition-property: all;
	transition-duration: 250ms;
	transition-timing-function: cubic-bezier(.88, -0.01, .07, 1);
	transition-delay: 0s;
}
.logo .logo-image:hover {
	filter: grayscale(0%);
}
header#pageheader nav.menu > ol > li a {
	transition: all 250ms;
}
header#pageheader {
	/* 	background-image: url(http://jom3.es/i/carbon-edit.jpg); */
	background-size: 1.5%;
	background-repeat: repeat;
	opacity: 0.7;
	transition-property: all;
	transition-duration: 250ms;
	transition-timing-function: ease;
	transition-delay: 0s;
}
body#root-page-mobile header#pageheader nav.menu > ol li.active a {
	background-color: #172645;
}
header#pageheader:hover {
	opacity: 1;
}
a {
	transition: all 250ms;
}
header#pageheader nav.menu > ol > li a:hover {
	color: #f5a52e;
}
a:hover {
	color: #f5a52e;
}
#mobile-page .games-list .game-item .game-image {
	border-radius: 7px;
}
#mobile-page .games-list .game-item {
	padding-right: 7px;
	margin-bottom: 7px;
}
.game-image {
	transition: all 0.25 cubic-bezier(.86, 0, .07, 1);
}
.game-image:hover {
	filter: grayscale(65%);
}
#mobile-page .games-list .game-item .game-name-stats {
	border-bottom-left-radius: 7px;
	border-bottom-right-radius: 7px;
}
 ::-moz-selection {
	background: #fc932c;
	color: white;
}
#mobile-page #profile-page .section-top .username h2 {
	color: #ffffff;
	text-shadow: 0 0 1px rgba(0, 0, 0, .43);
	margin: 0;
	padding: 0;
	text-overflow: ellipsis;
	overflow-wrap: normal;
	white-space: nowrap;
	padding-right: 20%;
	overflow: hidden;
	text-align: center;
}
#mobile-page #profile-page .section-top .username h2 {
	line-height: 4rem;
	font-size: 3rem;
	z-index: 50;
}
#mobile-page #profile-page .section-top .progression .level {
	margin-left: -30px;
	margin-right: 0;
	padding-left: 1px;
	padding-right: 33px;
	border-radius: 0 2px 2px 0;
	background-color: #1F2029;
	background-color: #25283a;
}
#mobile-page #profile-page .section-top .progression {
	left: 4%;
}
body#root-page-mobile header#pageheader #meta-nav > li.profile > a .image-container .image {
	filter: saturate(50%) grayscale(75%);
	transition: filter 147ms;
}
body#root-page-mobile header#pageheader #meta-nav > li.profile > a .image-container .image:hover {
	filter: grayscale(0%);
}
body#root-page-mobile {
	background-color: #2A2A2A;
	background-image: -webkit-linear-gradient(top, #323232, #2A2A2A);
	background-image: linear-gradient(180deg, #323232, #2A2A2A);
	filter: none;
}

/* Shop Page */
#mobile-page .content-shop .shop-list .shop-item a:hover {
	color: #505050;
}
#mobile-page .content-shop .shop-list .shop-item .shop-price {
	background-color: #272727;
	right: 0;
	left: 0;
}
#mobile-page .content-shop .shop-list .shop-item .shop-image {
	border-radius: 7px;
}
#mobile-page .content-shop .carousel-shop-list .shop-item {
	padding-right: 7px;
	margin-bottom: 6px;
}

/* End Shop Page*/

/* News Page */
#mobile-page #news-list .news-list .news-item {
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23);
	padding-right: 0;
}
.news-item.first {
	width: 99.1% !important;
}
#mobile-page #news-list .news-list .news-item:not(.first) {
	width: 49%;
	margin-right: 2px;
	margin-left: 2px;
	float: left;
}

/* End News Page */

/* Media Queries */
@media only screen and (min-width: 500px) {
	#mobile-page #profile-page .section-top .progression {
		left: 10%;
		width: 27% !important;
	}
}
@media only screen and (min-width: 1210px) {
	#mobile-page #profile-page .section-top .progression {
		left: 5%;
		width: 21% !important;
	}
}
@media only screen and (min-width: 1000px) {
	#mobile-page #profile-page .section-top .progression {
		left: 9%;
		width: 26% !important;
	}
}
@media only screen and (min-width: 1024px) {
	#mobile-page #profile-page .section-top .progression {
		width: 25% !important;
		left: 8%;
	}
}
@media only screen and (max-width: 300px) {
	#mobile-page #profile-page .section-top .progression {
		width: 64% !important;
		left: 0%;
	}
}

}
