@-moz-document domain('wikipedia.org') {

font-face {

  font-family: 'Open Sans';

  font-style: normal;

  font-weight: 400;

  src: local('Open Sans'), local('OpenSans'), url(https://themes.googleusercontent.com/static/fonts/opensans/v6/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');

}

@font-face {

  font-family: 'PT Serif';

  font-style: normal;

  font-weight: 400;

  src: local('PT Serif'), local('PTSerif-Regular'), url(https://themes.googleusercontent.com/static/fonts/ptserif/v4/sDRi4fY9bOiJUbgq53yZCfesZW2xOQ-xsNqO47m55DA.woff) format('woff');

}


#mw-navigation > div#mw-head{
  background-image: linear-gradient(to bottom, #ffffff, #f0f0f0) !important;
}  
  
#left-navigation > div > ul > li{
  border-top-left-radius: 5px !important;
  border-top-right-radius: 5px !important;
  border:0px !important;
}

#left-navigation > div > ul > li > span{
  background:none !important;
}  
  
#left-navigation > div > ul{
  background:none !important;    
}  


  
#left-navigation > div > ul > li{
  background:none !important;  
}
  
#left-navigation > div > ul > li > span{
  background:none !important;  
  padding-left:20px !important;
  padding-right:20px !important;
}  

#left-navigation > div > ul > li.selected{
    background:none !important;  
    background: #fff !important;    
  
}    
  
  

#right-navigation > div > ul > li{
  border-top-left-radius: 5px !important;
  border-top-right-radius: 5px !important;
  border:0px !important;
}

#right-navigation > div > ul > li > span{
  background:none !important;
}  
  
#right-navigation > div > ul{
  background:none !important;    
}  
  
#right-navigation > div > ul > li{
  background:none !important;  
}
  
#right-navigation > div > ul > li > span{
  background:none !important;  
  padding-left:10px !important;
  padding-right:10px !important;
  height:20px !important;
}  

#right-navigation > div > ul > li.selected{
    background:none !important;  
    background: #fff !important;    
}    
    
div#simpleSearch{
   border-radius: 5px !important;
   padding: 3px !important;
   background:#fff !important;
   left: -9px !important;
   margin-top:-5px !important;
}
  
#content > div#bodyContent > div > table > tbody > tr > td.MainPageBG{
  background: none !important;
  border:0px !important;
}
  
  
#content > div#bodyContent > div > table > tbody > tr > td.MainPageBG > table{
  background: none !important;
  border:0px !important;
}  
  

  #mp-topbanner{
   border-radius: 5px !important;    
  }
  
  

  
#p-logo > a.mw-wiki-logo{
    background-image: none;
    background: url(https://s10.postimg.cc/jejh9z9fd/wiki1.png) center 3px !important;
    background-size:cover !important;
    width:210px !important;
    height:60px !important;
    background-repeat:no-repeat !important;
    margin-top: 5px !important;  
}  
  
.mw-body {
    margin-left: 15em !important;
    padding: 1.25em 1.5em 1.5em 1.5em;
}  
  
#left-navigation {
    margin-left: 15em !important;
}  
  
body{
    background: #fff !important;
}  

  #mw-panel{

  }  
  
.body > ul > li > a{
    font-size:14px !important;
    color:#4a90e2 !important;
    line-height: 21px !important;
  }  
  
#mw-navigation > div#mw-panel > div#p-navigation{
    margin-top:-70px !important;
}
  
#mw-content-text > table > tbody > tr > td.MainPageBG > table > tbody > tr > td > h2{
  border:0px !important;
  padding:9px !important;
  padding-left:15px !important;
  border-radius: 5px !important; 
}
  
a {
    text-decoration: none;
    color: #4a90e2;
    background: none;
}
  
#mw-head > div#p-personal{
  padding-right:10px !important;  
}  
  
#mw-panel > div.portal > h3{  
  font-weight:bold !important;
  font-size:14px !important;
  } 

.portal{
    min-width:210px !important;
    padding-top:20px !important;
    padding-bottom:20px !important;
}

  #bodyContent.mw-body-content > div#mw-content-text > table.infobox.vcard{
    border:none !important;
    border-radius: 5px !important;
    padding:10px !important;
  } 

  .thumbinner{
    border:none !important;
    border-radius: 5px !important;
    padding:10px !important;    
  }  
  
#bodyContent.mw-body-content > div#mw-content-text > div#toc.toc{
    border:none !important;
    border-radius: 5px !important;        
}
  
  body, div, p, span{
    color: #4a4a4a !important;
    line-height: 24px;
  }    
    
  table, div{
    border-radius:5px;
  }  

  #content.mw-body{
    border:none !important;
  }



a, p, span, div, body, table, tr, td, small, h1, h2, h3, h4, h5, h6, h7{
    font-family: Arial, Times, serif !important;
  
}  
  
  #mp-topbanner > tbody > tr > td > table > tbody > tr > td{
    text-align:left !important;
  }

#mp-topbanner > tbody > tr > td > ul{
  list-style-image: none !important;
  lsit-style-type:none !important;
  color: #f9f9f9 !important;
}

#mp-topbanner > tbody > tr > td > ul{
  list-style-image: none !important;
  lsit-style-type:none !important;
}
  
.firstHeading{
  border: 0px !important;
  padding: 9px !important;
  padding-left: 15px !important;
  border-radius: 5px !important;  
  background: #cef2e0 !important;
}  
  
  
  #bodyContent.mw-body-content > div#mw-content-text.mw-content-ltr > table.infobox.vcard{
   background:#cfdff1 !important;
  }  
    
  ul > li#ca-talk > span > a{
    color: #4a90e2 !important;
  }
    
  .vectorTabs > ul > li > span > a{
    color: #4a90e2 !important;     
  }


  #toc.toc > ul > li > a > span.toctext{
    color:#4a90e2 !important;
      
  }
  
  
  #toc.toc > ul > li > ul > li > a > span.toctext{
    color:#4a90e2 !important;    
  }
    
  
  
  
  
  
}

