css:
@-moz-document domain("locutus.io") {
body {
	color      : #EEE;
	background : #222;
}
#header, #profile .inner, .article-inner, #comments, .archive-article, .timeline-row .content , #toTop {
	background : #000;
}
#header a, #header a:visited {
	color : #BBB;
}

/* recent posts - right sidebar */
#recent-post li .item-inner .item-title a {
	color : #CCC;
}
#recent-post li:hover .item-title a {
	color : #38b7ea; /* just like original */
}
#recent-post li .item-inner .item-date {
	color : #777;
}
/* all functions - right sidebar */
.widget ul li a, .widget ol li a, .widget ul .category-list-child a, .widget ol .category-list-child a {
	color : #777;
}
.widget {
	color : #777;
}
.widget a {
	color : #AAA;
}
.widget a:hover {
	color : #FFF;
}
.widget ul:not(#recent-post) li a:before, .widget ol:not(#recent-post) li a:before {
	color: #777;
}
.widget ul:not(#recent-post) li a:hover:before, .widget ol:not(#recent-post) li a:hover:before {
	color: #FFF;
}

code {
	color            : #e96900; /* just like original */
	background-color : #111;
}
.article-title {
	color : #BBB;
}
.article-entry {
	color : #BBB;
}
.article-entry th {
	border-color : #555;
}
.article-entry td {
	border-color : #555;
}
.article-entry hr, .widget hr {
	border-color : #555;
}

/* search mask */
.ins-search-input {
	background   : #333;
	border-color : #555;
	color        : #FFF;
}
.ins-section-container {
    background : #333;
}
.ins-section .ins-section-header {
	color : #EEE;
	border-color : #555
}

/* blog aka /archives */
.timeline-row .content {
	border : 1px solid #555;
}
.timeline-row .content .article-meta {
	color            : #EEE;
	border-color     : #555;
	background-color : #000;
}
.timeline-row .content:after {
	border-right-color : #555;
}
.archive-article-header a, .layout-wrap-inner.tag-cloud a, .timeline-row .content h1 a, .archive-article-header a:visited, .layout-wrap-inner.tag-cloud a:visited, .timeline-row .content h1 a:visited {
	color : #DDD; /* */
}
.archive-article-header a:hover, .layout-wrap-inner.tag-cloud a:hover, .timeline-row .content h1 a:hover, .archive-article-header a:visited:hover, .layout-wrap-inner.tag-cloud a:visited:hover, .timeline-row .content h1 a:visited:hover {
	color : #38b7ea; /* just like original */
}
.article-meta a {
	color : #DDD;
}
.article-meta a:hover {
	color : #38b7ea; /* just like original */
}

/* correction for locutus head - otherwise there is a terrible black hole on picards blad head */
#profile .inner .base-info #avatar {
	background-color : #FFF;
	border-radius    : 10px;
}
}
