css:
@-moz-document domain("jsbeautifier.org") {
/* site without editor */

body {
	background : #111;
	color      : #DDD;
}
select ,
button.submit {
	background-color : #333;
	color            : #DDD;
	border           : 1px solid #a9a9a9;
}
a.self {
	color        : #AAA;
	border-color : #AAA;
}
a {
	color : #66A968;
}
}

@-moz-document domain("jsbeautifier.org") {
/**
 * just copied that code from 
 * https://userstyles.org/styles/138753/stylish-editor-pastel-on-dark-opera
 */
/**
 * OPERA - STYLISH - PASTEL ON DARK
 * 
 * this style is inpired by the Pastel On Dark theme
 * used in the stylus | stylish editor in chrome
 * and to make it useable in opera too
 *
 * original credits from
 * https://github.com/schomery/stylish-chrome/blob/master/codemirror/theme/pastel-on-dark.css
 *
 * Pastel On Dark theme ported from ACE editor
 * @license MIT
 * @copyright AtomicPages LLC 2014
 * @author Dennis Thompson, AtomicPages LLC
 * @version 1.1
 * @source https://github.com/atomicpages/codemirror-pastel-on-dark-theme
 */

.CodeMirror { /* editor textarea background */
	/* Set height, width, borders, and global font properties here */
	background-color : #2c2827 !important;
	color            : #8F938F !important;
}

.CodeMirror-gutters { /* editor - line numbers - background */
	border-right-color : silver;
	border-right       : 0px;
	background-color   : #34302f;
}
.CodeMirror-linenumber { /* editor - line numbers - the numbers */
	color: #8F938F;
}
.CodeMirror div.CodeMirror-cursor { /* cursor */
  border-left: 1px solid #A7A7A7;
}
.cm-tab { /* tabulator sign */
	background : url(data:image/png;base64,);
	background-position : center center;
	background-repeat : no-repeat;

}

.cm-s-default .cm-keyword {color: #AEB2F8;}
.cm-s-default .cm-atom {color: #DE8E30;}
.cm-s-default .cm-number {color: #CCCCCC;}
.cm-s-default .cm-def {color: #757aD8;}
.cm-s-default .cm-variable { color: lightblue; }
.cm-s-default .cm-punctuation,
.cm-s-default .cm-property,
.cm-s-default .cm-operator {}
.cm-s-default .cm-variable-3 {color: #DE8E30;}
.cm-s-default .cm-comment {color: #A6C6FF;}
.cm-s-default .cm-string {color: #66A968;}
.cm-s-default .cm-meta {color: #8F938F;}
.cm-s-default .cm-qualifier {color: #C1C144;}
.cm-s-default .cm-builtin {color: #C1C144;}
.cm-s-default .cm-tag {color: #C1C144;}
.cm-s-default .cm-error { background : #757aD8; color : #f8f8f0; }

/* Default styles for common addons */

div.CodeMirror span.CodeMirror-matchingbracket { /* matching brackets like { }, ( ) */
    border : 1px solid rgba(255,255,255,0.25);
    color  : #8F938F;
}
/* selected code */
.CodeMirror-selected { background: rgb(35, 35, 35); }
.CodeMirror-focused .CodeMirror-selected { background: rgba(221,240,255,0.2); }
.CodeMirror-crosshair { cursor: crosshair; }
.CodeMirror ::selection { background: #d7d4f0; }
.CodeMirror ::-moz-selection { background: #d7d4f0; }

/* search */
.cm-searching {
  background: #ffa;
  background: rgba(255, 255, 0, .4);
}

}
