css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.foxnews.com") {
  
/*--links --*/

a {color: #a6b5c6 !important;}
a:visited {color: #04aaaa !important;}
    

/*--Page color changes-- */
div.page, div.eyebrow, div.article-body, div.article-meta, div.wrapper, span, p 
            {background: #390101 !important;
             color: silver !important;}

section {background: #390101 !important;}
div.content {background: #390101 !important;}
div.collection {background: #390101 !important;}
h1, h2, h3, h4 {color: silver !important;}
div.article-list {background: #390101 !important;}
div.article-comments {color: navy !important;
                      background: silver !important;}
/* div.alert-banner, div.alert-info, div.alert-banner {color: black !important; 
                                  background: black !important;} */



/*-- Dropouts -- */
div.promo {display: none !important;}
div.community-promo {display: none !important;}
div.commenting-nation-promo {display: none !important;}
div.alert-container {display: none !important;}
section.sticky-nav {display: none !important;}


}
