css:
@-moz-document regexp(".*foundry\\.com\\/discuss.*") {
article.discuss a {
	background-color: rgba(0, 0, 0, .5) !important;
	color: rgb(120, 120, 120) !important;
}
/* Header */
 .category-tabs ul.options li a {
	color: rgba(255, 255, 255, .75) !important;
}
.category-tabs {
	background-color: rgba(0, 0, 0, .9);
}
.category-tabs ul.subnav {
	background-color: rgba(0, 0, 0, .5) !important;
	border: 2px rgb(120, 120, 120) !important;
}
article.comment .comment-footer .comment-actions button, article.comment .comment-header .admin-links button, article.comment .comment-header .admin-links a {
	color: rgb(120, 120, 120) !important;
}
.discussion .group .forums article.discuss a:hover {
	background-color: rgba(35, 35, 35, 1) !important;
}
article.discuss a p {
	color: rgba(255, 255, 255, .5) !important;
}
.discussion .group .group-name h3 a {
	color: rgba(255, 255, 255, .90) !important;
}
article.discuss a h4 {
	color: rgba(255, 255, 255, .75) !important;
}
.subsection {
	background-color: rgb(50, 50, 50);
	padding-bottom: .1px;
	color: rgb(25, 25, 25);
}
/* Profile */
 .navbar nav.primary a.icon .avatar {
	width: 50px;
	height: 50px;
	border-radius: 50%;
}
.comment-container .profile-name, .thread .topic .contributions .contribution-info .profile .profile-name {
	color: rgba(252, 180, 52, .75);
}
.profile .profile-image {
	border-radius: 10%;
}
/* Headers

#heroBanner {
 	background-image: url("https://community.foundry.com/common/img/assets/headers/discuss_header.png") !important;
}
 */
 .thread-filters .thread-sort .sort-link, .thread-filters .thread-sort .sort-link.active, .thread .topic .contributions .contribution-info .time, .thread-filters .thread-count, article.comment .comment-header .rating span, article.comment .comment-header .time {
	color: rgba(255, 255, 255, .75) !important;
}
article.comment .comment-header .profile .profile-name {
	color: rgba(255, 255, 255, .90) !important;
}
.thread-filters .thread-sort .sort-link:after {
	border: 1px solid rgba(255, 255, 255, .90);
}
.discussion .group .forums article.discuss a::after, .thread-filters .thread-sort .sort-link.active::after {
	background-color: rgba(255, 255, 255, .90);
	-webkit-mask-image: url('https://community.foundry.com/common/img/icons/cta_arrow.svg');
}
.discussion .group .forums article.discuss a:hover:after {
	background: url('https://community.foundry.com/common/img/icons/cta_arrow.svg') no-repeat;
	background-color: rgba(252, 180, 52, .9) !important;
	-webkit-mask-image: url('https://community.foundry.com/common/img/icons/cta_arrow.svg');
}
.icon.icon-after:after {
	background-size: Auto;
}
.icon.icon-before:before, .icon.icon-after:after {
	width: 24px;
	height: 24px;
}
.icon.icon-quote:before, .icon.icon-quote:after, .icon.icon-quote:not(.icon-after):not(.icon-before) {
	background-color: rgba(255, 255, 255, .90);
	background-image: url('https://community.foundry.com/common/img/icons/form/form-info-quote.svg');
	-webkit-mask-image: url('https://community.foundry.com/common/img/icons/form/form-info-quote.svg');
}
.icon.icon-reply:before, .icon.icon-reply:after, .icon.icon-reply:not(.icon-after):not(.icon-before) {
	background-color: rgba(255, 255, 255, .90);
	background-image: url('https://community.foundry.com/common/img/icons/reply_arrow.svg');
	-webkit-mask-image: url('https://community.foundry.com/common/img/icons/reply_arrow.svg');
	-webkit-mask-size: auto;
}
.icon.icon-liked:before, .icon.icon-liked:after, .icon.icon-liked:not(.icon-after):not(.icon-before) {
	background: url('https://community.foundry.com/common/img/icons/heart_outline.svg');
	background-size: 15px;
}
/* Comments */
 .comment-body-content code, .preview code {
	background: rgb(40, 40, 40) !important;
	width: 100%;
	height: 100%;
	display: block;
}
a[href="/discuss/modo"] {
	color: rgb(61, 120, 134) !important;
}
article.comment .comment-actions, article.comment .comment-footer, article.comment .comment-profile>div, article.post-extended .post-body, article.comment .comment-body, article.comment .preview {
	color: rgb(220, 220, 220) !important;
	background-color: rgb(40, 40, 40) !important;
}
article.comment .comment-footer .comment-info {
	background-color: rgba(255, 255, 255, .50) !important;
}
.comment-body-container blockquote {
	color: rgba(255, 255, 255, .50) !important;
}
.comment-body-container {
	color: rgba(255, 255, 255, .75) !important;
}
.comment-body-container a {
	color: rgba(252, 180, 52, .5) !important;
	text-decoration: underline;
}
.comment-body-container a:visited {
	color: rgb(120, 120, 120) !important;
	text-decoration: underline;
}
.comment-body-container a:hover {
	color: rgba(252, 180, 52, .75) !important;
	text-decoration: underline;
}
.thread .topic .info {
	border-bottom: 1px solid rgba(0, 0, 0, 0.2) !important;
}
/* Search */
 .form-group label.active, .form-group label.static {
	color: rgb(220, 220, 220);
}
.form-group select, .form-group input {
	color: rgb(220, 220, 220);
	background-color: rgba(0, 0, 0, .25) !important;
	background: url('https://community.foundry.com/common/img/icons/none.svg');
}
.form-group input[type="checkbox"]~label, .form-group input[type="radio"]~label {
	color: rgb(220, 220, 220) !important;
}
.form-group input[type="checkbox"]~label:before, .form-group input[type="radio"]~label:before {
	border: 2px solid rgba(220, 220, 220, .2)
}
.form-group input[type="checkbox"]:checked~label:before, .form-group input[type="radio"]:checked~label:before {
	background-color: rgba(255, 255, 255, .90);
	-webkit-mask-image: url('https://community.foundry.com/common/img/icons/ticks.svg');
}
/* Footer */
 #globalFooter {
	margin-top: -18px !important;
	border-top: 2px;
}
/* Products Drop down */
 .category-tabs ul.subnav li:nth-child(1):hover {
	background-color: rgba(161, 48, 61, 0.5);
}
.category-tabs ul.subnav li:nth-child(2):hover {
	background-color: rgba(144, 68, 96, 0.5);
}
.category-tabs ul.subnav li:nth-child(3):hover {
	background-color: rgba(55, 163, 80, 0.5);
}
.category-tabs ul.subnav li:nth-child(4):hover {
	background-color: rgba(61, 120, 134, 0.5);
}
.category-tabs ul.subnav li:nth-child(5):hover {
	background-color: rgba(45, 105, 153, 0.5);
}
.category-tabs ul.subnav li:nth-child(6):hover {
	background-color: rgba(208, 120, 134, 0.5);
}
.category-tabs ul.subnav li:nth-child(7):hover {
	background-color: rgba(69, 188, 237, 0.5);
}
/* Extra */
 body {
	background: rgb(50, 50, 50) !important;
}
article.comment .comment-body .comment-body-container, .thread {
	background-color: rgba(0, 0, 0, .5) !important;
	border-bottom: 1px solid rgba(50, 50, 50, 0);
	margin-bottom: 2px;
}
.comment-body-content pre, .preview pre {
	border: 1px solid black !important;
	background-color: rgb(34, 34, 34) !important;
}
.comment-body-content, .preview, .thread .topic .info h4 a {
	color: rgba(255, 255, 255, .75) !important;
}
.thread .topic .info h4 a:hover {
	color: rgba(255, 255, 255, .90) !important;
}
.content-list .content-dl .download {
	background: rgba(252, 180, 52, .50) !important;
}
.content-list .content-dl .download:hover {
	background: rgba(252, 180, 52, .75) !important;
}
#heroBanner .hero-content .with-button>button {
	padding-bottom: 4px;
	padding-top: 4px;
	font-size: 15px;
}
.floating-button button {
	min-width: 136px;
	margin: 0 0 0 auto;
	padding: 0 10px;
	height: 40px;
	pointer-events: all;
}
}
