css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("taringa.net") {
  /* General stuff */
  * {
    color: #CCC;
    border-radius: 0 !important;
    text-shadow: none !important;
  }

  body,
  html {
    background: #1D1D1D !important;
    color: #CCC;
  }
  /* Remove background */
  #footer,
  #footer-links,
  #friends-live-activity,
  #friends-live-activity .list-item,
  #jugandoa,
  #main,
  #taringa-iframe-profile #taringa-profile-title,
  #taringa-iframe-profile #taringa-profile-title span,
  .box .title .action,
  .box div.title,
  .box div.title h2,
  .box-acciones .row:first-child,
  .breadcrump,
  .cont-filterH,
  .cont-filterH ul li,
  .container-post .fuente::after,
  .container-post .fuente::before,
  .container-post .keywords::after,
  .content-left,
  .content-left::before,
  .divider,
  .footer-list,
  .list .list-element,
  .list-element:nth-child(2n+1),
  .list-m a.ver,
  .list-m ul,
  .list-m ul li,
  .list-m ul li a,
  .list-m.list-recomendados a,
  .list-m.list-recomendados a:hover,
  .list-m.list-top-posts .header::before,
  .list-s h2,
  .list-s ul li,
  .list-s ul li a,
  .nav-pages,
  .nav-pages ul li:hover,
  .paginator,
  .paginator .button-container a,
  .paginator .button-container a.disabled,
  .paginator .list-container ul li,
  .paginator .list-container ul li a:hover,
  .paginator .list-container ul li.active,
  .paginator .list-container ul li.active a,
  .reg-ct,
  .reg-ct .form-item,
  .reg-sb,
  .reg-wr,
  .reg-wr .divider a::after,
  .reg-wr .divider a::before,
  .reg-wr .divider h3.secondary-divider,
  .shout-live .shout-actions .shout-stats,
  .sidebar,
  .ui-dialog .ui-widget-header .ui-state-hover,
  .v6,
  .v6-beta .v6-content,
  .v6-content,
  .view-more,
  body .header-sticky .user-actions,
  body .v6 .v6-container,
  body .v6 nav.tabs .player-post::before,
  body div.meta-data .player-post::before,
  body footer.contentinfo::before,
  div.meta-data ul li.active a,
  footer.contentinfo #footer-links,
  nav.tabs ul li.active a {
    background: none !important;
    border: none;
  }
  /* Remove element */
  #footer .wrapper,
  .box .read-more,
  .footer-android-app,
  .hide,
  footer p.hosted {
    display: none;
  }
  /* Only text color */
  #footer .posts-relacionados .item-post h2 a,
  .big-group-data ul.action-data li span,
  .bio span,
  .box .title .action.text span,
  .box.comustat ul.action-data li span,
  .box.filters ul li a.selected,
  .button-action-s .action-number,
  .button-action-s:hover span,
  .comments-primary .comment-content,
  .container-post .post-content .main-content-post,
  .container-post .tags p strong,
  .date,
  .estadisticas .slide-panel p span.txt,
  .list .list-element--post .preview-content a,
  .list .list-element--post a,
  .list .list-element--tema .preview-content a,
  .list .list-element.list-element--post .value,
  .list .list-element.list-element--tema .value,
  .list-l li:hover .list-l__meta a,
  .list-l li:hover .list-l__meta span,
  .list-l__meta a,
  .list-l__meta span,
  .mis-comunidades .infoBox strong,
  .m-asunto span,
  .m-select__delete,
  .post-create .form-item .item-name,
  .post-create label,
  .recomendar-post.ui-dialog .ui-dialog-title,
  .reg-ct h2,
  .reg-ct label,
  .result-post .points,
  .result-post .points span,
  .shout-live .shout-actions .shout-stats *,
  .sidebar .widget-usuario.material-box li.icon-posts a,
  .sidebar .widget-usuario.material-box li.icon-puntos strong,
  .sidebar .widget-usuario.material-box li.icon-usuarios a,
  .title .action .min-dropdown li a.active,
  .tools li a h5 .ico-usuario,
  body .shout-heading .shout-user-info .icon-tiempo:hover,
  h1,
  h1.nick span,
  h2,
  h3,
  h4,
  h5,
  h6,
  p {
    color: #CCC !important;
  }
  /* Only links */
  #comm-thread-list .thread .info a.nick,
  #full-col .big-group-data h1 a,
  .activity-element a,
  .categoriaList li:hover a span,
  .cont-filterH ul li a,
  .filterBy li a,
  .list-l__title,
  .m-remitente a,
  .panels .panel ol li p a,
  .reg-sb ul li span a,
  .tools .mas,
  a,
  body .header-sticky .shouts-nav li a,
  body .header-sticky .user-actions .user-action > a {
    font-weight: normal;
    color: #2594ef;
  }

  #comm-thread-list .thread .info a.nick:hover,
  #full-col .big-group-data h1 a:hover,
  .activity-element a:hover,
  .categoriaList li:hover a:hover span,
  .cont-filterH ul li a:hover,
  .filterBy li a:hover,
  .list-l__title:hover,
  .m-remitente a:hover,
  .panels .panel ol li p a:hover,
  .panels .subnav li a.active,
  .panels .subnav li a:hover,
  .reg-sb ul li span a:hover,
  .tools .mas:hover,
  a:focus,
  a:hover,
  body .header-sticky .shouts-nav li a:hover,
  body .header-sticky .user-actions .user-action > a:hover {
    color: #6cb7f4;
  }
  /* Only border color */
  .reg-ct p,
  .reg-wr .divider.bt,
  .social-connect {
    border-color: #000 !important;
  }
  /* Dialog */
  .t-modal,
  .ui-widget-overlay {
    background: rgba(0, 0, 0, 0.5) !important;
  }

  .t-modal__inner,
  .ui-dialog .ui-dialog-content,
  .ui-widget-content,
  .ui-widget-header {
    background: #2A2A2A;
  }

  .ui-dialog .ui-widget-header {
    border-bottom: 2px solid #101010 !important;
  }

  .ui-dialog .ui-dialog-buttonpane {
    background: #2A2A2A !important;
  }

  .recomendar-post.ui-dialog #recomendar-post .item span {
    color: #CCC !important;
  }

  .t-modal__footer {
    border-top: 1px solid #101010;
  }
  /* Input V5 */
  #newPost .form-item .fake-textarea.focus,
  #newPost .form-item input[type="text"]:focus,
  #newPost .form-item select:focus,
  #newPost .form-item textarea:focus,
  #search-big,
  .add-url input.simple,
  .box .clearfix input[type="text"],
  .form-input-text,
  .form-input-text:active,
  .form-input-text:focus,
  .post-create .form-item .fake-textarea,
  .post-create .form-item input[type="text"],
  .post-create .form-item select,
  .post-create .form-item textarea,
  .post-create input.text-inp {
    background: #101010 !important;
    color: #CCC;
    border: 2px solid #000 !important;
    box-shadow: none;
  }
  /* Input V6 */
  #tool-shout #shout-pane-link input,
  #tool-shout textarea,
  #tool-shout textarea:focus,
  .myComment .myComment-text-box textarea,
  .myComment .myComment-text-box textarea:focus,
  .recomendar-post.ui-dialog #recomendar-post .item input,
  .recomendar-post.ui-dialog #recomendar-post .item input:focus,
  .recomendar-post.ui-dialog #recomendar-post textarea,
  .recomendar-post.ui-dialog #recomendar-post textarea:focus,
  .reg-ct input.form-input-text,
  .ui-widget-content textarea,
  .ui-widget-content textarea:focus {
    background: #101010;
    color: #CCC;
    border: 2px solid #000;
    box-shadow: none;
  }

  #tool-shout #shout-pane-link input {
    width: 192px;
  }

  #markItUp {
    background: #101010 !important;
  }
  /* Shout Box V5 */
  .shout-box {
    background: #2A2A2A;
    border: none;
  }

  .my-shout-attach {
    border: none;
  }

  .my-shout-textarea-mi textarea,
  .my-shout-textarea-perfil textarea,
  .my-shout-textarea-wall textarea {
    background: #101010 !important;
    color: #CCC !important;
    border: 2px solid #000 !important;
  }

  #my-shout-body-wall,
  .my-shout-textarea-mi span,
  .my-shout-textarea-mi textarea,
  .my-shout-textarea-wall textarea {
    width: 539px !important;
  }
  /* Busca amigos box */
  .tip-box {
    background: #3C3C2B;
  }
  /* Shouts V5 */
  .activity-element a.alter,
  .activity-element a.avatar img,
  .activity-element a.avatar img:hover,
  .shout-box a.avatar img,
  .shout-box a.avatar img:hover {
    background: none;
    border: 2px solid #000;
    box-shadow: none;
  }

  .activity-content,
  .activity-element p,
  .button-action-s,
  .quick-reply,
  .shout-content,
  .shout-footer,
  .shout-live,
  .shout-live .shout-actions,
  .shout-live .shout-body,
  .shout-live .shout-body .shout-link,
  .shout-live .shout-head,
  .small-list.activity-log,
  .small-list.quick-reply {
    background: #2A2A2A !important;
    border: none !important;
    color: #CCC !important;
    box-shadow: none;
  }

  .shout-footer {
    margin-left: 80px !important;
  }

  .dialog {
    background: none;
    width: 0;
    height: 0;
    border-top: 10px solid transparent;
    border-bottom: 10px solid transparent;
    border-right: 10px solid #2A2A2A;
  }

  .shout-live-container .shout-arrow {
    right: -10px;
    width: 0;
    height: 0;
    border-top: 10px solid transparent;
    border-bottom: 10px solid transparent;
    border-left: 10px solid #2A2A2A;
  }

  .shout-live {
    box-shadow: 0 0 5px rgba(0,0,0,0.6);
  }

  .activity-element.new-post .link-data,
  .activity-element.new-tema .link-data,
  .activity-element.respuesta .link-data,
  .link-data .link-desc,
  .link-summary,
  .link-summary p {
    background: #101010 !important;
  }

  .activity-element.new-tema .link-data {
    padding: 5px;
  }

  .bio i.icon,
  .button-action-s i.icon,
  .shout-live .shout-actions .shout-stats i.icon,
  .small-list.activity-log i.icon,
  i.icon.privacy {
    filter: invert(1) brightness(0.8);
  }

  .my-shout-attach-options .btn.g:not(:hover) .btn-text i {
    filter: invert(1);
  }

  i.icon.remove-s {
    filter: invert(1);
  }

  .button-action-s span {
    opacity: 0.5;
  }

  .button-action-s:hover span {
    opacity: 1;
  }
  /* Sidebar things V5 */
  #friends-live-activity .list-item:hover {
    background: #101010 !important;
  }

  .categoriaList li,
  .textrank-tags ul li {
    border-bottom: 1px solid #000;
    height: 20px;
  }

  .categoriaList li:hover,
  .comunidades-list .list-single,
  .textrank-tags ul li:hover {
    background: none;
    border-bottom: 1px solid #000;
  }
  /* Dropdown V5 */
  .title .action .min-dropdown {
    background: #2A2A2A;
    border: none;
  }

  .title .action .min-dropdown li {
    border: none;
  }
  /* Button sidebar V5 */
  .box.pin-add input.add,
  input[type="button"] {
    width: 30px;
    height: 30px;
    background: #505050;
    color: #CCC;
    border: none;
  }
  /* Button V5 */
  .ui-dialog .ui-dialog-buttonpane .ui-dialog-buttonset .ui-button-cancel,
  .ui-dialog .ui-dialog-buttonpane .ui-dialog-buttonset .ui-state-default {
    background: #505050;
    color: #CCC;
  }

  .ui-dialog .ui-dialog-buttonpane .ui-dialog-buttonset .ui-button-cancel:hover,
  .ui-dialog .ui-dialog-buttonpane .ui-dialog-buttonset .ui-state-default:hover {
    background: #5C5C5C;
  }

  .ui-button-positive,
  .ui-dialog-buttonset .ui-button-positive {
    background: #0dd769;
    color: white;
    box-shadow: none;
    border: none;
  }

  .ui-button-positive:hover,
  .ui-dialog-buttonset .ui-button-positive:hover {
    background: #0bea71;
    color: white;
    border: none;
  }

  .ui-button-positive .ui-button-text,
  .ui-dialog-buttonset .ui-button-positive .ui-button-text {
    color: white;
  }

  .add-url input.simple-btn,
  .btn.g,
  .sidebar .listados h2 span,
  .sidebar .listados h5 span,
  .sidebar .listados.widget-relacionados .posts-nav,
  .ui-btn.ui-state-default,
  .view-more-list,
  button {
    background: #505050;
    color: #CCC;
    border: none;
    box-shadow: none;
  }

  .add-url input.simple-btn:hover,
  .btn.g:hover,
  .sidebar .listados h2:hover span,
  .sidebar .listados h5:hover span,
  .sidebar .listados.widget-relacionados .posts-nav:hover,
  .ui-btn.ui-state-default:hover,
  button:hover {
    background: #5C5C5C;
    color: #CCCCCC;
    border: none;
    box-shadow: none;
  }

  .btn.r .btn-text,
  .btn.v .btn-text {
    color: white;
  }

  .btn.v {
    background: #0dd769;
    color: white;
    border: none;
  }

  .btn.v:hover {
    background: #0bea71;
    color: white;
    border: none;
  }
  /* Button V6 */
  #tool-shout #shout-pane-link button.add,
  #tool-shout .botones > a,
  #tool-shout .botones > a.icon-camara,
  #tool-shout .botones > a.icon-video,
  #tool-shout .botones > a.link {
    background: #505050;
    color: #CCC;
  }

  #tool-shout #shout-pane-link button.add:hover,
  #tool-shout .botones > a.icon-camara:hover,
  #tool-shout .botones > a.icon-video:hover,
  #tool-shout .botones > a.link:hover,
  #tool-shout .botones > a:hover {
    background: #5C5C5C;
  }

  #tool-shout .share > a.share-off {
    background: #505050;
  }

  .recomendar-post.ui-dialog #recomendar-post .share-shout a.share-off {
    background: #5C5C5C;
  }
  /* Nav V5 */
  .cont-filterH ul li a.active,
  .v6-beta nav.tabs li.active a {
    color: #CCC !important;
    background: none !important;
    border-bottom: 3px solid #2594ef;
  }
  /* Perfil V5 */
  .big-info li.sep,
  .perfil-data,
  .perfil-user,
  .user-level ul li {
    background: #2A2A2A;
    border: none;
  }

  .big-info li {
    border-bottom: 1px solid #000;
  }

  #main-col #full-col {
    border: none !important;
  }
  
  .activity-min {
    border-bottom: none;
  }
  
  /* Mensajes Privados V5 */
  .m-select {
    background: #101010;
    border-bottom: none;
  }
  
  .m-top {
    background: #2A2A2A;
  }
  
  .m-bottom {
    border-top: none;
  }
  
  .m-linea-mensaje {
    background: #505050;
    border-bottom: 1px solid #101010;
  }
  
  .m-linea-mensaje.open {
    background: #2A2A2A;
    border-bottom: 1px solid #101010;
  }
  
  .m-remitente img.offline {
    border-color: #000;
  }
  
  .m-remitente img.online {
    border-color: #0dd769;
  }
  
  /* Comunidad V5 */
  .avatar-list-48 li {
    margin: 0;
    border: 2px solid #000;
  }

  .avatar-list-48 li.admin {
    background: none;
    border: 2px solid #2594ef;
    box-shadow: none;
  }

  .big-group-info,
  .filterBy {
    background: #2A2A2A;
    border: none;
  }

  .big-group-data ul.action-data li,
  .box.comustat ul.action-data li {
    border-right: 1px solid #000;
    color: #CCCCCC;
  }

  #comm-thread-list {
    border: none;
  }

  #comm-thread-list .list-header,
  #comm-thread-list .thread:nth-child(2n+1),
  .result-post h3 a span {
    background: #2A2A2A;
  }

  body.v6-beta.section-comunidad #page #main #comunidad-container #sidebar .form-input-text {
    width: 200px !important;
    height: 22px;
  }

  .box.sticky {
    background: #101010;
    border: none;
  }

  .box.sticky .title {
    border: none;
  }
  /* Search Results V5 */
  .mis-comunidades .resultBox {
    border-bottom: 1px solid #000;
  }

  .result-post,
  .result-post:hover {
    background: none;
    border-bottom: 1px solid #000;
  }

  i.icon.less {
    filter: invert(1) brightness(0.8);
  }

  .image-search-preview {
    background: #2A2A2A;
    border: none;
  }
  /* Square with number V5 */
  .cont-filterH ul li a span {
    box-shadow: none;
    background: #363636;
    color: #CCC;
  }
  /* Shouts V6 */
  body .header-sticky {
    background: #101010;
    border: none;
  }

  body .header-sticky .shouts-nav li.active a {
    color: #CCC;
  }

  body .shouts-nav {
    border: none;
  }

  body .shouts-nav li.active a {
    color: #CCC;
    border-bottom: 3px solid #2594ef;
  }

  body .shout-item {
    border-bottom: 2px solid #000;
  }

  body .v6-container .v6-content .sidebar .hashtags_list li {
    border-bottom: 1px solid #000;
  }
  
  body .shout-main-content .article-content-wrapper {
    background: #101010;
  }
  /* Viewing Shout V6 */
  .comments-primary .comment:hover,
  body .comments-primary,
  body .myComment,
  body .shout-detail {
    background: #2A2A2A;
    border: none;
  }
  /* Buttons V6 shouts */
  .v6 .btn {
    border-radius: 0 !important;
  }

  .comments-primary .comments-closed,
  .comments-primary .comments-empty,
  .tema-footer .favorite-post-post,
  .tema-footer .favorite-tema-tema,
  .tema-footer .follow-button,
  .tema-footer .follow-tema-tema,
  .tema-footer .unfollow-tema-tema,
  .v6 .btn,
  body .comments-primary .more-replies,
  body .secondary-actions .list-main-actions li a {
    background: #505050;
    color: #CCC;
  }

  .tema-footer .favorite-post-post:hover,
  .tema-footer .favorite-tema-tema:hover,
  .tema-footer .follow-button:hover,
  .tema-footer .follow-tema-tema:hover,
  .tema-footer .unfollow-tema-tema:hover,
  .v6 .btn:hover,
  body .comments-primary .more-replies:hover {
    background: #5C5C5C;
  }
  
  /* Emoticons Box */
  .emoji-categories,
  .myComment .markItUpHeader,
  .myComment .myComment-text-box .markItUpHeader,
  .myFastreply .markItUpHeader {
    background: #101010;
    border: none;
  }
  .compose-msg .markItUpHeader {
    width: 516px;
  }
  .compose-msg .emoji-categories {
    width: 532px;
  }
  .compose-msg .emoji-list,
  .emoji-list {
    background: #CCC;
    border: none;
  }
  .emoji-nav.left,
  .emoji-nav.right,
  .compose-msg .emoji-nav.left,
  .compose-msg .emoji-nav.right,
  .myComment .myComment-text-box .emoji-nav.left,
  .myComment .myComment-text-box .emoji-nav.right {
    color: #CCC;
    background: none;
    text-align: center;
    border: none;
  }
  .emoji-nav.left::before {
    content: "\2039";
    font-size: 34px;
    line-height: 34px;
  }
  .emoji-nav.right::before {
    content: "\203A";
    font-size: 34px;
    line-height: 34px;
  }
  
  /* Home V6 */
  div.meta-data ul li.active a,
  nav.tabs ul li.active a {
    border-bottom: 3px solid #2594ef;
    color: #CCC !important;
  }

  .list-l li,
  .list-l li:hover,
  .list-m,
  .list-m h2,
  .list-s,
  .top-usuarios ul li,
  .top-usuarios ul li:hover {
    background: #2A2A2A;
    border: none;
  }

  .list-m.list-top-posts .header {
    border: 2px solid #101010 !important;
  }

  .sidebar h2 {
    border: none;
  }

  .estadisticas .slide-panel .desplegable .col,
  .estadisticas .slide-panel .info1 {
    border-right: 1px solid #101010;
  }

  .estadisticas .slide-panel,
  .shout-sidebar .shoutsb {
    border-bottom: 1px solid #101010;
  }
  /* Post v6 */
  .container-post__inner,
  .sidebar .widget-usuario.material-box {
    background: #2A2A2A;
  }

  .container-post .post-content {
    border-top: 2px solid #101010;
  }

  .box-acciones,
  .box-acciones .row,
  .container-post .fuente,
  .container-post .keywords,
  .container-post .tags {
    border-top: 2px solid #101010;
    background: #2A2A2A;
  }

  .container-post .dar-puntos-p {
    background: #2A2A2A;
    color: #CCC;
  }

  .box-acciones .dar-puntos #point-bar.container-puntos li {
    background: #505050;
    color: #CCC;
    border-color: #101010;
  }

  .box-acciones .favorite-post-post,
  .box-acciones .fg-button,
  .box-acciones .flag-post-post,
  .box-acciones .ui-btn,
  .box-acciones .ui-state-default:active,
  .box-acciones .ui-state-default:active,
