css:
@-moz-document url-prefix("http://android.com.pl") {
.sidebar {
    display: none!important;
}
.posts {
    width: 100%!important;
    margin: 0!important;
}
.posts li {
    /* width: calc(100% - 40px)!important; /* jedna kolumna z artykułami */
    width: calc(50% - 40px)!important; /* dwie kolumny z artykułami */
    /* width: calc(33.33333% - 40px)!important; /* trzy kolumny z artykułami */
    /* width: calc(25% - 40px)!important; /* cztery kolumny z artykułami */
}
.big-posts {
    /* display: none!important; /* ukrycie wyróżnionych artykułów */
}
.share-btn, .wp-polls .Buttons {
    display: none!important; /* ukrycie przycisków do udostępniania artykułów */
}
/* szerszy tekt w artykułach */
.post-content {
    width: auto!important;
    margin-left: 10%!important;
    margin-right: 10%!important;
    padding-right: 0!important;
    text-align: justify!important;
}
/* usunięcie belki z tagami */
.tags {
    display: none!important;
}
}
