css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slashdot.org") {

* {background-color: #ffffff !important;background-image: none !important;color: #999999 !important;border-color: #333 !important;border:1px !important}
a:link, a:active, a:visited {color: #999 !important;}
a:hover {color: lightgrey !important;}
div.cw div.commentSub a {color:#999!important}
.story{background-color:#ffffff !important}
a[class="skin topic main tag"]{background-color:#0a0a0a !important}
a[class="datitle"]{background-color:#0a0a0a !important}
span[class="soda pop1"]{background-color:#0a0a0a !important;}
span[class="cnin"]{background-color:#0a0a0a !important;}
span[class="sodify"]{background-color:#fff !important;}
.vote{display:none !important}
#ft{display:none !important}
#fh-pag-div{display:none !important}
#modal_box{display:none !important}
#modal_cover{display:none !important}
#fh-paginate{display:none !important}
#slashboxes{display:none !important}
.railrad{display:block !important;width:0px !important}
.yui-b {margin:0px !important;width:100% !important;border:1px solid #333 !important;-moz-border-radius:15px !important;padding:1px !important}
.hideleftmenu{display:block !important;margin:0px !important;width:100% !important}
.yui-u {margin:0px !important;width:100% !important}
.message{display:none !important}
.fhroot{display:block !important; width:99% !important}
.fhitem{display:block !important; width:100% !important}
.article{display:block !important; width:100% !important}
.briefarticle{display:block !important; width:100% !important}
.topic img {opacity: 0.3}


.janrainSocialPlaceholder {display:none}
.adwrap, .adwrap-sticky, iframe, #taboola-below-article-thumbnails-2nd {display:none}   /* advertisements really stand out now that everything else is toned down */

}
