css:
@-moz-document domain("choualbox.com") {
body {
    background-color: #222 !important;
}
.headbar {
    background-color: #000;
}
.menuvertical.menuprincipal {
    background-color: #151111;
}
.bg-near-white {
    background-color: #1f1e1e;
}
.bg-white, .bg-light-gray {
    background-color: #131111;
}
.menuvertical.menusection li a {
    color: #eee;
    background-color: #130f0f;
}
.nav {
    background-color: hsla(0, 4%, 7%, 0.9);
}
.black {
    color: #eee;
}
.mainmodal {
    background-color: hsla(0, 7%, 15%, 0.9);
}
.mainmodal-header {
	background-color: #1d1919;
}
.black-70 {
    color: #eee;
}
.black-90, .black-80 {
    color: #ddd;
}
.mainmodal-wrapper {
	background-color: #1b1b1b;
}
.relative.pv1 > .height-100.absolute {
    display: none;
}
.dark-gray {
    color: #dcdcdc;
}
.box_itemboucle a.fs15:visited, .box_itemboucle a.fs14:visited {
	color: #6b4545;    
}
.gototop {
	background: initial;
}
h1.titrepage {
	background-color: #100f0f;
}
.black-40 {
	color: rgba(175, 175, 175, 0.8);
}
.count {
	color: #16ddff;
}
*, .gray {
    color: #b9b7b7;
}
.hover-bg-near-white:focus, .hover-bg-near-white:hover {
	background-color: #333;
}
.post_commentaire textarea {
    background: #222;
    color: #ddd;
}
.dropdown-content {
    background: #211f1f;
}
#coldroite, #colgauche {
    background-color: #131313;
}
.cb-contenu-a {
    background-color: #222;
}
.thead_boucle .dropdown-content a, .thead_boucle a {
    color: #d4d4d4;
}
}
