css:
@-moz-document domain("wikipedia.org") {
/* Stylish port of StyleBot's http://stylebot.me/styles/15941 */
/* @import url(https://fonts.googleapis.com/css?family=Lato); */

#column-one {
    padding-top: 0;
}

#column-one .portlet {
    position: static;
}

#column-one .portlet ul {
    margin-left: 0;
    padding-left: 0;
}

#column-one .portlet ul li {
    display: list-item;
    margin-left: 0;
    margin-right: 0;
    text-align: left;
    border: none;
    background-color: inherit;
}

#bodyContent p {
    font-size: 15px;
    line-height: 24px;
    margin-bottom: 15px;
}

#bodyContent ul li {
    font-size: 14px;
    line-height: 22px;
    margin-bottom: 5px;
}

#content {
    background-color: #f2f2f2;
    background-image: none;
    border-bottom-color: #adadad;
    border-bottom-style: solid;
    border-bottom-width: 0;
    border-left-color: #adadad;
    border-left-style: solid;
    border-left-width: 1px;
    border-right-color: #adadad;
    border-right-style: solid;
    border-right-width: 1px;
    border-top-color: #adadad;
    border-top-style: solid;
    border-top-width: 0;
    font-family: Georgia;
    margin-bottom: 0;
    margin-left: auto;
    margin-right: auto;
    margin-top: 0;
    width: 860px;
}

#contentSub {
    font-family: Lato;
    font-size: 12px;
    padding-left: 15px;
    padding-top: 5px;
}

#coordinates {
    margin-top: 10px;
}

#footer {
    display: none;
}

#mw-head {
    display: none;
}

#mw-navigation {
    display: none;
}

#p-logo {
    display: none;
}

#p-logo a {
    display: none;
}

#siteSub {
    display: none;
}

#toctitle h2 {
    font-size: 20px;
}

.rating-container, .rating-container * {
    background-color: inherit;
}

.references li {
    font-size: 12px;
    padding-bottom: 10px;
}

a.external.free {
    color: #0083c9;
}

a.external.text {
    color: #0083c9;
}

a.internal.hoverZoomLink img {
    display: none;
}

a.mw-redirect {
    color: #0083c9;
}

blockquote.templatequote div {
    color: #4d4d4d;
    font-size: 14px;
    font-style: italic;
    line-height: 20px;
}

body {
    background-color: #e6e6e6;
    background-image: none;
}

caption.fn {
    margin-bottom: 5px;
}

div.catlinks {
    display: none;
}

div.collapsible-nav {
    display: none;
}

div.mw-body {
    font-family: Lato;
    margin-top: -20px;
}

div.mw-content-ltr {
    font-family: Lato;
    padding: 20px;
}

div.mw-content-ltr dl {
    font-size: 14px;
    line-height: 20px;
}

div.mw-content-ltr h2 {
    margin-bottom: 0;
}

div.mw-content-ltr p {
    line-height: 23px;
}

div.mw-content-ltr p a {
    color: #0083c9;
}

div.mw-jump {
    display: none;
}

div.noprint {
    display: none;
}

div.references-small {
    font-family: Helvetica, Arial, sans-serif;
}

div.references-small.references-column-count.references-column-count-2 {
    font-family: Helvetica, Arial, sans-serif;
}

div.references-small.references-column-width {
    font-family: Helvetica, Arial, sans-serif;
}

div.thumbinner {
    -webkit-box-shadow: 3px 3px 8px #999;
    background-color: #ffffff;
    border-bottom-style: none;
    border-left-style: none;
    border-right-style: none;
    border-top-style: none;
    font-family: "Lucida Grande", Verdana, sans-serif;
}

h1.firstHeading {
    font-family: Lato;
    margin-bottom: -20px;
    margin-top: -10px;
    padding: 20px;
}

h1.firstHeading span {
    font-family: Lato;
}

h1.firstHeading span i {
    font-family: Lato;
    font-size: 24px;
    font-style: normal;
}

img {
    height: auto;
    max-width: 100%;
}

img.thumbimage {
    border-bottom-style: none;
    border-left-style: none;
    border-right-style: none;
    border-top-style: none;
}

li i a {
    color: #0083c9;
}

ol.references {
    font-size: 14px;
}

span.citation.web a {
    color: #0083c9;
}

span.editsection {
    color: #dedede;
    display: none;
}

span.editsection a {
    color: #d6d6d6;
}

/*
span.mw-editsection {
}
*/

span.mw-headline {
    font-size: 18px;
}

span.tocnumber {
    color: #97accc;
    font-size: 11px;
}

span.toctext {
    color: #0083c9;
    font-size: 14px;
}

sup.reference a {
    color: #bdbdbd;
}

table.infobox {
    -webkit-box-shadow: 3px 3px 8px #999;
    background-color: #ffffff;
    border-bottom-style: none;
    border-left-style: none;
    border-right-style: none;
    border-top-style: none;
    margin-bottom: 5px;
    margin-left: 5px;
    margin-right: 2px;
    margin-top: 5px;
    padding: 10px;
}

table.infobox.biography.vcard {
    font-family: Lato;
    font-size: 12px;
    margin-left: 0;
    padding: 10px;
}

table.infobox.vcard {
    -webkit-box-shadow: 3px 3px 8px #999;
    background-color: #ffffff;
    border-bottom-style: none;
    border-left-style: none;
    border-right-style: none;
    border-top-style: none;
    font-family: "Lucida Grande", Verdana, sans-serif;
    margin-bottom: 20px;
    margin-left: 20px;
    margin-right: 20px;
    margin-top: 20px;
}

table.metadata.mbox-small.plainlinks {
    display: none;
}

table.metadata.plainlinks.ambox.ambox-content {
    display: none;
}

table.navbox.vcard {
    display: none;
}

table.nowraplinks.collapsible.expanded.navbox-inner {
    border: 1px solid #eee;
}

table.toc {
    -webkit-box-shadow: 3px 3px 8px #999;
    background-color: #ffffff;
    border-bottom-style: none;
    border-left-style: none;
    border-right-style: none;
    border-top-style: none;
    font-family: "Lucida Grande", Verdana, sans-serif;
    margin-bottom: 15px;
    margin-top: 20px;
}

table.toc.tochidden {
    margin-bottom: 15px;
    margin-top: 20px;
}

table.vertical-navbox.nowraplinks {
    -webkit-box-shadow: 3px 3px 8px #999;
    background-color: #ffffff;
    border-bottom-style: none;
    border-left-style: none;
    border-right-style: none;
    border-top-style: none;
    margin-bottom: 5px;
    margin-left: 5px;
    margin-right: 2px;
    margin-top: 5px;
}

tbody tr td {
    background-color: #ffffff;
    border-bottom-color: #e0e0e0;
    border-left-color: #e0e0e0;
    border-right-color: #e0e0e0;
    border-top-color: #e0e0e0;
    color: #555;
    font-family: Lato;
    font-size: 13px;
    line-height: 20px;
    padding-bottom: 5px;
}

tbody tr th {
    background-color: #ffffff;
    font-family: Lato;
    font-size: 14px;
}

td.mbox-image div {
    display: none;
}

td.mbox-text {
    display: none;
}

th.navbox-title {
    background-color: #e3e3e3;
    font-family: Helvetica, Arial, sans-serif;
}

th.summary {
    font-family: Lato;
    font-style: normal;
    padding-bottom: 10px;
    padding-top: 10px;
}

tr td a {
    color: #0083c9;
}

tr td div {
    background-color: #ffffff;
}

ul li a {
    color: #0083c9;
}
}
