css:
@-moz-document domain("boardgamegeek.com") {
body {padding:20px;}
body, html {
    background:#111;
    font-family: arial;
}
.profile_block {
    background: #a7add4;
}
table.innermoduletable {background: white;}
html, body, table, td, tr, div, span, a, b, strong, li, ul, ol, i {
    font-size:14px;
}
a:link, a:visited {
    color:#000;
}
a:hover {
    color:#55cc00;
}
div#header {
    min-width: 1070px !important;
    background: #292e62;
    border: none;
}
#simpleCarousel, .bggstore_widget_controls {
    width: auto;
}
.bggstore_widget_slide {
    position: absolute;
    width: 210px;
}
#simpleCarousel {
    background: #fff;
}
.bggstore_widget_controls a:link {
    color: #ffffff;
}
.searchmenu, .menucontainer, .mainmenu {background:transparent !important; float:left;width: auto;}
#header_top {
    height: auto;
}
.searchmenu {float: right;}
.mainmenu {margin-top: -30px;}
.mainmenutabs, .mainmenutabs li a.selected {background:transparent;}
li#bggcon_label {display: none;}
.fl {
    color: #fff;
}
.geekmodule tr {border-bottom:1px solid #ccc;}
.geekmodule tr:nth-child(odd) {
    background: #fff;
}
.menu_quickbar {
    background: #292e62;
    width: 280px;
    color: #fff;
    border: none;
    font-size: 10px;
    padding: 5px;
    margin-left: 20px;
}
.menu_quickbar a {
    color: #fff;
}
.quickbar_pager a {
    margin-right: 5px;
}
.quickbar_pager a:last-child {
    color:#ff0000;
}
.menu_login {background: #292e62; border:none;}
.menu_login table a, .menu_login div.mt5 a {color:#ddd;}
a#geek-logout {color: #ff0000;}

#quickbar_label {display: none;}
div#container {
    margin: auto;
    width: 1300px;
}
.ng-binding {
    font-weight:bold;
}
.sf.ml10 .ng-binding a {
    font-weight:normal;
    font-size: 12px;
}
.innermoduletable td {
	padding:10px 5px;
}
.recentitems.moduletable .innermoduletable td {
	padding:0px;
}
.recentitems.moduletable .innermoduletable td a {
	padding:10px;
    float: left;
    width: 190px;
}
.moduletable th a {
    color: lightblue;
    font-weight:bold;
}
.hotitems, .recentitems {
    width: 210px;
}
.recentitems.moduletable > tbody > tr:nth-of-type(2) > td > .innermoduletable > tbody > tr > td, .hotitems .innermoduletable td {background:#fff !important; border-bottom:#ccc 1px solid;}
div#maincontent table:nth-child(1) {margin:0 !important;}
div#maincontent div.menu_login table {margin: auto !important;}

div#maincontent table:nth-child(1) > td:nth-child(1) {min-width:200px;}

div#maincontent table:nth-child(2) > td:nth-child(1) {min-width:200px;}
div#dfp-leaderboard {margin:0 !important;}
.recentitems {width:210px;}
.recentitems .innermoduletable {width:210px;}
.recentitems .innermoduletable div {width:210px;}
.recentitems .innermoduletable div a {font-weight:bold; font-size:13px;}

.pt5.ng-scope {background:#fff;}
.geekcentral_rightcol div.geekcentral_module:first-child {margin:0px !important;}

.quickbar_link a {font-size: 12px;}
div.quickbar_pager {margin-bottom:0;}

td:nth-of-type(2) > table > tbody > tr > td:nth-of-type(2) {display:none;}

table.collection_table {background:#fff;}
}
