css:
@-moz-document domain("gradescope.com") {
.text-file {
    background: black !important;
    font-size: 22px !important;
    color: #999 !important;
    max-width: 100%;
}
.text-file-body .text-file-line {
    height: 30px;
    line-height: inherit;
}
.text-file-body .text-file-line pre {
    height: 100% !important;
    line-height: 100% !important;
}
pre {
    color: #999 !important;
    font-family: "Lucida Console", Monaco, monospace !important;
}
pre span {
    font-family: "Lucida Console", Monaco, monospace !important;
}
pre span[style*="color: #505050"] {
    color: #999 !important;
}
pre span[style*="color: #586e75"] {
    color: #6898a6 !important
}
.text-file .commentThread {
    background-color: snow !important;
    margin-bottom: 7px !important;
}
.text-file .commentThread p  {
    color: black !important;
    font-size: 18px !important;
}
.text-file .commentThread pre  {
    color: #16869c !important;
    font-size: 18px !important;
}
.text-file pre::selection, .text-file span::selection {
  background-color: #FFFFE0 !important;
  color: black !important;
}
a {
    text-decoration: underline !important;
}
#grade-submission #submission {
    margin-right: 295px !important;
}
#grade-submission #rubric {
    width: 295px !important;
    overflow: scroll !important;
}
.programmingAssignmentViewer {
    max-width: 100% !important;
}
.codeComment--textarea {
    height: 130px;
}
.text-file-line-number {
    display: none;
}
.text-file-line::before {
    user-select: none;
    height: 20px;
    line-height: 20px;
    width: 40px;
    text-align: center;
    margin-right: 5px;
    border-right: 1px solid rgba(74, 74, 74, 0.12);
    font-family: monospace;
    position: absolute;
    left: 0;
    top: 0;
    content: attr(data-line-number);
}
}
