css:
@-moz-document url("https://facebook.com"), url("https://www.facebook.com/") {
body {
	background-image:url(https://i.ytimg.com/vi/SkgTxQm9DWM/maxresdefault.jpg);
	background-size:cover;  
 	background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: center;     
}

div[role=main] {    
	visibility: hidden;
}

div[role=complementary],
div[role=navigation] {    
    background-color: #fff;
    opacity:0.9;
}


.__fb-dark-mode div[role=complementary],
.__fb-dark-mode div[role=navigation] {    
    background-color: #333;
    opacity:0.9;
}

.UIPage_LoggedOut div[role=main] {
    visibility: visible;
}
}
