css:
@-moz-document domain("progtest.fit.cvut.cz") {
a.navLink {
    color: #c2c2c2;
    padding-right: 8px;
}

tbody tr td {
    background-color: rgba(0,0,0,0.2) !important;
    border-style: solid !important;
    border-top-width: 0 !important;
    border-left-width: 0 !important;
    border-right-width: 0 !important;
    border-color: rgba(0,0,0,0.6) !important;
    border-bottom-width: 1px !important;
}

body {
    background-color: #181818;
    color: #d11414;
    border-color: #000000;
    background-image: url(data:image/gif;base64,R0lGODlhBAAEAKECABERESQkJP///////yH5BAEKAAIALAAAAAAEAAQAAAIFhB6nhlIAOw==);
}

td.rBox {
    background-color: rgba(0,0,0,0.2);
    border-style: solid;
    border-color: #000000;
    padding-top: 5px;
    padding-bottom: 5px;
    color: #c6c6c6;
    border-radius: 0px 5px 5px 0px;
    border-left-width: 0;
    width: 1%;
    border-bottom-width: 1px;
    border-right-width: 1px !important;
    border-top-width: 0;
}

td.lBox {
    background-color: rgba(0,0,0,0.2);
    border-style: solid;
    border-color: #000000;
    padding-top: 5px;
    padding-bottom: 5px;
    padding-left: 20px;
    color: #c6c6c6;
    border-radius: 5px 0px 0px 5px;
    border-right-width: 0;
    width: 40%;
    border-bottom-width: 1px;
    border-top-width: 0;
    border-left-width: 0;
}

span.menuListDis {
    color: #c4c4c4;
}

td.lrtCell.ac img {
    background-color: #AAAAAA;
    border-style: none;
    border-radius: 20px;
    opacity: 0.8;
}

#main {
    background-color: rgba(0,0,0,0);
    border-right-width: 1px;
    border-color: #000000;
    border-style: solid;
    border-top-width: 0;
    border-left-width: 0;
    border-bottom-width: 1px;
    border-radius: 5px;
}

.outBox {
    background-color: rgba(0,0,0,0);
    border-style: none;
    border-color: #000000;
    border-top-width: 0;
    border-left-width: 0;
    border-bottom-width: 1px;
    border-right-width: 1px;
    border-radius: 5px;
}

td.rtbCell {
    border-style: solid;
    border-color: #000000;
    border-left-width: 0;
    border-top-width: 0;
    border-bottom-width: 1px;
    border-right-width: 1px;
}

td.lrtCell.ac {
    border-style: solid;
    border-color: #000000;
    border-top-width: 0;
    border-bottom-width: 0;
    border-left-width: 0;
    border-right-width: 1px;
}

td.lrbCell.ac {
    border-style: solid;
    border-color: #000000;
    border-top-width: 0;
    border-left-width: 0;
    border-bottom-width: 0;
    border-right-width: 1px;
}

td.Cell {
    border-style: solid;
    border-color: #000000;
    border-right-width: 1px;
    border-bottom-width: 1px;
    border-top-width: 0;
    border-left-width: 0;
    border-radius: 0px 0px 5px 5px;
}

td.al.ltbCell {
    padding-left: 8px;
}

select.std.fw {
    width: 100;
    border-color: #000000;
    border-style: solid;
    border-top-width: 0;
    border-left-width: 0;
    border-bottom-width: 1px;
    border-right-width: 1px;
    border-radius: 5px 5px 0px 0px;
    background-color: rgba(0,0,0,0.2);
    color: #c4c4c4;
}

td.lrtbCell {
    border-style: none;
    background-color: rgba(0,0,0,0);
    padding: 0;
}

option {
    background-color: rgba(0,0,0,0.2);
    color: #D11414;
}

span.navLink b {
    padding-left: 8px;
}

a.butLink {
    color: #eb0303;
}

td.mBox {
    background-color: rgba(0,0,0,0.2);
    border-style: solid;
    border-color: #000000;
    border-bottom-width: 1px;
    border-right-width: 0;
    border-top-width: 0;
    border-left-width: 0;
}

div.hdrDis {
    border-right-width: 1px;
    border-bottom-width: 1px;
    border-color: #000000;
    border-style: solid;
    border-top-width: 0;
    border-left-width: 0;
    background-color: rgba(0,0,0,0.2);
}

div.but2 {
    background-color: rgba(0,0,0,0);
    border-style: solid;
    border-bottom-color: #000000;
    border-right-color: #000000;
    border-top-color: #515151;
    border-left-color: #515151;
    border-bottom-width: 1px;
    border-top-width: 1px;
    border-left-width: 1px;
    border-right-width: 1px;
    border-radius: 5px;
}

div.but1 {
    border-style: none;
    border-radius: 5px;
    border-color: #000000;
    background-color: rgba(0,0,0,0.2);
    border-top-width: 1px;
    border-left-width: 1px;
    border-bottom-width: 0;
    border-right-width: 0;
}
td.rtbCell {
    border-right-width: 1px;
    border-color: #000000;
    border-bottom-width: 1px;
    border-top-width: 0;
    border-left-width: 0;
}

td.rbCell {
    border-style: solid;
    border-color: #000000;
    border-left-width: 0;
    border-bottom-width: 1px;
    border-right-width: 1px;
    border-top-width: 0;
}

input.updButton {
    background-color: rgba(0,0,0,0);
    border-style: none;
    color: #d11414;
}

div.outButton.w120 {
    border-style: solid;
    border-radius: 5px;
    border-color: #000000;
    background-color: rgba(0,0,0,0.4);
    border-top-width: 1px;
    border-left-width: 1px;
    border-bottom-width: 0;
    border-right-width: 0;
}

input.std.w200 {
    background-color: rgba(16,16,16,0.4);
    border-style: solid;
    border-color: #000000;
    border-left-width: 0;
    border-top-width: 0;
    border-right-width: 1px;
    border-bottom-width: 1px;
}

td.rtCell {
    border-style: solid;
    border-color: #000000;
    border-right-width: 1px;
    border-bottom-width: 1px;
    border-top-width: 0;
    border-left-width: 0;
}

td.lrtCell.ac {
    border-style: solid;
    border-color: #000000;
    border-top-width: 0;
    border-left-width: 0;
    border-bottom-width: 1px;
    border-right-width: 1px;
}

td.lrbCell.ac {
    border-style: solid;
    border-color: #000000;
    border-top-width: 0;
    border-left-width: 0;
    border-bottom-width: 1px;
    border-right-width: 1px;
}

#ldap3 > td > table > tbody > tr > td {
    background-color: rgba(0,0,0,0);
    border-style: none;
}
}
