css:
@-moz-document url-prefix("http://fate-go.cirnopedia.org/") {
div#main {
background: rgb(25,25,25)
}

div.border {
border-color: rgb(0,0,0);
background-color: rgb(0,0,0)
}

div.update-body {
background-color: rgb(23,23,23);
color: rgb(143,143,143)
}
.update-body {
        border-top-color: rgb(90,90,90);
        border-left-color: rgb(90,90,90);
        border-right-color: rgb(90,90,90);
        border-bottom-color: rgb(90,90,90);
    }
td.profile{
      background-color:rgb(45,45,45) !important;
}
td.desc.free{
        background-color: #2d2d2d !important;
    color: red;
}
table#rounded-corner:hover tbody tr td.profile {
    background-color: rgb(45,45,45) !important;
}
#rounded-corner tr.divider td {
        border-top-color: rgb(90,90,90) !important;
    }
element.style {
    width: 45%;
}
table#rounded-corner tbody tr td.profile, table#rounded-corner tbody:hover tr td.profile{
    font-weight: 700;
    font-size: 13px;
    text-align: left;
    padding: 10px;
    background-color: rgb(45,45,45);
}
table#rounded-corner:hover tbody tr td.profile {
    background-color: rgb(45,45,45);
}

div.update-body span {
color: black;
    text-shadow:
    -1px -1px 0 #FFF,
    1px -1px 0 #FFF,
    -1px 1px 0 #FFF,
    1px 1px 0 #FFF,  
    -2px 0 0 #FFF,
    2px 0 0 #FFF,
    0 2px 0 #FFF,
    0 -2px 0 #FFF; 
}

div.bullet1 {
background: none
}

div.quote {
background: rgb(50,50,50)
}

#rounded-corner th {
background: rgb(45,45,45);
color: rgb(150,150,215);
border-left-color: rgb(90,90,90)
   
}


#rounded-corner td {
background: rgb(29,29,29);
color: rgb(190,190,190);
border-top-color: rgb(90,90,90);
border-right-color: rgb(90,90,90);
border-bottom-color: rgb(90,90,90);
border-left-color: rgb(90,90,90);        
}

#rounded-corner tr.divider1 td {
border-top: rgb(12,12,12)
}

 


#rounded-corner tfoot td {
background: rgb(45,45,45);
border-color: rgb(90,90,90)
}

div.footerwrap {
background-color: rgb(25,25,25)
}

table#rounded-corner tbody tr td.profile {
    font-family: 'Droid Serif';
    font-weight: 700;
    font-size: 13px;
    text-align: left;
    padding: 10px;
    width: 15%;
    background-color: rgb(45,45,45);
}


nav#top.mb div.title-episode {
background-color:rgb(43,43,43);
color: rgb(200,200,200);
text-shadow: none;
border: rgb(12,12,12)
}

div li {
color: rgb(150,180,200)
}

span.rng {
color: #17c4f0
}
span.str {
color: #ff5a5a
}

#rounded-corner tbody tr:hover td {
background-color: rgb(60,60,60)
}
    
#rounded-corner tr td a:hover {
color: rgb(2,253,253)
}

span.blue {
color: rgb(2,225,225)
}
    
 
#rounded-corner tbody tr.story td {
        background-color: rgb(65,52,29)
    }
#rounded-corner tbody tr.story:hover td {
        background-color: rgb(104,83,45)
    }
    

#mboard td.empty a {
        background-color: rgb(40,40,40)
           
    }
#mboard td.start a {
        background-color: rgb(40,40,40);
        color: #FF1616
    }
#mboard td.start:hover a {
        background-color: rgb(80,80,80)
    }
#mboard td.normal a {
        background-color: rgb(40,40,40);
       color: #FF1616
    }
#mboard td.normal:hover a {
        background-color: rgb(80,80,80)
    }
#mboard td.story a {
        background-color: rgb(40,40,40)
    }
#mboard td.story:hover a {
        background-color: rgb(80,80,80)
    }
#mboard .columnhead {
        background: none;
        color: rgb(12,184,203)
    }
#mboard th {
        background: none;
        color: rgb(12,184,203)
    }
table#mboard tbody tr td.free a{
        background-color: rgb(40,40,40);
            color: #FF6666
    }
table#mboard tbody tr td.free:hover a{
        background-color: rgb(80,80,80);
            color: #FF6666
    }    
table#mboard tbody tr td.normal a {
        color: #FF1616
    }
table#rounded-corner tbody tr.free td {
        background-color: rgb(43,43,43);
            color: #FF6666
    }
table#rounded-corner tbody tr.free:hover td {
        background-color: rgb(80,80,80);
            color: #FF6666;
          }
table#chart tr div {
        background-color: rgb(52,52,52);
            color: rgb(201,136,236)
    }
table#chart tr div:hover {
        background-color: rgb(80,80,80)
    }
    
span {
        color: rgb(201,136,236)
    }
    table#chart tr div span.str {
        color: #FF5555
    }
        table#chart tr div span.rng {
        color: rgb(4,169,232)
    }
        table#chart tr div span.dex {
        color: rgb(13,183,4)
    }

span.bullet_top {
        background: none;
    }
nav#mini li a {
        background: none;
       border-bottom-color: rgb(100,100,100);
        border-left-color: rgb(100,100,100);
        border-right-color: rgb(100,100,100);
        border-top-color: rgb(100,100,100);
    }

div.wrapper {
        background: rgb(25,25,25)
    }
div.title {
        color: #807ed6
    }
#rounded-corner.nohover tbody:hover tr td {
    background-color: rgb(29,29,29)
}
#rounded-corner tbody tr td.set {
        background-color: rgb(31,31,31)
    }
#rounded-corner tbody tr:hover td.set {
        background-color: rgb(65,65,65)
    }
    
div.treeborder {
        background: none;
    }
table#skilltree tr div {
        background-color: rgb(65,65,65);
        border-color: rgb(135,135,135)
    }    
table#skilltree tr div a {
        color: rgb(200,200,200)
    }
table#skilltree tr div:hover {
        background-color: rgb(100,100,100)
    }    
table#skilltree tr td div.lock {
        background-color: rgb(65,65,65)
    }
td {
        color: rgb(53,150,255)
    }
#rounded-corner tr td.empty {
        background-color: rgb(31,31,31)
    }
#rounded-corner tbody tr td.sel {
        background:rgb(31,31,31)
    }
#rounded-corner tbody tr:hover td.sel {    
        background: rgb(65,65,65)
    }

}

@-moz-document url("http://fate-go.cirnopedia.org/servant_all.php#nav") {
element.style {
    width: 85%;
}
div.border {
    border-color: rgb(90, 90, 90);
    background-color: rgb(25, 25, 25);
}
}

@-moz-document url("http://fate-go.cirnopedia.org/guide_faq.php#nav") {
