css:
@-moz-document domain("github.com") {

    body .header {
        color: #333;
        background-color: #f5f5f5;
        border-bottom: 1px solid #e5e5e5;
    }
    body .header-logo-invertocat {
        color: #333;
    }
    body .header-logo-invertocat:hover,
    body .header-logo-invertocat:focus {
        color: #4078C0;
    }
    body .header-nav-link {
        color: #333;
    }
    body .header-nav-link:hover, body .header-nav-link:focus {
        color: #4078C0;
    }
    body .header .header-search-scope,
    body .header .header-search-wrapper.focus .header-search-scope {
        font-size: 12px;
        color: #767676;
        border-right: 1px solid #eee;
    }
    body .header .header-search-wrapper {
        min-height: 0;
        padding: 0;
        font-size: 14px;
        color: #333;
        vertical-align: middle;
        background-color: #fff;
        border: 1px solid #ddd;
        box-shadow: inset 0 1px 2px rgba(0,0,0,0.075);
    }
    body .header .header-search-wrapper.focus {
        border-color: #51a7e8;
        outline: none;
        box-shadow: inset 0 1px 2px rgba(0,0,0,0.075),0 0 5px rgba(81,167,232,0.5);
        background-color: #fff;
    }
    body .header-nav-link:hover .dropdown-caret, 
    body .header-nav-link:focus .dropdown-caret {
        border-top-color: #4078C0;
    }
    body .header .header-search-input {
        font-size: 12px;
        color: #333;
    }
    body .header .header-search-input::-webkit-input-placeholder {
        color: #333;
    }
    body .header .header-search-input::-moz-placeholder {
        color: #333;
    }
    body .header .header-search-input:-ms-input-placeholder {
        color: #333;
    }
    body .header .header-search-input:focus::-webkit-input-placeholder {
        color: #cacaca!important;
    }
    body .header .header-search-input:focus::-moz-placeholder {
        color: #cacaca!important;
    }
    body .header .header-search-input:focus:-ms-input-placeholder {
        color: #cacaca!important;
    }
    body .notification-indicator .mail-status {
        border: 1px solid #f5f5f5;
        width: 12px;
        height: 12px;
        background-image: -webkit-linear-gradient(#1cafff, #0086ce);
        background-image: linear-gradient(#1cafff, #0086ce);
    }
}
