css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ent.unistra.fr") {
    #portalPageBodyColumns{
        -webkit-animation:name 2s infinite;
    }

    @-webkit-keyframes name {
        0% {background-color: pink;}
        10% {background-color: violet;}
        20% {background-color: purple;}
        30% {background-color: blue;}
        40% {background-color: green;}
        50% {background-color: brown;}
        60% {background-color: orange;}
        70% {background-color: red;}
        80% {background-color: yellow;}
        90% {background-color: white;}
        100% {background-color: pink;}
    }
    
    .up #portalPageHeaderInner{
        background-image: url("https://s-media-cache-ak0.pinimg.com/originals/7e/a2/1d/7ea21dac7278dc1eab6d5363730c8769.png");
        background-size: 200px 120px;
        background-color: white;
   }
    div #portalPageHeaderInner{
       background-color: white;
        background-image: none;
    }
    #portalPageBarLogin{
        float:right;
    }
    .up #portalWelcomeInner{
        background-image: url("https://media.giphy.com/media/tTyTbFF9uEbPW/giphy.gif");
        background-position: 60% 100%;
        width: 200px;
        height: 20px;
        margin-right: 200px;
    }
    .up #portalWelcomeInner p{
        width:500px;
        height: 50px;
        padding-left: 50px;
    }
    
    .up #portalPageBarLinks span#portalPageBarLogin a:hover {
        color: white !important;
        background: url("https://media.giphy.com/media/xTiTnLmaxrlBHxsMMg/giphy.gif") no-repeat scroll left center transparent;

        background-size: 110px 110px;
        background-position-x: 100%;
   }
     .up #portalPageBarLinks span#portalPageBarLogin a{
        color: #31a612 !important;
        background: url("https://encrypted-tbn1.gstatic.com/images?q=tbn:ANd9GcRkX8d-JcYrFq1TeAhM9H3BQzLWdg8eKtDdq1xk5cz8YrLVMPpxJQ") no-repeat scroll left center transparent;
        background-size: 110px 110px;
         background-position-x: 100%;
    }
    #portalPageBarLogin a{
        float: right;
    }
    #portalPageBarLogin span{
        color: black;
        margin-right: 20px;
    }
    #portalLogo{
        margin-left: 5%;
    }
    #portalPageBarLinks a{
        color: #f9098b;
    }
    
    .portal-flyout-container div ul{
        background-image: linear-gradient(#f9098b,pink);
    }
    .up #portalNavigation #portalNavigationSubrow{
        background-image: linear-gradient(#f9098b,pink);
    }
    .up .up-portlet-titlebar{
        background-image: linear-gradient(#f9098b,pink);
    }
    .up .dashboard .up-portlet-titlebar, .up .up-portlet-titlebar, .up .dashboard .locked .up-portlet-titlebar{
        background-image: linear-gradient(#f9098b,pink);
    }
    #portlet-controls-align a{
        background-image: linear-gradient(#f9098b,pink);
    }
    
    h1{
        color: #f9098b;
    }
    
    .fl-tabs-active a.portal-navigation-link{ 
        background-image: linear-gradient(pink,#f9098b);
    }
    
    body#portal{
        background-color: black; 
        background-image: url("https://ih1.redbubble.net/image.77008790.1997/sticker,420x460-pad,420x460,f8f8f8.lite-1u3.jpg") no-repeat scroll left center transparent;
    }
}
