@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("moodle3.unistra.fr") {
    
    .block_navigation,  block{
       background-image: url("https://vignette3.wikia.nocookie.net/log-horizon/images/c/c2/Tetora_sng_lr.png/revision/latest?cb=20160702005535");
       background-repeat: no-repeat;
       background-size: 100%;
        background-position-y: 5%;
       text-shadow: 1px 0 0 #000, 0 -1px 0 #000, 0 1px 0 #000, -1px 0 0 #000;
       color: white;    
    }
    
     .block_navigation a{
       text-shadow: 1px 0 0 #fff, 0 -1px 0 #fff, 0 1px 0 #fff, -1px 0 0 #fff;
       color: black;    
    }
    
    .navbar-inner{
        background-image : url("https://twitrheaders.com/wp-content/uploads/2014/06/TwitrHeaders.com-hatsune-miku-vocaloid-2-twitter-header.jpg");
        background-size: 10%;
        background-position-x: 80%;
        background-repeat: no-repeat;
    }
    
    #page-footer{
        background-image : url("http://x3m.co/wp-content/uploads/2012/08/saber_footer.png");
        background-size: 10%;
        background-position-x: 100%;
        background-repeat: no-repeat;
    }
    .footer-icon{
        margin-right: 50px;
    }
}

@-moz-document url("https://moodle3.unistra.fr/login/index.php") {
    #page-footer{
        margin-top: 100px;
        height: 75px;
    }
}


