css:
@charset "utf-8";

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(fi.muny.cz) {
  body, input {
    font-family: arial, helvetica, sans-serif !important;
  }

  #logo {
    background: url("https://i.imgur.com/XaFBUMC.png") !important;
  }

  #loginbox {
    background: url("https://i.imgur.com/j8n4laC.png") top left no-repeat !important;
  }

  h2 {
    color: #8c8c8c !important;
  }

  input.button:hover {
    background-color: #8c8c8c !important;
    color: #fff !important;
  }

  a:link {
    color: #666 !important;
    font-weight: bold !important;
    text-decoration-style: dotted !important;
  }

  a:hover {
    color: #444 !important;
    background-color: transparent !important;
  }

  a.plain, #newsletter {
    display: none !important;
  }

  #menu a {
    color: #fff !important;
    background: #444 !important;
    font-family: "Arial Black", Gadget, sans-serif !important;
  }

  #menu a:hover {
    background: #333 !important;
  }

  table td.fresh_1 {
    background-color: #1aff1a !important;
  }

  table td.fresh_2 {
    background-color:  #4dff4d !important;
  }

  table td.fresh_3 {
    background-color: #80ff80 !important;
  }

  table td.fresh_4 {
    background-color:  #b3ffb3 !important;
  }

  table>th>a {
    color: red !important;
  }

  tr:nth-child(odd) {
    background-color: #f2f2f2 !important;
  }

  tr:hover {
    background-color: #fff !important;
  }

  td.jmeno a {
    color: #8c8c8c !important;
    text-decoration: none !important;
  }

  td.jmeno a:hover {
    color: #444 !important;
  }

  #user a {
    color: #ededed !important;
    text-decoration: none;
  }

  #footer a, #muny_bar a {
    color: #fff !important;
    text-decoration: none !important;
  }

  #footer a:hover, #muny_bar a:hover {
    color: #e6e6e6 !important;
  }
}
