css:
@-moz-document domain("portal.ccu.edu.tw") {
body {
    background-color: #1a2632;
    color: #fff;
}
#container {
    background-color: #243447;
    border: none;
}
#content {
    background-color: #243447;
}
#ccu_logo,
#header {
    display: none;
}
#announcement {
    background-color: #888;
    color: #265da1;
}
.widget {
    border: 1px solid #6495ed;
}
.widget:hover {
    box-shadow: 2px 2px 2px #6495ed;
}
.widget-content a {
    color: #aaaaaa;
}
.widget-content {
    background-color: #243447;
}
.ec_panel_infoTitle {
    color: #aaaaaa;
}
.daily_course_day {
    background-color: inherit !important;
    color: #888888 !important;
}
#w_sso li:hover {
    background-color: #1a2632 !important;
}
#news_content {
    color: inherit !important;
}
.jPag-first,
.jPag-last {
    color: grey !important;
}
.login_header {
    color: #628cbd !important;
    font-family: "微軟正黑體";
}
input[type=text],
input[type=password] {
    background-color: #1a2632;
    color: white;
}
}
