css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("megashara.com") {
  
  
  body, html, .all, .comment-block.dark {
    background-color: #101010 !important;
    color: #A7A7A7; 
  }
  
  .box-style-corners, .back-bp1 {
    background-color:  #222222  !important;
    border: 0px !important;
  }
 
  .box-style-grey {
    background-color: #A7A7A7 !important;
  } 
  
  .back-bs1, 
  .back-bs2, 
  .back-bs3, 
  .back-bs4, 
  .back-left, 
  .back-right, 
  .back-ts1, 
  .back-ts2, 
  .back-bp1, 
  .back-bp2, 
  .back-bp3, 
  .back-bp4, 
  .back-bp5, 
  .hm2-back-left, 
  .hm2-back-right, 
  .hm1-back-left, 
  .hm1-back-right, 
  .fb-1, 
  .fb-2, 
  .fb-3, 
  .fb-4, 
  .back-bg1, 
  .back-bg2, 
  .back-bg3, .item-panel-top, .back-ipt1, .back-ipt2, .back-ipt3, .comments-block, .item-info-top, .posts-block {
    background: none !important;
  }
    
  .cell1,
  .info-table,
  .zero_color,
  .box-style-grey,
  .item-info-top  .info-table  .dark td{
    background-color: #101010 !important; 
    color:  #A7A7A7 !important;
    background-image: none !important;
  }
  
  .date {
    background: none !important;
    background-color:transparent !important;
  }
    
  [class^="list-style"] > div[style^="padding-bottom: 10px"] {
    color: #A7A7A7 !important;  
  }
 
  .markItUpMain {
    border: none !important;
  }
  
  .markItUpContainer {
    background: none !important;
  }
  
  .line span, .back-ts2 {
    border: none !important;
    display: none !important;
  }
  
  .line {
    height: 0px !important;
  }

}
