css:
@-moz-document url-prefix("https://discordapp.com/") {
.guild-channels .channel-text .channel-name:before {
    content: '#';
    margin-right: 0;
}
body {
    background: url(http://www.clipular.com/c/6650141702619136.png?k=3VbTdt2XfbjnjESs_2Hu-80ZzvQ);
    background-size: cover;
}
.embed .embed-author,
.embed .embed-footer,
.embed .embed-title {
    color: initial !important;
}
.system-message-icon {
    margin-left: 20px;
}
.user-popout .body .roles .member-roles {
    max-height: 240px;
    overflow-y: auto;
}
.upload-drop-modal .bgScale {
    background: grey;
}
.upload-modal {
    background-color: grey;
}
.upload-modal .footer {
    background-color: #fba200;
}
.upload-modal .footer .button {
    background-color: #fba200;
}
.upload-modal .footer .button-primary {
    background-color: #ffe700;
}
.user-popout .avatar-wrapper .avatar-popout {
    border: 4px solid lightgrey;
}
.user-popout .header {
    background: url(https://i.imgur.com/AMJKUUs.png) white;
}
.server-info button {
    background: lightgreen;
}
.server-info button:hover {
    background: grey;
}
.server-official {
    color: grey !important;
    text-shadow: none;
}
#pubs-footer {
    padding: 5px;
    background: grey;
}
#pubs-header {
    padding: 10px;
    background: grey;
}
#pubs-container {
    overflow: hidden;
    border: 1px solid white;
    border-radius: 6px;
    box-shadow: 1px 1px 1px lightgrey;
}
#friends {
    overflow: hidden;
    border-radius: 6px;
}
.confirm-message-modal .message-group {
    box-shadow: none;
}
.message-group .edit-message .edit-container-inner {
    margin-left: 103px;
}
.message-group .edit-message .channel-textarea-inner {
    border-color: white;
}
.message-group .edit-message {
    margin: 6px -26px 0 -97px;
}
.message-group .edit-message .edit-container-outer {
    margin-left: -103px;
}
.now-playing {
    background: grey;
}
.need-help-modal .header {
    background-color: grey;
}
.radio-theme input[type=radio]:checked~label {
    border-color: grey;
}
.checkbox .checkbox-inner input[type=checkbox]:checked+span {
    background-color: grey;
    border-color: grey;
}
.app,
.callout-backdrop,
.connecting,
.titlebar {
    margin: 30px;
    border-radius: 6px;
    overflow: hidden;
   
}
.chat .divider {
    margin: 25px 6px 25px 25px;
}
.chat .welcome-message {
    opacity: 0.7;
    padding: 0 25px;
    margin: 0 25px;
    display: inline-block;
    background: white;
    border-radius: 0 0 3px 3px;
    box-shadow: 1px 1px 1px lightgrey;
}
.guilds-wrapper {
    opacity: 1;
    background: #d54b0e;
    
}
.guilds-wrapper .guilds .friends-icon {
    background-image: url(https://en.gravatar.com/userimage/71026131/5979bcdf3ae67d3707084091e8749278.jpeg);
    background-size: cover;
    
}
.chat .empty-message {
    opacity: 1;
    margin-left: 25px;
    padding: 60px 25px 0 25px;
    display: inline-block;
    background: white;
    border-radius: 0 0 3px 3px;
    box-shadow: 1px 1px 1px lightgrey;
}
.chat .welcome-message .empty-message {
    display: none;
}
.scroller-wrap .scroller::-webkit-scrollbar-track-piece {
    border: none;
    background: inherit;
}
.scroller-wrap .scroller {
    margin-right: 6px;
}
.channel-members {
    opacity: 1;
     background-color: #cf8226;
}
.typing {
    width: 96%;
    border-radius: 3px;
    margin: 70px;
    margin-left: 5px;
    padding-left: 5px;
    
   
}
div.avatar-large {
    background-size: cover;
    width: 62.5px;
    height: 62.5px;
    border-radius: 6px;
    float: left;
    margin: 0 15px;
}
.message-group .comment .markup {
    color: black;
}
div.message-group {
    opacity: 1;
    background: white;
    margin: 10px 6px 0 25px;
    border-radius: 3px;
    box-shadow: 1px 1px 1px #ee8900;
    height: auto;
}
.message-group.is-local-bot-message {
    opacity: 1;
    background: white;
    margin: 10px 6px 0 25px;
    border-radius: 3px;
    box-shadow: 1px 1px 1px lightgrey;
    height: auto;
}
.chat form {
    margin: 4px 25px -5px 25px;
}
.channel-textarea-inner {
    margin: 0;
    border-radius: 3px;
    box-shadow: 1px 1px 1px white;
    
}
.private-channels:after {
    content: "Pesterchum";
    background: #ffe700;
    color: #23272a;
    font-size: 14px;
    font-weight: 600;
    display: flex;
    -webkit-box-align: center;
    align-items: center;
    padding: 10px;
    border-top: 1px solid #ffe305;
    flex-shrink: 0;
    
}
.chat .content {
    background-size: cover;
    background-color: #ffd00a
}
.chat>.title-wrap {
    overflow: hidden;
    border-radius: 6px 6px 0 0;
    box-shadow: 1px 1px 1px lightgrey;
    background-color: #d59b08;
}
.flex-vertical .channels-wrap {
    opacity: 1.00;
    overflow: hidden !important;
    border-radius: 0 6px 6px 0 !important;
    margin-right: 25px;
    background: #d59b08;
   
}
.image {
    border-radius: 3px;
}
.flex-vertical .flex-spacer .guild-channels-wrapper {
    border-radius: 1;
    overflow: hidden;
    background: #cf8226;
}
.flex-spacer .flex-vertical {
    overflow: hidden;
    background-color: #ffe305;
    border-radius: 6px;
}
.theme-light .hljs {
    color: white;
}
/* Better discord area */

.bd-detached-editor .app {
    width: 90%;
}
#bd-customcss-attach-controls {
    height: auto;
    border: 0;
    margin: 5px;
    background: #ebebeb;
}
#bd-customcss-detach-container {
    margin: 25px;
    padding-top: 7px;
    overflow: hidden;
    border-bottom: 0;
    border-radius: 6px;
    background: #d59b08;
}

div.account{
	background-color: #d59b08;
}  


div.scroller-wrap.fade.dark > div{
    background-color: black;
    background-image:none;
}

