css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("qalampir.uz") {
  body{background: #171717;}
  .top-slogn{display: none}
  .bg-top{display: none;}
  .statiya .img{border-radius: 0;}
  /*Slider display none*/
  .section-5{display: none}
  
  hr{border-color:#171717;}
  
  .statiya{border-bottom: none;}
  
  
  /*background*/
  .left-con, .section-7{background: #171717;}
  
  /*color*/
  .statiya .info h4 a{color:#009999;}
  body{color:#fff;}
  .statiya p{color:#f3f3f3;}
  
  .in-info{color:#eee;}
  .left-con .title{color:#00ffff;}
  
  .prev-article h4, .next-article h4{color:#009999;}
  .prev-article p, .next-article p{color:#00ffff;}
  
  
 /*comment*/
  .statiya-mini .info a{color:#009999;}
  .coments span, .statiya-mini .info span{color:#00ffff;}
  .coments .img, .statiya-mini .img{border-radius: 0;}
  
  /*button*/
  .tags a{background: #333;color:#009999;}
  .prev-article i, .next-article i{color:#00ffff;}
  
  /*disable share*/
  .podelitsa, .st-set{display:none;}
  
  /*footer*/
  .footer, .bg-buttom{display: none;}
  
}
