css:
@-moz-document domain("linustechtips.com") {
body,
input,
#ipsLayout_body,
.ipsPagination li a,
.ipsQuote,
#elUserLink_menu {
    background-color: #1e1e1e !important;
}
mark {
    background-color: #DB4105 !important;
}

div.vjs-time-control vjs-time-divider div span{
background-color: transparent !important;
}

.vjs-resolution-button-label{
    background-color: transparent !important;
}

.ipsDataItem_icon,
.ipsDataItem_main,
.ipsDataItem_stats,
.ipsDataItem_lastPoster,
.ipsWidget,
#elSearchFilters,
.ipsAreaBackground_light,
.ipsStreamItem,
.lmgComment_footer,
.lmg_header,
.ipsComment_author,
.cPost_contentWrap,
.ipsColumn,
.ipsQuote p span,
.advrep-profile,
#elProfileTabs_content,
.ipsAreaBackground,
#elProfileTabContent,
#ipsTabs_elSettingsTabs_setting_overview_panel {
    background-color: #111111 !important;
}
.ipsWidget_title,
.ipsType_sectionTitle,
.ipsButtonBar,
.ipsQuote_citation,
#elUserLink_menu li a:hover,
.ipsType_pageTitle,
.ipsType_normal {
    background-color: #333333 !important;
}
.ipsDataItem,
.ipsQuote {
    border-color: #DB4105 !important;
}
.ipsComment_author {
    border-left: 1px #DB4105 solid !important;
}
.lmgComment_header,
.cPost_contentWrap,
.lmgComment_footer {
    border-right: 1px #DB4105 solid !important;
}
.ipsDataItem_icon,
.ipsDataItem_main,
.ipsDataItem_stats,
.ipsDataItem_lastPoster,
.ipsPad,
.ipsComment_author,
.lmgComment_header {
    border-top: 1px #DB4105 solid !important;
}
.ipsType_sectionTitle + .ipsButtonBar,
.ipsComment_author,
.lmgComment_footer,
.ipsBreadcrumb.ipsBreadcrumb_top {
    border-bottom: 1px #DB4105 solid !important;
}
.ipsPagination li a,
#elUserLink_menu {
    border: 1px #DB4105 solid !important;
}
.ipsDataItem_meta p,
.ipsDataItem_stats_number,
.ipsType_blendLinks,
.ipsQuote,
span {
    color: #D8CDCD !important;
}
h1,
h2,
h3,
h4,
h5,
p,
.ipsType_sectionTitle,
input,
.ipsType_pageTitle,
.ipsType_medium,
.ipsBreadcrumb li,
.ipsResponsive_hidePhone span,
.cAuthorPane_info li,
.ipsQuote_citation,
.ipsDataItem_main,
.ipsGrid li {
    color: #FFFFFF !important;
}
a {
    color: #c9c9c9 !important;
    text-decoration: none !important;
}
a:hover {
    background-color: #db744c !important;
}
a span:hover {
    color: #DB4105 !important;
}
.ipsWidget_title,
.ipsType_sectionTitle,
.ipsButtonBar,
.ipsQuote_citation,
#elUserLink_menu li a:hover,
.ipsType_pageTitle,
.ipsType_normal {
    background-color: transparent !important;
}
.signatureShowMoreText.signatureToggle {
    background-color: rgb(219, 65, 5) !important;
}
.lmg_memberName {
    background-color: transparent !important;
}
.ipsFollow.ipsButton.ipsButton_light.ipsButton_verySmall a {
    background-color: #db744c;
}
.ipsApp .ipsButton_light {
    background: #db744c !important;
}
h2.ipsType_sectionHead {
    color: #525252 !important;
}
html[dir="ltr"] .ipsFollow .ipsCommentCount:after {
    border-color: transparent #db744c transparent transparent;
}
.ipsSpoiler_header.ipsSpoiler_open,
.ipsSpoiler_header,
.ipsSpoiler,
.ipsStyle_spoiler {
    background: #353535 !important;
}
.ipsDataItem_meta p,
.ipsDataItem_stats_number,
.ipsType_blendLinks,
.ipsQuote,
span {
    color: #c9c9c9 !important;
}
a:hover .ipsPager_title.ipsType_light.ipsTruncate.ipsTruncate_line {
    background-color: #111111 !important;
}
h2.ipsType_sectionTitle.ipsType_reset.cForumTitle {
    background-color: #111 !important;
}
p.noAwards.ipsType_light {
    color: #111 !important;
}
div.ipsList_reset.cForumList.ipsBox.ipsSpacer_bottom h2.ipsType_sectionTitle.ipsType_reset,
div.ipsBox .ipsType_sectionTitle.ipsType_medium.ipsType_reset.ipsClear,
div.ipsButtonBar.ipsPad_half.ipsClearfix.ipsClear {
    background-color: #111111 !important;
    color: #c9c9c9 !important;
}
.ipsButtonBar .ipsButtonRow li > a {
    background-image: -webkit-linear-gradient(top, #353535, #353535) !important;
    border: 1px solid rgb(219, 65, 5) !important;
}
div.ipsType_richText.ipsType_normal.ipsSpacer_top p span {
    background-color: transparent !important;
}
div.ipsQuote_contents.ipsClearfix p,
blockquote div.ipsQuote_contents.ipsClearfix p span {
    background-color: rgb(30, 30, 30) !important;
}
div ul.ipsTags.ipsList_inline li a span {
    background-color: #484848 !important;
}
a span {
    background-color: transparent !important;
}
}
