css:
@-moz-document domain("kotaku.com"), domain("io9.com"), domain("deadspin.com"), domain("gawker.com"), domain("gizmodo.com"), domain("jalopnik.com"), domain("jezebel.com"), domain("lifehacker.com"), domain("kinja.com"), domain("gawker-labs.com"), domain("theroot.com"), domain("fusion.net"), domain("theonion.com"), domain("avclub.com"), domain("earther.com"), domain("splinternews.com") {
/*Dark Background*/
body,
.global-nav,
.networknav__hovernav-wrapper,
.section-nav,
.editor,
.editor-toolbar .toolbar-formatting-controls,
.editor.quickreply .editor-buttons,
.editor-inner,
.reveal-modal,
.f-dropdown.content,
.featured-post__header,
.featured-post__meta-container,
.featured-post__asset-group:after,
.networknav__active-blogname,
.discussion-empty-state,
.author-bio,
nav,
.cLruyF,
.cxwEXV,
.item,
.kFjVfm,
.irHVOF,
.jjdtiB,
.dui-modal,
.dui-modal-body,
img-permalink-sub-wrapper,
.hfIcGc,
.dGKrNM,
.dryiyJ,
.eLSGXc,
.dMYtLe,
.eoQdAO,
.bGbRIe,
.hYuDWj,
.huxqqS,
.kDFvVa,
.cOHdFr,
.gMOvOZ,
.gxuPto,
.iNeiTC,
.lmCRMe,
.gIDval,
.elPtEE,
.jcBwHx,
#layout,
.ejPXgq,
.doUIsj,
.fpoEDn,
.iHpzjB,
.iYoMhr,
.eHEkan,
.dVsmLK,
.gotkrb,
.bOeIqm
{
    background: #222222 !important;
}

.reveal-modal .toggle-signup-login
{
    background: none !important;
}

blockquote,
.quickreply .editor-toolbar .toolbar-formatting-controls li.dropdown-select button,
.editor-toolbar .editor-toolbar-dropdown.f-dropdown,
.button--social,
.reveal-modal .key-box,
.read-more-container
.kiJlqc,
.bdAhbz,
.cKkzIz,
.hpWhLq,
.gbWnCZ,
.f-dropdown,
.toggle-signup-login,
.networknav__sitelist li:hover,
.jprGGH
{
    background: #666666 !important;
}

.frDmEQ,
.eXbWbk
{
    background: #444444 !important;
}

.networknav__search
{
    background-color: #222222 !important;
}

.networknav,
.unapproved-filter .filtered-view,
.unapproved-filter .unapproved-view
{
    background-color: #111111 !important;
}

/* Light text */
body,
text,
p,
h1,
h2,
h3,
h4,
h5,
h6,
span,
li,
.sidebar-module header h2 strong,
.meta__byline,
strong
{
    color: #e5e5e5 !important;
}

a
{
    color: #e6e6e6 !important;
}

.svg-icon,
.svg-icon.stroked,
#iconset-order-popular,
#iconset-chevron-bottom,
#iconset-bubble-plus,
#iconset-search
{
    stroke: #ffffff !important;
}

/* Dropdown Menus */
.global-nav,
.replies-wrapper .reply ,
.networknav__sitelist li.hover,
.networknav__hovernav-wrapper>div

{
    border-color: #aaa !important;
}

.networknav__sitelist li.hover
{
    background-color: #222222 !important;
}

/* Border changes */
.global-nav,
.replies-wrapper .reply
{
    border-color: #aaa !important;
}

.global-nav--permalink .topnav__logostrip .section-nav:before, 
.global-nav--scrollback .topnav__logostrip .section-nav:before,
.postlist__item:after
{
    background-color: #aaa !important;
}

.leftrailmodule, .sidebar-module {
    border-bottom: 1px solid #aaa !important;
}

a.discussion__button
{
    background-color: #666 !important;
}

.curation-mountain:not(.Headline):not(.Editor) {
    border-bottom: 1px solid #aaa !important;
}

.curation-module__item {
    border: 1px solid #aaa !important;
    -webkit-box-shadow: 0px 0px 0px #aaa !important;
    box-shadow: 0px 0px 0px #aaa !important;
    background-color: #333 !important;
}

.sidebar {
    border-right: 1px solid #aaa !important;
}

.trending-progress__segment {
    background-color: #666 !important;
}

/* Border removal */
.section-nav ul li a
{
    border-left: 0px !important;
    border-right: 0px !important;
}

.section-nav
{
    border-top: 0px !important;
    border-bottom: 0px !important;
}

.replies-wrapper .reply-footer-bar
{
    border: 0px !important;
}

/* Per site colors */
.blog-group-kotaku a,
.blog-group-kotaku h1
{
    color: #FBC000 !important;
}

.blog-group-deadspin a
{
    color: #00b3b3 !important;
}

.blog-group-deadspin a
{
    color: #249D9D !important;
}

.blog-group-jezebel a
{
    color: #EC1238 !important;
}

.blog-group-lifehacker a
{
    color: #94B330 !important;
}

.blog-group-anitay a
{
    color: #b30000 !important;
}

.blog-group-theroot a
{
    color: #509B22 !important;
}

.fusion .topnav__logostrip .logo svg
{
    stroke: #5c5cd6 !important;
}

.blog-group-fusion a
{
    color: #5c5cd6 !important;
}

.gizmodo .topnav__logostrip .logo svg
{
    stroke: #999999 !important;
}

.blog-group-gizmodo a
{
    color: #72CAFF !important;
}

.io9 a
{
    color: #B384AB !important;
}

.theonion .topnav__logostrip .logo svg
{
    stroke: #00a85b!important;
}

.blog-group-theonion a
{
    color: #00a85b!important;
}

.avclub .topnav__logostrip .logo svg
{
    stroke: #ffffff !important;
}

.blog-group-avclub a
{
    color: #5575ba !important;
}

.blog-group-earther a
{
    color: #0086cc !important;
}

.splinter .topnav__logostrip .logo svg
{
    stroke: #F85637 !important;
}

.blog-group-splinter a
{
    color: #F85637 !important;
}

/* Long article tweaks */
.post-cutoff__gradient
{
    background-image: linear-gradient(rgba(51, 51, 51, 0), #222222 100%) !important;
}

.f_new_linkpreview_css_on .referenced-wide,
.inset--story
{
    border: #000000 1px solid !important;
    background: #111111 !important;
    box-shadow: #000000 0 2px 5px !important;
}

.button--tertiary
{
    background-color: #666 !important;
    border-color: #aaa;
    color: #ccc;
}

.button--tertiary .svg-icon
{
    stroke: #fff !important;
    transition: stroke 0.4s ease-in-out;
}

.button--tertiary:hover .svg-icon
{
    stroke: #000 !important;
    transition: stroke 0.4s ease-in-out;
}

/* Add stars to the site */
.section-nav ul li a,
.global-nav--permalink .topnav__logostrip .section-nav ul li a,
.global-nav--scrollback .topnav__logostrip .section-nav ul li a
{
    display: inline-block !important;
    font-size: 12px;
}

.topnav .section-nav ul li:before
{
    content: "★";
    position: relative;
    top: 1px;
}

.topnav__logostrip + .section-nav ul li + li:before,
.topnav__logo-wrapper + .section-nav ul li + li:before
{
    display: inline-block !important;
    content: "★" !important;
    position: relative !important;
    top: 1px !important;
}

.global-nav--permalink .topnav__logostrip.topnav__logostrip--sectionnav,
.global-nav--scrollback .topnav__logostrip.topnav__logostrip--sectionnav
{
    -ms-flex-pack: center !important;
    justify-content: center !important;
}

/* Sidebar Slideshow */
.reel__feature,
.reel__item
{
    border: 0px solid #fff !important;
}

.trending-description-wrapper
{
    background-image: none !important;
}

.sidebar-meta__wrapper
{
    background-color: transparent !important;
}

.custom-kinja__editorial-item .meta strong,
.sidebar-item .meta strong
{
    color: #aaaaaa !important;
}

.custom-kinja__editorial-item .meta,
.sidebar-item .meta
{
    color: inherit !important;
}

/* icon tweaks */
.bdAhbz svg,
.jJcIcd,
.kuzIhV,
.gbWnCZ 
{
    stroke: #fff;
}
}
