css:
@-moz-document url-prefix("http://www.dreamanimes.com.br/"), url-prefix("http://www.dreamanimes.com.br/inicio"), url-prefix("http://www.dreamanimes.com.br/lista"), url-prefix("http://www.dreamanimes.com.br/lancamentos"), url-prefix("http://www.dreamanimes.com.br/assistir"), url-prefix("http://www.dreamanimes.com.br/temporadas"), url-prefix("http://www.dreamanimes.com.br/loja"), url-prefix("http://www.dreamanimes.com.br/painel"), url-prefix("http://www.dreamanimes.com.br/forum"), url-prefix("http://www.dreamanimes.com.br/equipe"),  url-prefix("http://www.dreamanimes.com.br/eventos"), domain("www.dreamanimes.com.br"), domain("dreamanimes.com.br") {

#novo-menu.navbar-default {
    background: none;
}

.navbar-header img {
    margin-bottom: 20px;
    width: 100% !important;
    height: auto !important;
}

.navbar-nav {
    margin: 10px -10px;
    background-image: url(http://i.imgur.com/Cfgc858.png); 
    background-repeat: repeat-x;
    background-size: contain;
    -moz-box-shadow: 0px 0px 14px #6590ed;
    -webkit-box-shadow: 0px 0px 14px #6590ed;
    box-shadow: 0px 0px 14px #6590ed;
    width: 100%;
}

.navbar-nav>li {
    margin-top: 0px;
    padding-top: 0px;
}

#novo-menu.navbar-default .navbar-nav>li>a {
    background: none;
    padding: 13px 2.3em;
    min-height: 50px;
}

.header_titulo {
    background-size: contain;
    border-bottom: 2px solid #393d98;
    background-image: url(http://i.imgur.com/Cfgc858.png)!important;
}
/* url(http://www.dreamanimes.com.br/_cdn/burn/box_one_title.png);*/
#conteudoPrincipal .article {
    box-shadow: 0px 0px 14px #ffffff;
    background-color: #393d98;
}

#novo-menu.navbar-default .navbar-nav>li>a:active, #novo-menu.navbar-default .navbar-nav>li>a:hover, #novo-menu.navbar-default .navbar-nav>li>a[aria-expanded=true] {
    background-color: #6590ed!important;
}

h3 {
    font: 500 1.10em 'Arial',sans-serif!important;
    background: url(http://i.imgur.com/Cfgc858.png);
}

.small-box {
    background: none;
    -moz-box-shadow: 0px 0px 14px #ffffff;
    -webkit-box-shadow: 0px 0px 14px #ffffff;
    box-shadow: 0px 0px 14px #ffffff;
	
}
@media(min-width:1px){body{background: url(http://i.imgur.com/Ith2W4A.jpg); background-size: cover;}
.tudo, #grupo-cover {
    width: 740px;
}
}
@media (min-width: 992px){
.tudo, #grupo-cover  {
    width: 960px;
}
}

@media (min-width: 1200px){
.tudo, #grupo-cover  {
    width: 1160px;}
}


.tudo  {
        margin-right: auto;
        margin-left: auto;
        padding-left: 10px;
        padding-right: 10px;
}


#grupo-cover {
    border-bottom: 0px !important;
    margin: auto;
    margin-bottom: 10px;
}

#grupo-cover .container div {
    width: 98% !important;
}

.small-box a[href^="/assistir/"] {
    font-size: 95%;
}

.btn {
    background-color: #00e5ff;
    border: none;
}

input[type=password],input[type=text] {
    background-color: #000000;
    border-color: #00e5ff;
    text-shadow: none;
}

.nav .dropdown button {
    background: none;
    margin-top: 9px;
}

.dropdown-menu {
    background-color: #0938d5;
}
.header_window {
    background: #0938d5 !important;
}

#users_online {
    border: 0px !important;
}

.footer {
    background: url(http://i.imgur.com/GrXZqiT.png);
    background-size: contain;
}

.video-texto {
    background: none;
    font-size: 96%;
	/*border-color: #6590ed;*/
}

.lista-videos {
    background-color: #6590ed;
}
@media (min-width: 768px){
.anime-box img {
    max-height: 130px;
    min-height: 130px;
}
}

.anime-detalhes {
    border: 2px solid #ffffff;
}

.anime-caixa {
    border-color: #393d98;
    box-shadow: none;
}

.anime-dialanc, .anime-nome {
    border-color: #393d98;
}
::-webkit-scrollbar-track {
background-color: #00e5ff;
}
input[type=submit] {
    background-color: #6590ed;
}
nav.navbar-collapse.navbar-menubuilder.collapse.in {
    background-color: #393d98 !important;
    background-repeat: repeat-x;
    background-size: contain;
} /*#202020*/

#novo-menu.navbar-default .navbar-nav>li>a {
    background-color: transparent !important;
}

.small-box { background-color: #2201c5; }

.navbar-header img{ content: url("http://i.imgur.com/lbETjyj.png") }

.navbar-nav>li>.dropdown-menu {
    background-color: #0938d5 !important;
}

.dropdown-menu.rb::after{border-bottom:6px solid #6590ed}

.pagination>.active>a, .pagination>.active>span, .pagination>.active>a:hover, .pagination>.active>span:hover, .pagination>.active>a:focus, .pagination>.active>span:focus{    background-color: #6590ed; }
.pagination>li>a, .pagination>li>span{  background-color: #393d98;}
.pagination>li>a:hover, .pagination>li>span:hover, .pagination>li>a:focus, .pagination>li>span:focus{    background-color: #6590ed;}

/* Info Descrição anime */
.tooltip-inner{background-color: #6eb6f6 !important; }
}
