css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.babelio.com") {
  

  body {background: #000000; color: #fff;}
  header {background-color: #636363;}
  header .menu .menu_link {color: #C0BABA;}
  header .menu .menu_sep {width: 0px;background-color: #000;}
  header .menu .menu_item_sep {width: 2px;background-color: #ff9d38;}
  a {color: #86fc08;}
  a:hover {color: #d4fc08;}
  .text {color: #fff;}
  .text a {	color: #d4fc08;}
  .text a:hover {	color: #d4fc08;}
  .livre_header .livre_header_links a {color: #ff9d38;}
  .corps {background-color: #636363;}
  .livre_resume {color: #fff;}
  .grey_light {color: #fff;}
  .tiny_links {color: #636363;}
  .tiny_links.current, .tiny_links:hover {color: #fff;}
  .titre {background-color: #393939; color: #fff;}
  .list_livre h3 {color: #fff;}
  .contribution_progress {background-color: #000;}
  .sep {height: 1px; background-color: #393939;}
  .rateit .rateit-preset {background: url(http://www.babelio.com/images/rateit_star.gif); color: rgb(165,255,0);}
  .rateit-font .rateit-empty {color: rgb(90,90,90);}
  .side_l {	border-right: 1px solid #393939;}
  .tags a {color: #C0BABA;}
  .tags a:hover {color: #d4fc08;}
  .mes_livres_form .mes_livres_top {background-color: rgb(90,90,90);}
  .mes_livres_form .mes_livres_top .mes_livres_top_style.current, .mes_livres_form .mes_livres_top .mes_livres_top_style:hover {background-color: rgb(255, 157, 56); color: rgb(255, 255, 255);}
  .mes_livres_form .mes_livres_top .mes_livres_top_style.current, .mes_livres_form .mes_livres_top .mes_livres_top_style {background-color: rgb(0, 0, 0); color: rgb(255, 255, 255);}
  table {background-color: #636363;}
  tr {background-color: #636363;}
  td {background-color: #636363;}
  td.row1 {background-color: #636363;}
  td.row2 {background-color: #636363;}
  td.row3 {background-color: #636363;}
  td.row3Right {background-color: #636363;}  
  .gen, .genmed, .gensmall {color: #fff;}
  a.topictitle:hover {text-decoration: none;color: #fff;}
  a.topictitle {text-decoration: none;color: #d4fc08;}
  .postdetails {font-size: 11px;color: #fff;}
  .nav {font-weight: bold;font-size: 12px;color: #86fc08;}
td.cat, td.catHead, td.catSides, td.catLeft, td.catRight, td.catBottom {background-image: url(https://dl.dropboxusercontent.com/u/15020967/images/cellpic1.gif);}
  th {background-image: url(https://dl.dropboxusercontent.com/u/15020967/images/cellpic1.gif);}
  textarea {color:#000;}
  .toolbar_left {background-color: rgb(0, 0, 0);}
  .livre_edit {	background-color: #666;}
}
