css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("resource://activity-streams/data/content/activity-streams.html#/"), 
url("resource://activity-streams/data/content/activity-streams.html#/HOME") {

    body {
        background: #393F4C;
        color: #383E49;
        font-family: sans-serif;
    }
    
    .search-wrapper .search-label {
        top: 4px;
    }

    .search-wrapper button {
        display: none;
    }

    .spotlight-item {
        box-shadow: 0 1px 0 0 rgba(255, 255, 255, 0.1);
        color: #eee;
        background: #171B1F;
    }
 
    .search-wrapper input {
        height: 44px;
        width: 584px;
        line-height: 44px;
        border: none;
        border-radius: 2px;
        box-shadow: 0 2px 2px 0 rgba(0,0,0,0.16), 0 0 0 1px rgba(0,0,0,0.08);
        margin-bottom: 30px;
        font: 16px sans-serif;
        background-color: #272B35;
        background-repeat: no-repeat;
        background-size: 24px 24px;
        padding-left: 36px;
        outline: none;
        color: #eee;
        transition: background-color ease .15s;
     }   
    
    .search-wrapper input:hover {
        outline: none;
        box-shadow: 0 3px 8px 0 rgba(0,0,0,0.16), 0 0 0 1px rgba(0,0,0,0.08);
        transition: background-color ease .15s;
    }
    
    .search-wrapper input:focus {
        outline: none;
        box-shadow: 0 3px 8px 0 rgba(0,0,0,0.16), 0 0 0 1px rgba(0,0,0,0.08);
        background-color: #171B1F;
    }
    
    ::placeholder {
        color: transparent;
    }
    
    .search-wrapper .search-container {
        background: #272B35;
        margin-top: -2px;
        border: 1px solid #272B35;
        font-size: 12px;
        box-shadow: 0 5px 10px rgba(0, 0, 0, 0.1);
    }
    
    .search-wrapper .search-container .search-title {
        color: #eee;
        padding: 5px 10px;
        background-color: #272B35;
    }
    
    .search-wrapper .search-container section {
        border-bottom: 1px solid #666;
    }
    
    .search-wrapper .search-container .search-suggestions ul li a {
        color: #eee;
    }
    
    .search-wrapper .search-container .search-suggestions ul li a:hover, .search-wrapper .search-container     .search-suggestions ul li a.active {
        background: #5675B9;
    }
    
}
