css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pictorem.com") {
  
  /*** Palette ***/
  :root {
    --default-bkgd-color: #222;
    --bkgd-color-2: #282828;
    --bkgd-color-3: #333333;
    --bkgd-color-4: #666;
    --default-color: #aaaaaa;
    --color-2: #fafafa;
  }
  
  body {
    background-color: var(--default-bkgd-color);
    background-image: none;
    color: var(--default-color);
  }
  
  /** 1st Top Header **/
  .flat-mega-menu > ul > li > a {
    color: var(--default-color);
  }
  
/*   .drop-down .full-width .hover-fade {
    background-image: none !important;
  } */
  
  /** 2nd Top Header **/
  td[background="http://www.pictorem.com/images/back_collectionmenu.png"] {
    background-image: none;
  }
  
  /** 3rd Top Header **/
  table[style="background-color:rgba(255, 255, 255, 0.5);"] {
    background-color: var(--bkgd-color-2) !important;
  }
  
  table[style="background-color:rgba(255, 255, 255, 0.5)"] {
    background-color: var(--bkgd-color-2) !important;
  }
  
  img[src="http://www.pictorem.com/images/icon_thumb_square_off.png"], 
  img[src="http://www.pictorem.com/images/icon_thumb_big_off.png"], 
  img[src="http://www.pictorem.com/images/icon_thumb_slideshow_off.png"] {
    filter: invert(80%);
  }
  
  .tx2 {
    color: var(--default-color);
  }
  
  .txmedium {
    color: var(--color-2);
  }
  
  /*** Page No. buttons ***/
  div[style="background-color:#cb7f9c;cursor:default;;width:18px;height:14px;margin:3px;padding:5px;color:#FFFFFF;text-align:center;vertical-align: middle;float:left;"] {
    background-color: var(--bkgd-color-3) !important;
  }
  
  div[style="background-color:#de256d;cursor:pointer;;width:18px;height:14px;margin:3px;padding:5px;color:#FFFFFF;text-align:center;vertical-align: middle;float:left;"] {
    background-color: var(--bkgd-color-2) !important;
  }
  
  div[style="background-color:#de256d;cursor:pointer;width:18px;height:14px;margin:3px;padding:5px;color:#FFFFFF;text-align:center;vertical-align: middle;float:left;"] {
    background-color: var(--bkgd-color-2) !important;
  }
  
  /*** Picture boxes ***/
  .box {
    background-color: var(--bkgd-color-2);
  }
  
  .box .entry-desc {
    background-color: var(--bkgd-color-3);
    border-top-color: var(--bkgd-color-3);
  }
  
  .box .entry-content-cnt {
    border-bottom-color: var(--bkgd-color-3);
  }
  
  .tx1 {
    color: var(--default-color);
  }
  
  /**** Picture Page ****/
  
  /* Title */
  font[style="text-shadow:1px 1px #FFFFFF"] {
    text-shadow: none !important;
  }
  
  /* 'Purchase Print in: ' */
  div[style="Border-radius:5px;background-color:#e84b82;padding:4px;box-shadow: 1px 1px 6px rgba(255, 255, 255,1);width:auto;text-align:center"] {
    box-shadow: none !important;
  }
  
  div[style="Border-radius:5px;background-color:#e84b82;padding:4px;box-shadow: 1px 1px 6px rgba(255, 255, 255,1);width:80px;text-align:center"] {
    box-shadow: none !important;
  }
  
  div[style="Border-radius:5px;background-color:#e84b82;padding:4px;box-shadow: 1px 1px 6px rgba(255, 255, 255,1);width:120px;text-align:center"] {
    box-shadow: none !important;
  }
  
  /* Hearts and Likes */
  div[style="border-radius:4px;background-color:#dbdbdb;padding:4px;width:50px;height:27px;text-align:center"] {
    background-color: var(--bkgd-color-3) !important;
  }
  
  div[style="border-radius:4px;background-color:#dbdbdb;padding:4px 0px 4px 4px;width:55px;height:27px;text-align:center"] {
    background-color: var(--bkgd-color-3) !important;
  }
  
  /** White bkgd spaces **/
  td[bgcolor="#efeeea"] {
    background-color: var(--bkgd-color-1);
  }
  
  table[bgcolor="#f6f6f6"] {
    background-color: var(--bkgd-color-1);
  }
  
  .tabs-menu li, .tabs-menu li.current {
    background-color: var(--bkgd-color-1);
    border-color: var(--bkgd-color-3);
  }
  
  table[bgcolor="#f4f4f4"] {
    background-color: var(--bkgd-color-1);
  }
  
  /* Comments */
/*   ._5yk1 {
    background: var(--bkgd-color-2) !important;
  } */
  
  /**** http://www.pictorem.com/collectionmaincat.html ****/
  
  table[bgcolor="#FFFFFF"] {
    background-color: var(--bkgd-color-1);
  }
  
}
