css:
@-moz-document domain("www.deviantart.com") {

.mcbox-thumb-deviation {
	overflow: hidden;
}

.mcbox-thumb-deviation .mcb-line {
    opacity: 0;
    position: absolute;
    z-index: 30;
    bottom: 0;
    left: 0;
    right: 0;
    color: #fff;
    padding: 1em;
    background: rgba(0,0,0,0.6);
    transition: transform 0.2s ease-out, opacity 0.2s ease-out;
    transform: translate(0px, 20px);
}

.mcbox-thumb-deviation:hover .mcb-line {
    opacity: 1;
    transform: translate(0px, 0px);
}

.mcbox-thumb-deviation .mcb-line a {
	color: #fff !important;
}

.mcbox-thumb-deviation .mcb-commentcount {
	display: none;
}

body div.bubbleview div.messages div.mcbox-thumb-deviation div.ch-ctrl {
	border-radius: 0;
    background: #b2bdaf !important;
    border: none !important;
}

div.mcbox-thumb-deviation div.mcbox-inner-thumb-deviation,
div.mcbox-thumb-deviation div.mcbox-inner-thumb-status,
div.mcbox-thumb-deviation div.mcbox-inner-thumb-stack {
	margin: 0 !important;
}

.mcbox-thumb-deviation .stream .tt-w {
	border: 0;
}

div.mcbox-thumb.mcbox-thumb-deviation div.stream,
div.mcbox-thumb.mcbox-thumb-deviation div.coverstream,
div.mcbox-full-modbox div.stream {
	margin-top: 0;
}

.mcbox-thumb-deviation div.stream div.tt-a span.tt-w,
.mcbox-thumb-deviation div.stream div.tt-a div.tt-w,
.mcbox-thumb-deviation div.rs-customicon div.rs-customicon-cont {
	width: auto;
    margin: 0;
}

.mcbox-thumb-deviation a.thumb img {
	height: 280px;
    width: 210px;
    object-fit: cover;
}

.mcbox-thumb-deviation:hover a.thumb img {
	object-fit: contain;
}

.mcbox-thumb-deviation .shadow > a.thumb > img:not(.lit) {
	box-shadow: none;
}

.mcbox-thumb-deviation .mcdx {
	display: none !important;
}

.mcbox-thumb-deviation div.stream div.tt-a:not(.tt-fh) span.tt-w span.shadow {
	height: auto;
    padding: 0;
}

div.mcbox-thumb-deviation span.mcx,
div.mcbox-thumb-deviation span.mcdx {
	background: rgba(0,0,0,0.5);
    top: 0;
    right: 0;
	width: 26px;
    height: 26px;
    text-align: center;
    font-size: 1.6em;
    color: #fff;
}

div.mcbox-thumb-deviation span.mcx::before {
	content: "Ã—";
    display: inline;
}

}
