css:
@namespace url(http://www.w3.org/1999/xhtml);

/*LastUpdate: 20191012 By special-feng@QQ.com*/

@-moz-document domain("share.dmhy.org") {
body {
        font-size: 16px !important;
        font-family: Microsoft Yahei;
        /*加大页面字体字号，并解决一下新版FF字体渲染不正确的问题*/
    }
    
    .nav_title {
        color: #E7FF00;
        height: 26px;
        font-weight: 700;
        /*导航栏日期显示效果优化*/
    }
    
    .jmd {
        display: none;
        /*隐藏原有三日节目单*/
    }
    
    .jmd_base {
        display: table !important;
        /*恢复一周节目单*/
    }
    
    .jmd_base > tbody > tr > th {
        padding: 1px;
        width: 100px;
        background-color: #7E99BE;
        color: white;
        /*修复星期显示*/
    }
    
    .jmd_base tbody tr {
        background-color: #cdf;
        /*奇数行背景颜色*/
    }
    
    .jmd_base tbody tr:nth-child(even) {
        background-color: #fff !important;
        /*偶数行背景颜色*/
    }
    
    .jmd_base tbody tr td a {
        padding: 1px 2px;
        border: solid #224477 1px;
        border-radius: 3px;
        margin: 0 2px;
        text-decoration: none;
        /*恢复项目边框*/
    }
    
    .jmd_base tbody tr td a:hover {
        color: #FFA500;
        /*项目悬停颜色*/
    }
    
    [height="228"] {
        display: none !important;
        /*屏蔽底部广告*/
    }

    .topic-nfo > p > span > img,.topic-nfo > p > img {
         width: 100%!important;
         height: auto!important;
         /*修复帖子页面预览图宽度超出的问题*/
    }
}
