@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("google.com") {

/* Fixed Highlight for Unread Message Rows */
tr[class^="xweT7d QhHSYc"] {background: #CDDEFF!important;}

/* Background color of selected message row*/
.rfza3e {background-color : #D1FFD0 !important;
color : #000000 !important;}

/* Hover color for read message rows */
table.N2mZ7b tr.AnqB9d:hover, table.tlc tr.rr:hover { background-color: rgb(196,236,255) !important;}

/* Hover color for unread message rows */
table.N2mZ7b tr.QhHSYc:hover, table.tlc tr.ur:hover { background-color: rgb(196,236,255) !important;}

/* Hover color for selected rows */
/* Disabled only because I don't personally use this */
/* table.N2mZ7b tr.rfza3e:hover, table.tlc tr.ur:hover { background-color: rgb(172,203,220) !important;} */

}
