css:
@-moz-document domain("fallenlondon.storynexus.com") {

ul#cards.hand-size5 li {
    width: 96px;
    margin: 0 2px 40px 0;
}

ul#cards.hand-size5 li a {
    width: 88px;
    height: 115px;
    padding: 4px;
    background-size: 672px 123px;
}

ul#cards.hand-size5 li a.goldCard   { background-position:  -96px 0; }
ul#cards.hand-size5 li a.silverCard { background-position: -192px 0; }
ul#cards.hand-size5 li a.bronzeCard { background-position: -288px 0; }
ul#cards.hand-size5 li a.blackCard  { background-position: -384px 0; }
ul#cards.hand-size5 li a.redCard    { background-position: -480px 0; }
ul#cards.hand-size5 li a.purpleCard { background-position: -576px 0; }

ul#cards.hand-size5 li input[type="image"] {
    width: 88px;
    height: 115px;
}

ul#cards.hand-size5 li input.discard_btn {
    bottom: -10px;
    left: 26px;
}

ul#cards.hand-size5 li input.discard_btn:disabled {
    display: none;
}

ul#cards.hand-size5 .hand-slot-empty {
    width: 88px;
    height: 115px;
    padding: 4px;
}

}
