css:
@-moz-document domain("worldwidetorrents.eu") {
body {
    background: #191919;
    color: #fff;
}
input[type="text"],
input[type="insert"] {
    background: #a9a9a9;
}
#main {
    border-style: double;
    border-color: #323232;
    width: 99%;
    margin-left: auto;
    margin-right: auto
}
#mpbox:hover {
    background: #323232 !important;
}
.w3-theme-d2 {
    background: #4C4C4C !important;
}
.w3-theme-d4 {
    background: #323232 !important;
    margin: 0.2em !important;
}
.w3-theme-l1 {
    background: #323232 !important;
}
.w3-responsive tr {
    background: #333 !important;
}
.w3-centered {
    background: #050505 !important;
}
.textstyle {
    color: #7f7f7f !important;
    font-family: 'Space Mono', monospace !important;
    font-size: 88%;
}
.table_mb .table_head {
    background: #000 !important;
}
button.w3-btn {
    border: 0px;
    background-color: #323232 !important;
    border-radius: 0px;
    box-shadow: none;
    outline: none;
}
input[type="submit"],
input[type="reset"],
input[type="button"] {
    border: 0px;
    background-color: #323232 !important;
    border-radius: 0px;
    box-shadow: none;
    outline: none;
    padding: 6px 16px;
}
textarea[name="msg"] {
    background: #a9a9a9;
}
.p-title th {
    background: #a9a9a9 !important;
}
.comment-details {
    background: #717171 !important;
}
.f-title th,
.f-title a {
    color: #fff !important;
    background: #323232 !important;
}
.ttable_head {
    background: #323232 !important;
}
.table_head {
    background: #323232 !important;
}
legend {
    background: #323232 !important;
}
.f-cat td {
    background: #a9a9a9 !important;
}
.tabs a {
    background: #a9a9a9 !important;
}
.table-hover tr:hover {
    background: #323232 !important;
}
.main td {
    color: #000 !important;
}
.myFrame-content > center:nth-child(1) > img:nth-child(1) {
    /*[[hide-banner]]*/;
}
#userphoto img:hover {
    cursor: pointer;
    box-shadow: 1px 1px 2px white;
}
a[href*="https://worldwidetorrents.eu/shoutbox.php?del="] {
    color: white !important;
    text-decoration: none;
}
font[color="#000099"] {
    color: #4C4CFF !important;
}
.shoutbox_alt a, .shoutbox_noalt a {
    text-decoration: none;
}
.shoutbox_alt a:hover, .shoutbox_noalt a:hover {
    text-decoration: underline;
}
.myBlock-content > div[class^="slidingDiv"] > p > font[color="blue"] {
    color: #fff !important;
}
}
