css:
/*@namespace url(http://www.w3.org/1999/xhtml);*/
@-moz-document domain("www.zhihu.com") {
  .AppHeader-inner .Icon {
    fill: #eee !important;
  }
  
  .AppHeader-userInfo { top: 25px; }

  .QuestionHeader { padding: 5px 0; }
  .QuestionHeader-follow-status { 
    position: relative;
    top: 50%;
    transform: translateY(-50%); /* or try 50% */
  }
  .Tag, .VoteButton { line-height: 24px; height: 24px; }
  a.QuestionMainAction { line-height: 44px; height: 44px; }
  .VoteButton { padding: 0 5px;}
  .QuestionHeader-title, .ContentItem-title { font-size: 18px; font-weight: 450; }
  .QuestionHeader-detail>.QuestionRichText,
  .AnswerItem-extraInfo,
  .AuthorInfo-badge,
  .Button,
  .List-headerText,
  .List-headerOptions,
  .NumberBoard-item,
  .QuestionFollowStatus-people-tip,
  .CopyrightRichText-richText,
  .ContentItem-time,
  .VoteButton,
  .QuestionMainAction,
  .ProfileHeader-info,
  .Tabs-link,
  .Tag-content,
  .AnswerAuthor-user-headline,
  .AnswerAuthor-item-value,
  .PushNotifications-item,
  .EmptyState-inner,
  .PortraitItem,
  .ContentItem-meta,
  body{ font-size: 13px; }
  
  .PushNotifications-item { padding: 10px 15px; }
  .PushNotifications-tab { height: 40px; }
  
  .Card-header { height: 44px; }
  .Card-headerText, .UserItem-title { font-size: 14px; }
  .Card-section { padding: 8px 0; }
  
  .Profile-lightItem, .Profile-footerOperations { margin: 0 16px; }
  
  .List-item { padding-top: 10px; padding-bottom: 10px; }

  .Tabs-link { padding: 10px 0; }
  .Tabs-meta { color: #888; }
  
  .ProfileHeader-name { font-size: 22px;}
  .AnswerAuthor-user-name { font-size: 18px;}
  .ProfileHeader-headline { font-size: 16px;}
  
  .QuestionHeader-title, .Tabs-link {
    transition: color .3s, font-weight .3s;
  }
  
  .is-shown .QuestionButtonGroup button:nth-child(2),
  .is-shown .MemberButtonGroup button:nth-child(2) {
    background-color: #eee;
  }
  .is-shown .QuestionButtonGroup button:nth-child(2):hover,
  .is-shown .MemberButtonGroup button:nth-child(2):hover {
    background-color: #fff;
  }
  
  .is-shown .QuestionHeader-title {
    color: #eee;
  }
  
  .is-shown .Tabs-link {
    color: #eee;
  }

  .is-shown .Tabs-meta {
    color: #ddd;
  }
  
  .AppHeader-navItem:hover {
    color: #fff;
  }
  .AppHeader-navItem {
    font-size: 14px;
    color: #eee;
  }
  
  .AppHeader-inner { height: 50px;}
  
  .AppHeader {
    border:none; box-shadow: none;
    background-color: #0d79d1;
  }
  
  .Sticky--holder {display: none;}
  
  .NumberBoard-value { font-size: 15px; }
  [data-za-module="RelatedLives"] { display: none; }
  .Footer { text-align: center; padding: 0; }
  .List-header { padding: 4px 0; }
}
