css:
@-moz-document domain("www.youtube.com") {
/* "Unsubscribed" button or hover over subscribed button */
.yt-uix-subscription-button,
.yt-uix-button-subscribed-unbranded:hover,
.yt-uix-button-subscribe-branded,
.yt-uix-button-subscribed-branded:hover {
  background-color: #e62117;
  color: #fff;
}

/* "Subscribed" button or hover over unsubscribed button */
.yt-uix-button-subscribe-unbranded:hover,
.yt-uix-button-subscribed-unbranded,
.yt-uix-button-subscribe-branded:hover,
.yt-uix-button-subscribed-branded {
  background-color: #090;
  color: #fff;
}
.yt-uix-button-subscribed-branded:before {
  background-color: rgba(0,144,0,0.6);
}

/* Video result hover highlighting */
.yt-lockup-content:hover,
.yt-lockup-content:hover div,
.yt-lockup-content:hover h3,
.yt-lockup-dismissable:hover,
.yt-lockup-tile:hover {
  background-color: #ddd;
}
.standalone-collection-badge-renderer-icon,
.yt-ui-ellipsis {
  background-color: rgba(0, 0, 0, 0);
}
}
