css:
@-moz-document domain("news.google.com") {
.body {
    background: whitesmoke !important;
    background-color: whitesmoke !important;
}
.google-one-wrapper {
    position: relative;
    z-index: 9999;
}
a {
    color: #305da5 !important;
}
a:hover {
    color: #1976D2 !important;
    text-decoration: none !important;
}
/********** TOP BANNER **********/

.gb_Se {
    background: #e5e5e5;
}
.search {
    position: relative;
    z-index: 999;
}
.kd-appbar .kd-appname-wrapper {
    margin-left: 75px;
}
/***** LOGO *****/

.gb_Se>.gb_R {
    height: 70px;
}
#gbq1 {
    max-width: 204px !important;
    min-width: 200px !important;
}
.gb_1b {
    position: absolute;
    padding: 5px 30px 0 70px;
}
.gb_3b .gb_5b {
    background-position: -10px -158px;
    height: 59px;
    width: 59px;
    background-size: 75px 2000px;
}
.gb_3b {
    background: #fff;
    border-radius: 50%;
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.15);
    transition: box-shadow 0.2s;
}
.gb_3b:hover {
    box-shadow: none;
    transition: box-shadow 0.2s;
}
/***** END LOGO *****/
/***** SEARCH BAR *****/

#gbqfw,
#gbqfqw {
    height: 44px;
    border-radius: 5px;
    background: #FFF;
}
#gbqfw {
    /* main bar */
    
    box-shadow: 0.25px 0.5px 3px 0 rgba(0, 0, 0, 0.2);
    transition: box-shadow 0.5s, border 0.5s, background 0.5s;
}
#gbqfw:hover,
#gbqfw:active {
    border: none;
    box-shadow: 1.5px 1.75px 6px 0 rgba(0, 0, 0, 0.15) !important;
}
#gbqfqw,
#gbqfqw:hover {
    border: none;
    border-top-right-radius: 0;
    border-bottom-right-radius: 0;
    box-shadow: none;
}
#gbqfqwb {
    height: 44px;
    top: 0;
    overflow: hidden;
}
.gsfe_b {
    border: none !important;
}
#gbqfq {
    /* text input */
    
    position: absolute;
    height: 34px;
    width: 90%;
    top: 6px;
    left: 0px;
    margin: 0px;
    padding: 0 20px;
    font-size: 20px;
    transition: background 0.5s;
}
#gbqfab {
    height: 100%;
}
#advanced-search-toggler {
    top: 14px;
}
#advanced-news-search-popup,
.advanced-search-wrapper,
.asp,
.gbar-advanced-search-wrapper {
    left: 435px !important;
    top: 60px !important;
    z-index: 999;
}
#gbqfb {
    /* search button */
    
    border: none;
    border-radius: 5px;
    border-bottom-left-radius: 0;
    border-top-left-radius: 0;
    height: 44px;
}
#gbqfb:focus,
#gbqfb:focus:hover {
    -webkit-box-shadow: none;
    box-shadow: none;
    background: #3e75d4;
}
.gbqfi,
.gbqfi:hover {
    /* search icon */
    
    margin: 7px;
}
.gssb_c {
    /* autofill popup */
    
    z-index: 999;
}
/***** END SEARCH BAR *****/

#page-header {
    background: #e5e5e5;
    box-shadow: 0 2px 8px 0 rgba(0, 0, 0, 0.3);
}
.kd-appbar {
    border: none;
}
.goog-menu {
    z-index: 999;
}
/********** LEFT NAV **********/

.centered .nav {
    width: 152px;
}
.nav,
.left-nav-pinned {
    background: whitesmoke !important;
    border-right: 1px solid #ddd;
}
/********** CENTER COLUMN **********/

.content-pane {
    background: whitesmoke;
}
.basic-title {
    background: #fff;
    border-radius: 5px;
}
.basic-title .text,
.basic-title a {
    color: #dd4b39 !important;
}
.top-stories-section,
.story-section {
    margin: 10px 5px;
    background: #fff;
    border: 1px solid #ddd;
    border-radius: 5px;
    box-shadow: 0 0.5px 1px 0 rgba(0, 0, 0, 0.1);
}
/********** RIGHT COLUMN **********/

.rt-col .small-section {
    margin-top: 10px;
    background: #fff;
    border: 1px solid #ddd;
    border-radius: 5px;
    box-shadow: 0 0.5px 1px 0 rgba(0, 0, 0, 0.1);
}
#s_WEATHER_GADGET {
    margin-top: 15px;
    background: transparent;
    border: none;
    border-radius: 0;
    box-shadow: none;
}
#s_WEATHER {
    margin-top: 10px;
    background: transparent;
    border: none;
    border-radius: 5px;
    box-shadow: none;
}
.pog .eqp-pane-wrapper,
.pog .select-source-autocomplete-wrapper,
.pog .eqp-pane-footer {
    background-color: transparent;
}
.pog .eqp-pane-header {
    padding: 10px;
    height: 16px;
    border-bottom: 1px solid #ebebeb;
}
.pog .section-label {
    font-weight: normal;
    font-size: 16px;
    color: #dd4b39 !important;
}
/********** FOOTER **********/

.footer {
    background-color: whitesmoke;
}
}
