css:
@-moz-document domain("modiran.com") {
/* body {
      background: #fff;
  }
   */
  .wallPage__editor {
      background: #4285F4;
      padding: 30px;
      border-radius: 5px;

  }

  .wallPage__editor ul li ,.wallPage__editor ul li.upload:hover h5 ,.wallPage__editor ul li.share:hover h5 {
      color: #fff;
  }

  .content .avatar img {
      border-radius: 50%;
  }

  .whoToFollow ul li img {
      border-radius: 50%;
  }

  .wallPage_posts ul li .content {
      border-radius: 5px;
      box-shadow: 0 0 0 0;
      border: 1px solid #e2e2e2;
  }

  .userBox {
      background-color: #286090;
      border-radius: 5px;
      box-shadow: 0 0 0 0;
  }

  .content {
      box-shadow: 0 0 0 0;
      border-radius: 5px;
      border: 1px solid #e2e2e2;
  }

  .userBox .userBoxFoot ul {
      border-radius: 0 0 5px 5px;
      background-color: #fff;
      overflow: hidden;
  }

  .userBox .userBoxContent .userBoxAvatar img {
      border-radius: 50%;
      border: none;
      background: rgba(255, 255, 255, 0.4);
  }

  .userBox a {
      color: #fff;
  }

  .userBox .userBoxContent .userBoxInfo h2 {
      color:#fff;
  }

  .wallPage_posts ul li .user img {
      border-radius: 50%;
  }

  .wallPage_posts ul li .user {
      border-radius: 50%;
  }

  .btn {
    border: 0;
    -webkit-border-radius: 2px;
    border-radius: 2px;
    background-clip: padding-box;
  }
  .btn.btn-second {
    background-color: #e26657;
  }
  .btn.btn-gray {
    background-color: #8a8a8a;
    color: #fff;
  }
  .btn.btn-lg {
    -webkit-border-radius: 3px;
    border-radius: 3px;
    background-clip: padding-box;
  }
  .btn.btn-primary {
    background-color: #286090;
  }
  .btn.btn-primary:active {
    background-color: #1d4568;
  }
  .btn.btn-transp {
    background-color: transparent;
    color: #0c1d2b;
  }
  .btn.btn-transp:hover {
    background-color: #286090;
    color: #fff;
  }
  .btn.btn-raised {
    box-shadow: 0 3px 6px 0 rgba(0, 0, 0, 0.1);
  }
  .btn.btn-lg {
    padding: 15px 20px;
  }

  .form-control {
    font-weight: 300;
  }
  .form-control.raised {
    box-shadow: 0 3px 6px 0 rgba(0, 0, 0, 0.1);
  }
  .form-control:focus {
    box-shadow: 0 0 0 0 transparent;
  }
  .form-control.input-lg {
    padding: 15px 20px;
    height: 56px;
  }

  .btn.save, .btn-company, form .btn[type="submit"],.whoToFollow ul li .option .btn {
      box-shadow: 0 0 0 0;
  }

 body:before {
    content: "";
    display: block;
    top:0;
    left:0;
    right:0;
    height: 450px;
    position: absolute;
    background: #c5cddd;
    background: -webkit-linear-gradient(to bottom, #c5cddd , #F2F3F5);
    background: linear-gradient(to bottom, #c5cddd , #F2F3F5);
}
}
