css:
@-moz-document domain("academic.ru") {

body {
	background-color: #070707 !important;
	background: #090909 !important;
}

/* Header style */
#header { /* Header autohide */
	-moz-transition-duration: .4s !important;
	-webkit-transition-duration: .4s !important;
	z-index: 1 !important;
	position: fixed !important;
	top: -118px;
	width: 90.5%;
	margin: auto;
	border: #46a 3px solid !important;
	opacity: 0.2 !important;
	background-color: #111 !important;
	min-height: 118px;
}

#header:hover {
	opacity: 100 !important;
	background-color: #111 !important;
	top: 1px;
	height: 118px;
	padding-bottom: 0px !important;
	border: #223 1px solid !important;
	border-bottom: #223 3px solid !important;
	box-shadow: 0px 0px 90px 20px rgba(0, 0, 0, 0.9);
}

/* Supplementary header mods */
#logo {
	-webkit-filter: invert(90%) hue-rotate(180deg) contrast(90%) brightness(55%);
}

#site-title {
	padding-top: 0px !important;
}

/* Search bar */
#search-box {
	background: #445 !important;
	border: none !important;
	box-shadow: none !important;
}

#search-box > form,
#search-query {
	background: #445;
	border: none !important;
	box-shadow: none;
}

#search-button {
	background: #445 !important;
	border: 2px solid #223 !important;
	color: #fff;
}

#dic-query,
#search-query,
.translate-lang {
	background: #223 !important;
	color: #fff !important;
}

#search-tabs li {
	border: none !important;
	box-shadow: none !important;
}

#search-tabs li,
#search-tabs li a {
	height: 22px!important;
	background: #223 !important;
	color: #999 !important;
}

#search-tabs li.active {
	height: 20px!important;
	border-bottom: 2px solid #444 !important;
	border-bottom-left-radius: 0px;
	border-bottom-right-radius: 0px;
	background: #445 !important;
	color: #fff !important;
}

#search-tabs li.active,
#search-tabs li.active a {
	height: 20px!important;
	border-bottom-left-radius: 0px;
	border-bottom-right-radius: 0px;
	background: #445 !important;
	color: #fff !important;
}
/* Content restyle */

#content {
	padding-left: 20px;
	padding-right: 20px;
	margin-top: 0px;
	margin: auto;
	width: 90%;
	border-left: 1px dotted #223;
	border-right: 1px dotted #223;
}

#page {
	top: -5px !important;
	background-color: #0c0c0c;
	color: #aaa;
	width: 90% !important;
	padding-right: 10px;
	padding-top: 5px !important;
	margin: auto !important;
}

h3 {
	border-right: 2px dotted #226 !important;
	padding-right: 20px;
	padding-left: 18px;
	margin-left: -30px;
	padding-top: 2px;
	margin-top: 2px;
	display: inline-block;
}

#lang,
#contextmenu {
	background-color: #151515;
	color: #aaa;
}

#lang>ul>li:hover,
#contextmenu a:hover {
	background-color: #334;
	color: #fff;
}

#article span.w {
	cursor: pointer;
	border-bottom: 1px dotted #222;
}

#article span.w:hover {
	cursor: pointer;
	border-top: 1px solid #444;
	border-bottom: 1px solid #444;
	background-color: #222;
}

a {
	color: #88d;
	text-decoration: none;
}

a:visited {
	color: #454575;
	text-decoration: none;
}

#article a span.w,
#article a:hover span.w {
	cursor: help;
	border-bottom: none!important;
}

/* Misc mods */
iframe,
#ad_bottom,
#ad_right,
#footer,
#adv_kod_frame,
#video_banner {
	display: none;
}

ul.smm {
	display: none;
}

ad_top2 {
	margin: 0px 0px 0px 0px !important;
	display: none;
}

.breadcrumb {
	padding-top: -10px;
	margin-bottom: -10px;
}

#content > div:nth-child(1) > h2 {
	margin-bottom: -10px;
	float: right;
}

#article > dl > div > dt {
	display: none;
}

#article > dl > div > dd > dl > div > dt {
	padding-top: -20px;
}

#article > dl > div > dd > dl > div > dd > div > span.dic_comment {
	padding-top: -80px;
}
/* Hide the footer */
}
