css:
@-moz-document domain("app.youneedabudget.com") {
/* Blue grey + Teal accent

50    #ECEFF1
100   #CFD8DC
200   #B0BEC5
300   #90A4AE
400   #78909C
500   #607D8B
600   #546E7A
700   #455A64
800   #37474F
900   #263238

A700  #00BFA5   */
/**********************************************************/
/* YNAB Toolkit fixes start*/

.ynabtk-category-panel,
.ynabtk-payee-panel {
    overflow: auto;
}
.ynab-table-5-col .ynab-table-col {
    color: white;
}
.toolkit-activity-row:hover {
    background-color: #78909C;
}
.ynabtk-reports-filters {
    background-color: #546E7A;
    color: white;
}
.ynabtk-reports {
    background-color: #455A64;
}
.noUi-tooltip {
    background-color: #37474F;
}
.noUi-handle {
    background-color: #37474F;
    box-shadow: none;
}
.noUi-base {
    background-color: #78909C;
}
.noUi-connect {
    background-color: #00596f;
}
.ynabtk-reports-headers {
    color: white;
}
.highcharts-background {
    fill: #455A64;
}
text {
    fill: white !important;
    text-shadow: none !important;
    font-weight: lighter !important;
    font-size: 1.2em !important;
    text-decoration: none !important;
}
.income-vs-expense-report {
    color: white;
    font-weight: normal;
}
.inflows .ynabtk-header-row,
.inflows .ynabtk-footer-row {
    background-color: rgba(67, 160, 71, 0.8);
}
.outflows .ynabtk-header-row,
.outflows .ynabtk-footer-row {
    background-color: rgba(211, 60, 45, 0.8);
}
.highcharts-button text {
    fill: black !important;
}
.flaticon.stroke.expand-vertically-1:before,
.flaticon.stroke.expand-vertically:before,
.flaticon.stroke.V7:before {
    cursor: pointer;
}
.budget-table {
    background-color: #37474F;
}
.toolkit-goalindicator {
    color: white;
}
[style*="gradient"] > div,
[style*="gradient"] > div > div,
[style*="gradient"] > div > span,
[style*="gradient"] > li > div,
[style*="gradient"] > li > div > span {
    color: black !important;
    /* Fix for Pacing categories */
}
/*YNAB Toolkit fixes end */
/**********************************************************/

.budget-table-row.is-sub-category > .budget-table-cell-budgeted > button:hover {
    background-color: #37474F !important;
}
.budget-table-container .budget-table-header {
    background-color: #37474F !important;
}
.budget-table-container .budget-table-header li {
    color: white !important;
    font-weight: bold;
}
.modal-payee-list-button,
.budget-table-row.is-sub-category.is-checked .budget-table-cell-budgeted:hover .currency {
    color: white !important;
}
.manage-payees .modal-payees-list li:hover {
    background-color: #546E7A !important;
}
.modal-import-choose-file strong,
.modal-import-choose-file p {
    color: white;
}
.modal-import-choose-file em {
    color: rgba(255, 255, 255, 0.7);
}
.modal-overlay.active > .modal {
    background-color: #37474F;
}
.content {
    background-color: #263238;
}
.modal-account-reconcile-difference {
    color: white;
}
.modal-account-dropdown .modal {
    color: white;
}
.ynab-grid-body-empty {
    border-bottom: none !important;
}
.ynab-grid-body-row {
    border-bottom: 1px solid #37474F;
}
.modal-popup ul.modal-list button:hover,
.accounts-toolbar .button:hover:enabled {
    color: #009cc2;
}
.reports-net-worth .reports-net-worth-tooltip,
.modal-popup ul.modal-list ul {
    background-color: #37474F;
}
.reports-net-worth .reports-net-worth-tooltip .tooltip-arrow {
    border-right-color: #37474F;
}
.modal-user-prefs .modal-arrow {
    border-top-color: #37474F;
}
.ynab-calendar-months button,
.modal-popup ul.modal-list button {
    color: #40C4FF;
}
.ynab-calendar-selected-year {
    color: white;
}
.budget-table-row.is-master-category:not(.is-checked):hover .currency {
    color: #40C4FF;
}
.goal-progress-chart text {
    fill: white !important;
    text-shadow: none !important;
    font-weight: bold !important;
    font-size: 1em !important;
    text-decoration: none !important;
}
.goal-progress-chart .percent {
    font-size: 3em !important;
}
.budget-inspector,
.reports-inspector {
    border-left: none;
}
.budget-table-header .budget-table-cell-button:hover,
.budget-table-row .budget-table-cell-button:hover {
    color: #40C4FF;
}
.income-expense-income .income-expense-h1 button .flaticon,
.income-expense-expense .income-expense-h1 button .flaticon,
.income-expense-net-income .income-expense-h1 button .flaticon {
    color: white;
}
.income-expense-header .income-expense-column-header,
.income-expense-level1 {
    background-color: #37474F;
}
.income-expense-level1 .income-expense-column:first-child,
.income-expense-level1 .income-expense-totals .income-expense-column:first-child {
    background-color: #546E7A;
    font-weight: bold;
    color: white;
}
.income-expense-level2 .income-expense-column {
    background-color: #607D8B;
}
.income-expense-level1 .income-expense-totals .income-expense-column {
    background-color: #78909C;
}
.modal-budget-activity .activity-header {
    color: #40C4FF;
}
.income-expense-expense .income-expense-h1 button,
.income-expense-income .income-expense-h1 button {
    background-color: #37474F;
}
.modal-budget-activity .budget-activity-debt-info {
    background-color: #455A64;
}
.income-expense-net-income .income-expense-row {
    background-color: #37474F;
}
.income-expense-net-income .income-expense-h1 {
    background-color: #607D8B;
    margin-bottom: 0px;
}
.circle-progress .inner-circle {
    fill: #607D8B;
}
body.is-init-loading {
    background: -webkit-linear-gradient(top, #37474F, #607D8B);
    background: -moz-linear-gradient(top, #37474F, #607D8B);
    background: -o-linear-gradient(top, #37474F, #607D8B);
    background: -ms-linear-gradient(top, #37474F, #607D8B);
    background: linear-gradient(to bottom, #37474F, #607D8B);
}
::-webkit-scrollbar {
    width: 12px;
}
::-webkit-scrollbar-track {
    background-color: rgba(96, 125, 139, 1);
}
::-webkit-scrollbar-thumb {
    background: rgba(55, 71, 79, 0.8);
}
.budget-inspector h3,
.reports-inspector h3 {
    color: white;
}
.reports-controls .button:not(.button-disabled):hover,
.budget-toolbar .button:hover {
    color: #009cc2;
}
.modal {
    background-color: #37474F;
}
.modal-account-dropdown .modal-list .is-section-item {
    color: white;
    font-weight: bold;
}
.modal-account-dropdown .modal-list .button-list {
    color: white;
}
.modal-account-dropdown strong {
    color: white;
}
label {
    color: white;
}
.reports-spending-tooltip {
    background-color: #37474F;
    color: white;
}
.reports-spending-tooltip .tooltip-arrow {
    border-right: 15px solid #37474F;
}
.accounts-calendar-selected-date,
.accounts-calendar-grid .accounts-calendar-day {
    color: white;
}
.ynab-grid-header-row {
    background-color: #37474F;
    color: white;
}
.ynab-grid-header-row .is-sorting {
    color: #009cc2;
}
.sidebar {
    background: #263238;
}
.budget-toolbar {
    background-color: #455A64;
}
.budget-toolbar .button {
    color: #40C4FF;
    font-weight: bold;
}
.budget-toolbar .undo-redo-container .button-disabled {
    color: rgba(64, 196, 255, 0.5);
    font-weight: bold;
}
.budget-table-header {
    background-color: #546E7A;
}
.budget-table-header li {
    color: white;
    font-weight: bold;
}
.budget-table-row.is-master-category {
    background-color: #455A64;
}
.budget-table-header .budget-table-cell-button,
.budget-table-row .budget-table-cell-button {
    color: white;
}
.budget-table-row.is-master-category .positive,
.budget-table-row.is-master-category .negative,
.budget-table-row.is-master-category .cautious,
.budget-table-row.is-master-category .zero {
    color: rgba(255, 255, 255, 0.8);
    font-weight: bold;
}
.budget-table-row.is-sub-category {
    background-color: #546E7A;
}
.positive {
    color: white;
}
.zero,
.currency-input span.zero {
    color: rgba(255, 255, 255, 0.3);
}
.ynab-checkbox-button {
    background: none;
}
.budget-table-row.is-sub-category > .budget-table-cell-available .zero {
    background-color: #37474F;
}
.negative {
    color: rgba(255, 255, 255, 0.5);
}
.budget-table-row.is-master-category .budget-table-cell-add-category .flaticon {
    color: #40C4FF;
    opacity: 1;
}
is-master-ctegory:hover {
    color: white;
}
.budget-inspector,
.reports-inspector {
    background-color: #37474F;
}
.search-form--input {
    background-color: #263238;
}
.modal-content,
.modal-actions,
.modal-header,
.modal-generic .modal,
.modal-list {
    color: white !important;
    background-color: #37474F;
}
.nav-accounts .negative {
    background-color: #CFD8DC;
    color: #d33c2d !important;
}
.budget-table-row.is-sub-category > .budget-table-cell-available .positive {
    background-color: #43A047;
}
.modal-arrow {
    border-bottom-color: #37474F !important;
}
.reports-controls,
.accounts-toolbar {
    background-color: #455A64;
}
.button-categories,
.button-timeframe,
.button-accounts,
.add-transaction,
.accounts-toolbar-edit-transaction,
.accounts-toolbar-file-import-transactions,
.undo-button,
.redo-button {
    color: #40C4FF;
}
.modal-reports-dropdown .modal-list li span.main-category {
    color: white;
}
.reports-content {
    background-color: #455A64;
    color: white;
}
.reports-breadcrumb button,
.reports-spending .c3-chart-arcs-title tspan,
.reports-spending .c3-chart-arcs-title tspan.c3-chart-total-spending {
    color: white;
}
/* .user-data {
    color: white !important;
} */

.reports-net-worth-tooltip {
    color: white !important;
}
.c3-tooltip-container {
    color: black !important;
}
.reports-spending-toggle button {
    color: #40C4FF;
}
.ynab-grid {
    background-color: #546E7A;
    color: white;
}
.ynab-grid-body-row.is-scheduled:not(.is-checked) {
    background-color: #607D8B;
    color: rgba(255, 255, 255, 0.8);
}
.button-disabled {
    color: rgba(0, 155, 194, 0.5) !important;
}
.modal-user-prefs .modal-arrow {
    border-bottom-style: none;
}
.budget-table-row.is-master-category,
.budget-table-row.is-sub-category {
    border-top: 1px solid #263238;
}
.modal-budget-move-money .modal-actions,
.modal-budget-overspending .modal-actions {
    margin: 0px;
}
.button-primary .flaticon {
    color: rgba(255, 255, 255, 0.5);
}
.left-to-budget span,
.budget-header-totals-amount .budget-header-totals-amount-label {
    color: white;
}
.budget-header-days .budget-header-days-age {
    color: #16a336;
}
.ynab-radiobutton button,
.budget-inspector-goals .ynab-radiobutton button,
.budget-inspector-goals dt {
    color: white;
}
.inspector-category-note {
    color: rgba(255, 255, 255, 0.7);
}
.inspector-message.negative {
    background-color: #d33c2d;
    color: white;
}
.inspector-category-name,
.budget-inspector,
.reports-inspector {
    color: white;
}
.budget-inspector h3,
.reports-inspector h3 {
    background-color: #546E7A;
}
.inspector-message.cautious {
    background-color: #e59100;
    color: white;
}
.inspector-message.cautious .inspector-message-arrow {
    border-bottom-color: #e59100;
}
.inspector-message.negative .inspector-message-arrow {
    border-bottom-color: #d33c2d;
}
.goal-warning .inner-circle {
    fill: #607D8B;
}
.circle-progress .percent,
.circle-progress .percent-sign,
.circle-progress .percent-label {
    color: white !important;
}
.users-budgets {
    background-color: #37474F;
}
.users-budgets h3,
.users-budgets .select-budget,
.users-budgets .last-modified,
.users-budgets .create-new-budget button {
    color: white;
}
}
