css:
@-moz-document domain("slickdeals.net") {
#fpMainContent .gridCategory .grid .fpItem .itemImageAndName a.itemStore {
    color: #e1e1e1;
}
#fpMainContent .gridCategory .fpGridBox {
    background-color: #555;
}
#pageContent {
    background-color: #333;
}
body {
    background-color: #333;
}
#fpMainContent .gridCategory .grid .itemTitle {
    color: #adddaa;
}
#fpMainContent .gridCategory .grid .fpItem .priceLine .itemPrice {
    color: #eee;
}
#fpMainContent .gridCategory .grid .fpItem .itemBottomRow {
    background-color: #555;
}
.sideGridBox {
    background: #333;
}
.sideGridBox .subBoxContent a.split {
    color: #ddd9aa;
}
#fpMainContent .gridHeading>h2 {
    color: #e9e9e9;
}
.categoryBar a {
    color: #eee;
}
.categoryBar.stickyHeader {
    background: none;
}
#fpMainContent .gridCategory .likes .likesLabel .count {
    color: #eee;
}
#fpMainContent .gridCategory .grid .fpItem .itemBottomRow .comments .count {
    color: #eee;
}
}
