@-moz-document domain("pikabu.ru") {

.sidebar-block_border {
	background: rgba(255, 255, 255, 0.75);
}

#branding_ad_comment {
    height: 0px!important;
    width: 0px!important;
}
.b-page-header-v2 {
    width: 98.9%;
	background: none!important;
}
body {
    background: url("/*[[fon]]*/") no-repeat fixed!important;
    background-size: cover!important;
    background-position: top center!important;
}
.header-cont {
    background: none;
    width: 99%;
}
td.header-nav-bottom > ul.menu > li.menu-item-default {
    position: relative;
    width: 82px;
}
td.header-nav-bottom > ul.menu {
    height: 28px;
}
table#header_t ul {
    margin: -4px;
}
table#header_t {
    width: 101.8%;
}
.b-social {
    background: rgba(11, 11, 11, 0);
}
.b-story-blocks, .b-social {
    color: white;
    background-color: rgba(11, 11, 11, 0.2);
}
.b-story-blocks_with-border {
    border-color: rgba(11, 11, 11, 0);
}
.story__scroll-wrapper_enabled {
    display: none;
}
.b-story__show-all {
    background: rgba(1, 1, 1, 0.4);
} 
.b-story__show-all:hover {
    background: rgba(1, 1, 1, 0.8);
} 
.b-user-profile, .b-feed-panel {
    background: rgba(0, 0, 0, 0.35);
}
.b-story-blocks, .b-story-blocks__wrapper {
    position: relative!important;
    background-color: none!important;
}
table, td.header-logo, .b-tag-search, .story-search, div.menu-block-branding td.menu-best-comm {
    background-color: rgba(0, 0, 0, 0.35);
}
.story__header-additional, .story__header-additional a, .b-comment__time {
    color: black;
}
.rating_bl, td.menu-block-title, .rating_bl {
    border-color: rgba(75, 75, 75, 0.45)!important;
}
td.menu-block-title {
    background: rgba(0, 0, 0, 0.35)!important;
}
.story__tag {
    background: grey;
    border: none;
}
/*[[colors]]*/
}

