/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(https://www.google.com/calendar), url-prefix(https://www.google.ca/calendar) {
body { font-family: Arial, sans serif ! important; margin: 0 ! important; background: #E4E4EE ! important; }

#co table tbody td [name="esfebox"] { background: red ! important; }

.dayInMonth { height: 16px ! important; }
.dayNotInMonth { height: 16px ! important; }
#calendars table { background: #7E7FA0 ! important; }
#calendars td { background: #7E7FA0 ! important; }
#calendarsBottomChrome { background-color: #7E7FA0 ! important; }
#chrome { margin-left: 26px ! important; width: 95.5% ! important; }
#co [class] #guesttext { background: red ! important; }
#co [class] { background: #7E7FA0 ! important; }
#co [class] { border-color: #7E7FA0! important; }
#co table[id="stabs"]  [class="selected"] [href*="javascript"]  { background: #7E7FA0 ! important; }
#co tr { background: #7E7FA0 ! important; }
#decowner { background: #E4E4EE ! important; }
#decowner > [style] > .dayInMonth { background: #CACBDE ! important; }
#decowner > [style] > .dayNotInMonth { background: #CACBDE ! important; }
#dp_0_tbl { border: 0px ! important; }
#entries .entry-body { max-width: none ! important; padding-right: 2em ! important; }
#gridcontainer { border-color: #7E7FA0 ! important; }
#maincell { background: #7E7FA0 ! important; }
#mt1 div { background: #7E7FA0 ! important; }
#mt2 div { background: #E4E4EE ! important; }
#mt2 table { background: #E4E4EE ! important; }
#nav { background-color: #7E7FA0 ! important;  background-image: url("data:image/png;base64,") ! important;   background-position: center 5% ! important;  background-repeat: no-repeat ! important; width: 24px ! important; margin-left: 26px ! important; }
#nav > div { visibility: hidden; }
#nav:hover { background-color: transparent; width: 240px ! important; }
#nav:hover + #chrome { margin-left: 260px ! important; width: auto ! important; }
#nav:hover > div { visibility: visible; }
#nb_0 { padding-top: 0px ! important; }
#nb_0 div[style="padding: 0pt 3px 1px; background: rgb(195, 217, 255) none repeat scroll 0% 50%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;"] { background: #7E7FA0 ! important; padding: 0pt 10px 0px ! important; }
#nb_0 tr[style="height: 2px;"] { visibility: hidden ! important; }
#sbody { background: #7E7FA0 ! important; }
#sbody td { border-color: #7E7FA0 ! important; }
#sbody th { border-color: #7E7FA0 ! important; color: #000000 ! important; }
#set { background: #7E7FA0 ! important; }
#topBar td[valign="middle"] { visibility: hidden ! important; }
#uploadframe { background: #E4E4EE ! important; color: black ! important; }
[class], a:link, a:visited, a:active { text-decoration: none ! important; color: #000000 ! important; }
[class="adc"] { font-size: 70% ! important; text-decoration: none ! important; }
[class="bookmarks"] {  background: #E4E4EE ! important; }
[class="datePickerDiv"] { background: #7E7FA0 ! important; }
[class="lk"][id="comp"] { display: none ! important; }
[class="lk"][id="open-quick-add"] { display: none ! important; }
[class="lk"][onmousedown="_GenSettings(1)"] { display: none ! important; }
[class="lk"][style="font-size: 80%;"][onclick="fs(); return false;"] { visibility: visible ! important;  position: absolute ! important; top: 1px ! important; right: 99px ! important; font-size: 70% ! important; }
[class="nt"] { display: none ! important; }
[class="s h"] { background: #7E7FA0 ! important; }
[class="t1"] { display: none ! important; }
[class="t2"] { display: none ! important; }
[href*="javascript:void(_GenSettings());"] { visibility: visible ! important;  position: absolute ! important; top: 0px ! important; right: 55px ! important; }
[href*="logout"] { visibility: visible ! important; position: absolute ! important; top: 0px ! important; right: 3px ! important; }
input *, select *, textarea * { background: white ! important; color: black ! important; }
div[style="width: 100%; height: 2px;"] { background: #7E7FA0 ! important; }
table[width="100%"][cellspacing="0"][cellpadding="0"][bgcolor="#c3d9ff"] { background: #7E7FA0 ! important; }
td[align="center"] td { color: red ! important; }
td[unselectable="on"][onclick="uv(this)"] { background: #E4E4EE ! important; border: 0px ! important; }
td[valign="top"][align="right"] { visibility: hidden ! important; font-size: 70% ! important; }
tr[height="2"] { display: none ! important; }
tr[height="4"] { display: none ! important; }
table.ccc,
table.bookmarks {
   top: 5px !important;
   left: 5px !important;
   -moz-opacity: .6 !important;
   position: absolute;
   padding: 0 !important;
   display: block !important;
   width: 16px !important;
   height: 18px !important;
}
table.ccc:hover,
table.bookmarks:hover {
   -moz-opacity: .85 ! important;
   width: 480px ! important; 
   height: 17px ! important;
   padding-top: 0px ! important;
   padding-bottom: 30px ! important;
   padding-left: 5px ! important;
   -moz-border-radius: 7px;
   border: 1px solid #7E7FA0;
   background-color: #CACBDE ! important;
}
table.ccc *, table.bookmarks * { display: none  ! important; }
table.ccc:hover *, table.bookmarks:hover * { display: inline ! important; }
