css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://nyheter24.se/filmtipset') {


/* ADBLOCK */
#bannertoparrow,
A[href="become_supporter.cgi"],
EMBED,
IFRAME,
.linkhover[style*="Nyheter24logga.png"],
.front_row,
.back_row,
DIV[style*="http://nyheter24.se/template/resources/images/sprite_repeat_x.png"],
#bannerdiv, #bannerdivtop, .price, .headCoreNetworkNavigation,
IMG[src="http://images.filmtipset.se/Images/annons-med-pil.png"],
IMG[alt="annons"],
[id^="aswift_"],
[href^="vendor_redirect.cgi"],
[href^="pris/"],
#bannerdivright
{
display: none !important;
}


/* BAKGRUNDSFÄRG */
html, body
{
background: #e0e7e2 !important;
}


/* sidbredd */
TABLE[style="float: left"], /* special listor */
TABLE[width*="840"], /* mina sidor */
TABLE[width*="842"], /* första sidan */
TABLE[style*="841"], 
TABLE[style*="842"] /* TV */ 
{
width: 1080px !important;
}


/* 1 färg på BETYG */
.row[style*="grade_1_bg"]
{
background: #d3bc82 !important;
border-bottom: 1px solid #a0a0a0 !important;
}

.row[style*="grade_2_bg"]
{
background: #fabbd5 !important;
border-bottom: 1px solid #a0a0a0 !important;
}

.row[style*="grade_3_bg"]
{
background: #f5f17e !important;
border-bottom: 1px solid #a0a0a0 !important;
}

.row[style*="grade_4_bg"]
{
background: #b1d3f0 !important;
border-bottom: 1px solid #a0a0a0 !important;
}

.row[style*="grade_5_bg"]
{
background: #b6e8a2 !important;
border-bottom: 1px solid #a0a0a0 !important;
}

.row[style*="grade_-"]
{
background: #E5E5E5 !important;
border-bottom: 1px solid #a0a0a0 !important;
}


/* forum */
.subject
{
font: 1.3em arial !important;
font-weight: 900 !important;
padding: 2px !important;
}
#post_0_children .subject
{
display: none !important;
}

.headnew
{
padding: 5px !important;
border: 1px solid #9eb694 !important;
}
.headold
{
background: #e0e0e0 !important;
padding: 5px !important;
border: 1px solid #a0a0a0 !important;
}

.mess
{
padding: 5px !important;
background: #f8f8f8 !important;
border: 1px solid #d0d0d0 !important;
border-top: 0px !important;
}



}
