css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("subtitleseeker.com") {
@font-face {
    font-family:FontAwesome;
    src:url(https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.5.0/fonts/fontawesome-webfont.ttf);
  }

  body {
  background-color: #111 ;
}

.global_wrapper {
  background-color: #111  !important;
}

#search_main {
  background-color: black;
  color: white;
  border: 1px solid #333333;
}
.tab-content.tabSizeFull.bg_grey-bar {
  background-color: #181821;
}
.wrap a {
  color: #793610;
}
.bg_grey-bar.grey {
  color: #7d7d81;
  background: #181821;
}
.corner {
  background-image: none !important;
}
.corner.corner-right-grey-light,
.corner.corner-right-grey {
  border-radius: 0 10px 0 0;
}
.corner.corner-left-grey-light,
.corner.corner-left-grey {
  border-radius: 10px 0 0 0;
}
.bg_grey-light.blue {
  background-color: #0b0b11;
}
.corner.corner-left-grey-light,
.corner.corner-right-grey-light {
  background-color: #0b0b11;
}
.corner.corner-right-grey,
.corner.corner-left-grey {
  background-color: #181821;
}
.bg_grey-light.blue a {
  color: #3e3e3e !important;
}
.bg_grey-light.blue {
  color: #3e3e3e;
}
.search-loader {
  display: none;
}
.search-main-submit.f16b.white {
  -webkit-border-radius: 11;
  -moz-border-radius: 11;
  border-radius: 11px;
  font-family: Arial;
  color: #ffffff;
  font-size: 20px;
  background: #3498db;
  padding: 10px 20px 10px 20px;
  text-decoration: none;  
}
.search-main-submit.f16b.white:hover {
  background: #3cb0fd;
  text-decoration: none;
}

.search-main-submit.f16b.white::after{
  content: "Seek!";
}

.bg_blue-bright {
    background-color: #793610 !important;
    color: #ccc !important;
}
.blue {
  color: #d8793c;
}

.right-menu,
.left-menu {
  background-image: none !important;
}

.white.bg_blue-light.bg_blue-bright {
  padding: 7px 20px;
  border-radius: 10px 10px 0 0;
}

.header {
  background-color: #FFF;
}

.bg_blue-light.white {
  background-color: #4e230c;
  padding: 5px 20px;
  border-radius: 10px 10px 0 0;
}

div.chart-wrap .content .chart .bar:hover {
    background-color: #9FC54E;
}

div.chart-wrap .content .chart .bar {
    background-color: #F75342;
}

.bg_blue-light {
    background-color: #4E230C;
}
.innerRows.bg_white.f12b,
.innerRows.bg_white.f12,
.bg_white {
  background: #111;
}
.boxRows .blue {
  color: #fff !important;
}

.boxRowsInner.f12.grey-dark.arial {
  color: #7a7a73;
}

div.search-rows:hover,
.boxRows:hover {
    background-color: #222;
}
  
  
.green-light.bg_green-light {
  background-color: #fff;
}
#season-box span,
#season-box li,
#episode-box span,
#episode-box li .blue{
  color: white;
  background-color: #000;
}

#season-box span:hover,
#episode-box span:hover{
  background-color: #222;
}

#social{display: none;}

#redirect_episode::before {
  content: '\f002';
  font-family: FontAwesome;
  font-style: normal;
  font-weight: normal;
  text-decoration: inherit;
}

#redirect_episode::before {
  content: '\f002' !important;
  font-family: FontAwesome;
  font-style: normal;
  font-size: 20px;
  font-weight: normal;
  text-decoration: inherit;
  color: white;
  border: 1px solid white;
  padding: 4px 6px;
  margin-bottom: 20px;
}

#redirect_episode {margin-top: 5px;}
#redirect_episode img {display: none;}

#redirect_episode:hover {text-decoration: none;}
  
 
}
