css:
@-moz-document domain("teamtreehouse.com") {
  body {
    background: #3e474f !important;
    color: #b2bac2 !important;
  }

  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    color: #b2bac2 !important;
  }

  p {
    color: #b2bac2 !important;
  }

  a {
    color: #5fcf80;
  }

  a:hover {
    color: #34ad58;
  }

  .header,
  nav {
    background: #384047 !important;
  }

  .header-nav-link {
    color: #5fcf80;
  }

  .nav-anchor {
    color: #b2bac2;
  }

  .nav-anchor:hover {
    color: #fff;
  }

  .nav-item.current a {
    color: #fff;
  }

  .header-nav-item-profile-chevron {
    fill: #5fcf80;
  }

  .header-nav-item-notifications-icon {
    fill: #5fcf80;
  }

  ul#view.nav {
    background: #323a40 !important;
  }

  .control-container {
    background: #323a40;
  }

  .control-container .nav li.current a,
  .control-container .nav a.selected {
    color: #fff;
  }

  .control-container .nav a:hover,
  .control-container .nav li a:hover {
    color: #5fcf80;
  }

  .control-container .dropdown-parent .dropdown-parent-label {
    color: #5fcf80;
  }

  .control-container .dropdown-parent:hover > a,
  .control-container .dropdown-parent:hover a:hover {
    color: #fff;
  }

  div.search {
    background: #323a40;
  }

  .achievement-steps {
    background: #323a40;
  }

  .achievement-steps li a strong {
    color: #fff;
  }

  .achievement-steps li a {
    border-bottom: 1px solid #b2bac2;
  }

  .dashboard-index .content-suggestions .box-footer .suggestion-time-estimate strong {
    color: #b2bac2;
  }

  .tabs li a:hover {
    color: #fff;
  }

  .tabs li.current a {
    color: #fff;
  }

  .tabs li.current a:hover {
    color: #7b8b8e;
  }

  .card-box {
    background: #384047 !important;
  }

  a.card-box.card-link::before,
  a.card-box.card-link::after {
    background: #323a40 !important;
  }

  .card-status-title {
    color: #b2bac2;
  }

  #track-card:after,
  #track-card:before {
    background: #323a40;
  }

  .section-heading p {
    color: #7b8b8e;
  }

  .section-heading a {
    color: #5fcf80;
  }

  #no-results p {
    color: #b2bac2;
  }

  .topic-filter a span {
    color: #b2bac2;
    background: #384047;
  }

  .videos-show #video-meta span#video-authors a,
  .markdown-zone a,
  .videos-show #workspaces-tab-content .workspace-item--notcreated a {
    color: #5fcf80;
  }

  .videos-show #video-meta span#video-authors a:hover {
    color: #fff;
  }

  .markdown-zone a:hover {
    color: #34ad58;
  }

  .markdown-zone code {
    color: #7b8b8e;
  }

  .markdown-zone .highlight {
    overflow: hidden;
  }

  #cc-editor .editor .CodeMirror .CodeMirror-scroll,
  #cc-editor .preview .CodeMirror .CodeMirror-scroll {
    overflow: hidden !important;
  }

  #cc-editor .cm-s-solarized.cm-s-dark .CodeMirror-linenumber {
    color: #b2bac2;
  }

  #cc-editor .cm-s-solarized .cm-comment {
    color: #7b8b8e;
  }

  .videos-show #questions-container .content-discussion,
  .videos-show #downloads-tab-content .content-discussion {
    background: #323a40;
  }

  #quiz-feedback .alert {
    background: #323a40;
  }

  #quiz-answers .multiple-choice li .answer,
  #quiz-answers .true-false li .answer {
    color: #fff;
  }

  .button-secondary {
    color: #b2bac2 !important;
    border: 2px solid #b2bac2 !important;
  }

  .button-secondary:hover {
    color: #5fcf80 !important;
    border: 2px solid #5fcf80 !important;
  }

  .button.secondary {
    color: #b2bac2 !important;
    border: 2px solid #b2bac2 !important;
  }

  .button.secondary:hover {
    color: #5fcf80 !important;
    border: 2px solid #5fcf80 !important;
  }

  .workspace-table,
  .workspace-table .top,
  .workspace-table .bottom {
    background: #384047;
    border: none;
  }

  .workspace-item {
    border-top: 1px solid #7b8b8e !important;
    border-bottom: 1px solid #7b8b8e !important;
  }

  .discussion-answer .discussion-byline a,
  .discussion-comment .discussion-byline a {
    color: #b2bac2;
  }

  .discussion-byline a:hover {
    color: #fff !important;
  }

  .discussion-answer-best {
    background: #384047;
  }

  .discussion-answer-voting,
  .discussion-answer-voting .vote-up a {
    background: #384047;
  }

  .discussion-tags:after,
  #forum-post-breadcrumb:after {
    display: none;
  }

  #filter-breadcrumb {
    background: #323a40;
  }

  #filter-breadcrumb-tags li {
    color: #b2bac2;
  }

  .sorting-container a.selected, .sorting-container .dropdown-parent-label.selected {
    color: #b2bac2;
  }

  .leaderboard-list table {
    background: #323a40;
  }

  .leaderboard-truncate {
    background: #3e474f;
  }

  .leaderboard-user {
    color: #b2bac2;
  }

  .leaderboard-user:hover {
    color: #576366;
  }

  .leaderboard-total {
    color: #5fcf80;
  }

  .form-item p a {
    color: #5fcf80;
  }

  .box,
  .contained {
    background: #323a40;
  }

  .sidebar #sidebar-nav {
    background: #323a40;
  }

  .discussion-title a {
    color: #b2bac2;
  }

  .discussion-title a:hover {
    color: #fff;
  }

  .dashboard-profile div#profile-info figure figcaption ul#social-links li a {
    background: #323a40;
  }

  .mini-profile-heading {
    background: #323a40;
  }

  .modal.mini-profile .student-stats strong {
    color: #b2bac2;
  }

  .data-item a {
    color: #5fcf80;
  }

  .module p,
  .contained .secondary-module p {
    color: #7b8b8e;
  }

  .disclaimer {
    background: #384047;
  }

  .mixed-box .box-header,
  .mixed-box .box-footer {
    background: #384047 !important;
  }

  .mixed-box .box-actions.alert.info,
  .mixed-box .box-actions.alert.icons-only {
    background: #384047;
  }

  .mixed-box .box-header, .mixed-box .box-content, .mixed-box .box-actions, .mixed-box .box-alert, .mixed-box .box-footer {
    background: #384047;
  }

  .plan-billed strong {
    color: #7b8b8e;
  }

  html.accounts-controller .percent .value {
    color: #b2bac2;
  }

  .accounts-subscription #pause-enrollment-cta p a,
  .accounts-subscription .mixed-box .box-footer p a,
  a.business-callout-anchor {
    color: #5fcf80;
  }

  .business-callout .right-arrow-icon {
    fill: #5fcf80;
  }

  .newsletter {
    background: #323a40 !important;
  }

  .page-content .tag-bar li a {
    border: 2px solid #5fcf80;
    color: #5fcf80;
  }

  .page-content .tag-bar li a:hover {
    border: 2px solid #34ad58;
    color: #34ad58;
  }

  .footer-bonus-description a {
    color: #5fcf80;
  }

  .footer-social-link {
    background: #3e474f;
  }

  footer .social li a {
    background: #3e474f center no-repeat;
  }

  footer p a {
    color: #b2bac2;
  }
}
