css:
@-moz-document domain(brawldb.com) {
.content {
	background: none;
}

div>svg>rect {
	fill: none;
}

.navbar {
	background-color: rgba(0, 17, 31, 0.53);
}

#bs-example-navbar-collapse-2>ul>li>a:hover {
	background: none;
}

.confirmBtn,
.confirmBtn:active,
.confirmBtn:focus {
	background-image: linear-gradient(#3a28de 0%, #8f1a8c 100%);
}

.right {
	margin-top: 15px;
}

.fb-share-button {
	margin-right: 15px;
}

.search {
	margin-top: 400px;
}

a:hover,
a:focus,
a:active {
	text-decoration: none;
	color: #FCFCFC;
	text-shadow: 0 0 7px #3a28de;
}

#legendSelect {
	border-radius: 5px;
	padding: 3px;
	background: none;
	border: none;
	color: #FCFCFC;
	min-width: 120px;
	padding: 5px;
	margin: 10px 0 0 0;
}

.itemSelector {
	font-size: 24px;
}

.navbar {
	background-image: linear-gradient(rgba(58, 40, 222, 0.5) 0%, rgba(143, 26, 140, 0.5) 100%);
}

.nav>li>a {
	color: #FCFCFC;
}

.nav>li>a:hover,
.nav>li>a:focus,
.nav>li>a:active {
	background-color: rgba(0, 0, 0, 0) !important;
	color: #FCFCFC;
}

.clanInfo {
	margin-top: 15px;
}

#itemLevel {
	margin-top: 35px;
}

.playerLevel,
.teamElo,
.teamGames,
.teamWins,
.gameTime,
#itemTime,
.playedItemWrapper>div>div>p,
.rankedInfo>div>div:nth-child(2)>div {
	background-image: linear-gradient(rgba(58, 40, 222, 0.15) 0%, rgba(143, 26, 140, 0.15) 100%);
	border-top: 2px solid #8f1a8c;
	border-right: 3px solid #8f1a8c;
	border-bottom: 2px solid #3a28de;
	border-left: 3px solid #3a28de;
	min-width: 120px;
	padding: 5px;
	margin: 10px 3px 3px 3px;
	border-radius: 5px;
}

.rankedBannerImg {
	margin: 5px 0 0 15px;
}

#itemBanner {
	margin: 35px 0 0 15px;
}

.rankedData {
	margin: 20px 0 0 -15px;
}

.rankedInfo>div>div:nth-child(1) {
	margin: 0 20px 0 -40px;
}

.rankedInfo>div>div:nth-child(2) {
	margin: 5px 0 0 20px;
}

body>div.container.content>div.row.mostPlayed>div.col-md-6.mostPlayedWeapons>div>div:nth-child(1)>div>select {
	margin-left: 20px;
}

.footer {
	width: 100%;
}

img {
	-webkit-filter: drop-shadow(1px 1px 2px #8f1a8c) drop-shadow(-1px -1px 2px #3a28de);
	filter: drop-shadow(1px 1px 2px #8f1a8c) drop-shadow(-1px -1px 2px #3a28de);
}

.playedItemImg {
	margin: 40px 0 0 0;
}

.playerLevel {
	text-align: center;
	font-weight: bold;
}

.gameTime {
	text-align: center;
	margin: 10px 0 0 0;
}

p,
.position,
.elo,
.wins,
.loss,
.ratio {
	text-align: center;
}

select {
	border-radius: 5px;
	padding: 3px;
	background: linear-gradient(rgba(58, 40, 222, 0.15) 0%, rgba(143, 26, 140, 0.15) 100%);
	border-top: 2px solid #8f1a8c;
	border-right: 3px solid #8f1a8c;
	border-bottom: 2px solid #3a28de;
	border-left: 3px solid #3a28de;
	color: #FCFCFC;
	min-width: 120px;
	padding: 5px;
	margin: 10px 0 0 -20px;
}

select>option:nth-child(1),
select>option:nth-child(42),
select>option:nth-child(43) {
	background-color: #3a28de;
	color: #FCFCFC;
}

select>option:nth-child(2),
select>option:nth-child(41),
select>option:nth-child(44) {
	background-color: #3E27DA;
	color: #FCFCFC;
}

select>option:nth-child(3),
select>option:nth-child(40),
select>option:nth-child(45) {
	background-color: #4227D6;
	color: #FCFCFC;
}

select>option:nth-child(4),
select>option:nth-child(39),
select>option:nth-child(46) {
	background-color: #4726D2;
	color: #FCFCFC;
}

select>option:nth-child(5),
select>option:nth-child(38),
select>option:nth-child(47) {
	background-color: #4B25CE;
	color: #FCFCFC;
}

select>option:nth-child(6),
select>option:nth-child(37),
select>option:nth-child(48) {
	background-color: #4F24CA;
	color: #FCFCFC;
}

select>option:nth-child(7),
select>option:nth-child(36),
select>option:nth-child(49) {
	background-color: #5424C5;
	color: #FCFCFC;
}

select>option:nth-child(8),
select>option:nth-child(35),
select>option:nth-child(50) {
	background-color: #5823C1;
	color: #FCFCFC;
}

select>option:nth-child(9),
select>option:nth-child(34) {
	background-color: #5C22BD;
	color: #FCFCFC;
}

select>option:nth-child(10),
select>option:nth-child(33) {
	background-color: #6022B9;
	color: #FCFCFC;
}

select>option:nth-child(11),
select>option:nth-child(32) {
	background-color: #6421B5;
	color: #FCFCFC;
}

select>option:nth-child(12),
select>option:nth-child(31) {
	background-color: #6920B1;
	color: #FCFCFC;
}

select>option:nth-child(13),
select>option:nth-child(30) {
	background-color: #6D20AD;
	color: #FCFCFC;
}

select>option:nth-child(14),
select>option:nth-child(29) {
	background-color: #711FA9;
	color: #FCFCFC;
}

select>option:nth-child(15),
select>option:nth-child(28) {
	background-color: #761EA5;
	color: #FCFCFC;
}

select>option:nth-child(16),
select>option:nth-child(27) {
	background-color: #7A1EA0;
	color: #FCFCFC;
}

select>option:nth-child(17),
select>option:nth-child(26) {
	background-color: #7E1D9C;
	color: #FCFCFC;
}

select>option:nth-child(18),
select>option:nth-child(25) {
	background-color: #821C98;
	color: #FCFCFC;
}

select>option:nth-child(19),
select>option:nth-child(24) {
	background-color: #871B94;
	color: #FCFCFC;
}

select>option:nth-child(20),
select>option:nth-child(23) {
	background-color: #8B1B90;
	color: #FCFCFC;
}

select>option:nth-child(21),
select>option:nth-child(22) {
	background-color: #8f1a8c;
	color: #FCFCFC;
}

.content>div:nth-child(8)>p {
	text-align: center;
	float: none;
	opacity: 0.2;
	margin: 35px 0 -80px 0
}

.teamWins {
	margin-bottom: 40px;
}

.content>div:nth-child(8)>a {
	padding: 15px;
	font-size: 24px;
}

.Wins,
.Loss,
.Ratio {
	text-align: center;
}

.pluginButton {
	box-shadow: 0 0 20px #8f1a8c;
}

.content>div:nth-child(2)>p:nth-child(4) {
	display: none;
}

h1,
h2,
h3,
h4,
.itemSelector,
.backBtn,
input,
.topLegendName,
#xp,
.mostPlayed>div>div>div>div>div>p:nth-child(1) {
	background-image: linear-gradient(#3a28de 0%, #8f1a8c 100%);
	text-decoration: none;
	text-align: center;
	margin: 7% 0% 7% 0%;
	margin-bottom: 5%;
	min-height: 60px;
	box-shadow: 0 0 20px #8f1a8c;
	padding: 15px 0px 15px 8px;
	border-radius: 5px;
	outline: none;
	font-size: 24px;
	color: #FFF;
	text-shadow: #000 0px 1px 5px;
	border: 1px solid #000;
	opacity: 0.7;
	box-shadow: 0 8px 6px -6px rgba(0, 0, 0, 0.7);
	border-top: 1px solid rgba(255, 255, 255, 0.8) !important;
	-webkit-box-reflect: below 0px -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(50%, transparent), to(rgba(255, 255, 255, 0.2)));
}

.content>form>select {
	margin-left: 30px;
}

.pageNavigation,
#creationDate,
#memberCount,
.memberRank,
.memberXp,
.memberJoined {
	border-radius: 5px;
	padding: 3px;
	background: linear-gradient(rgba(58, 40, 222, 0.15) 0%, rgba(143, 26, 140, 0.15) 100%);
	border-top: 2px solid #8f1a8c;
	border-right: 3px solid #8f1a8c;
	border-bottom: 2px solid #3a28de;
	border-left: 3px solid #3a28de;
	color: #FCFCFC;
	min-width: 120px;
	padding: 5px;
	margin: 10px 0 0 -20px;
}

.pageNavigation>span {
	font-size: 24px !important;
	font-weight: bold;
}

#rankings>div:nth-child(2) {
	border-radius: 10px 10px 0 0;
	border-top: 2px solid #8f1a8c;
}

#rankings>div {
	border-right: 3px solid #8f1a8c;
	border-left: 3px solid #3a28de;
}

#rankings>div:nth-child(51) {
	border-radius: 0 0 10px 10px;
	border-bottom: 2px solid #3a28de;
}

.itemTitleRow {
	border-radius: 5px;
	background: linear-gradient(rgba(58, 40, 222, 0.15) 0%, rgba(143, 26, 140, 0.15) 100%);
	border-top: 2px solid #8f1a8c;
	border-right: 3px solid #8f1a8c;
	border-bottom: 2px solid #3a28de;
	border-left: 3px solid #3a28de;
	padding: 5px 0;
	margin-bottom: 10px !important;
	text-align: center;
	color: #FCFCFC;
	font-weight: bold;
}

.content>div>p {
	text-align: left;
}

#members {
	background: none;
}

h4 {
	margin-left: -20px;
}

#xp,
#creationDate,
#memberCount,
.pageNavigation {
	width: 50%;
	margin-left: 1px;
}

.pageNavigation {
	margin-left: 25%;
}

input {
	min-height: 67px !important;
	font-family: inherit !important;
	font-size: 24px !important;
}

form>input:nth-child(2) {
	float: left !important;
	width: 95% !important;
}
}
